css:
@-moz-document domain("www.autojunk.nl") {
html, body, section, main, br, .site-wrap,
#jssor_1, .jssort101                        { background-color: #181818 !important; color: #ccc }
textarea                                    { background-color: #282828 !important; color: #ccc !important; border: 1px solid #333 !important; border-radius: 5px !important }
footer                                      { background-color: #282828; border-top: 1px solid #333 }
h2, h2 a, .article, .section-title          { color: #ccc !important }
.comment-content, .tag-list-mixed li a      { color: #aaa !important; transition: none }
a.menulink                                  { text-decoration: none !important }
a.menulink:hover                            { color: #78009E }

.tag-list-mixed li a:hover,
.button.callout:hover, button.callout:hover, 
.photo-month .photo-month-author:hover, 
.author-box .author-name:hover, 
.comment .comment-author:hover              { color: #78009E !important; background-color: inherit !important }

.popular-tags                               { padding: 0px 0px 50px 0px }

button.callout, 
.comment img.avatar, 
.button.callout, 
.photo-month .photo-month-author, 
.author-box .author-name, 
.comment .comment-author                    { border-radius: 3px }

#ajheader .__global-our-titles, .translucent,
.__global-titles-bar .titles-bar__item a    { background-color: #181818 !important }
input[type='submit'],
.button.inverted, button.inverted           { background-color: #333; border: 1px solid #444; border-radius: 3px; box-shadow: 0px 0px 15px 0px inset #333; color: #eee }
.small, .medium, .large, .wide              { border-color: #222 }


.pagination-pages li a                      { border-radius: 3px; padding: 3px 8px; margin: 3px; color: #78009E; background-color: #282828 }
.pagination-button                          { border-radius: 3px; padding: 3px 5px }

/* Override top-menu colors */
.__global-titles-bar .titles-bar__item a:hover  { background-color: #ed1c24 !important }
.__global-titles-bar .titles-bar__item.active a { background-color: inherit; color: #ed1c24}

/* Override inline styles */
p[style='display: inline-block; padding: 10px 20px 10px 2px; white-space: nowrap; border-width: thin; border-style: dotted; border-color: #000000;'] 
{ padding: 15px !important; border: 1px solid #333 !important; border-radius: 5px !important; background-color: #282828 !important }

/* Suppress some ads */
.advertisement, div[id^='div-gpt-ad-'], div[id^='google_ads_iframe_'], 
iframe[name='google_osd_static_frame'], div[id^='scr_'], 
iframe[id^='google_ads_frame'], iframe[src='https://www.autojunk.nl/ads/autojunk_miniads.html'],
iframe.jcl-wrapper, div.jpx-wp-wrapper, #jpx-wp-layer-top-container, #jpx-wp-layer-right-wrapper, #jpx-wp-layer-left-wrapper, #jpx-wp-front-container
{ display: none !important; visibility: hidden !important; z-index: -99999 !important; left: -9999px !important; top: -9999px !important; width: 0px !important; height: 0px !important; transform: translateY(-9999px) !important; opacity: 0 !important }
}
