body {
	background: #fff;
}
p,
a,
h1+h1 {
	font-family: 'Roboto', sans-serif !important;
}
a,
a:visited {
	color: #1565C0 !important;
}
.section1 p[align=center] {
	font-family: 'Times new roman' !important;
	/*equations*/
}
.section1 {
	max-width: 900px;
	margin: 50px auto;
}
.section1 p+div[style],
h1+p+p {
	background: #B3E5FC;
	border: none !important;
	border-left: solid 4px #039BE5 !important;
	margin: 0.5em auto;
	padding: 0.4em !important;
}
#sklik-banner {
	transform: scale(0.7);
}
p.MsoTitle {
	color: gray;
	text-align: left;
	border-bottom: dashed 1px;
}
p.MsoSubtitle {
	color: black;
	text-decoration: none;
	font-variant: normal;
	text-align: left;
	margin: 1ex auto 0;
}
h1+h1 {
	text-align: left;
	font-size: 1em;
	font-weight: 400;
	font-style: italic;
}
}
