css:
@-moz-document
  domain("radianttranslations.com")
{
.backstretch {display:none}
body {background-color: #272525;}
body, input, button, select, textarea {font-family: 'Open Sans',sans-serif!important;}

.x-brand {font-size:25px}
.x-navbar .desktop .x-nav>li>a {padding-top:15px;height:30px}
.x-navbar .desktop .x-nav>li ul {top: 30px;}
.x-navbar-inner {padding-bottom: 10px;}
.desktop .sub-menu {background-color:rgb(46, 45, 45);}
.x-logobar {background-color: rgb(46, 45, 45);border-bottom: 1px solid rgb(70, 66, 66);}
.x-navbar {border-bottom: 1px solid rgba(0,0,0,0.1);box-shadow: 0 1px 0 0 rgba(70, 66, 66, 0.715);background-color: rgb(46, 45, 45);}
.x-navbar .desktop .x-nav>li>a:hover, .x-navbar .desktop .x-nav>.x-active>a, .x-navbar .desktop .x-nav>.current-menu-item>a {box-shadow: inset 0 4px 0 0 rgb(101, 101, 103)}

.x-breadcrumb-wrap {border-bottom: 1px solid rgba(0,0,0,0.1);box-shadow: 0 1px 0 0 rgba(70, 66, 66, 0.715);text-shadow: 0 1px 0 rgba(0, 0, 0, 0.55);}

.x-boxed-layout-active .site {border: 1px solid #d5d5d5;border: 1px solid rgba(0,0,0,0.275);background-color: rgb(46, 45, 45);}

.hentry {margin-top: 10px;}
.entry-wrap {padding: 30px;background-color:rgb(46, 45, 45);box-shadow:0 0.15em 0.35em 0 rgba(45, 43, 43, 0.715);}
.x-boxed-layout-active .entry-wrap {border: 1px solid rgba(0,0,0,0.1);}
.entry-title {font-size:175%}
hr {border-top: 1px solid #424242;}

.x-colophon {border-top: 1px solid rgba(0,0,0,0.1);background-color: rgb(46, 45, 45);box-shadow: 0 -0.125em 0.25em 0 rgba(70, 66, 66, 0.715);}
.x-colophon.top {padding: 1px 0 1px;}
.x-colophon+.x-colophon {border-top: 1px solid rgba(0,0,0,0.1);box-shadow: inset 0 1px 0 0 rgba(70, 66, 66, 0.715);}

article.comment {background-color: rgb(47, 47, 47);box-shadow:0 0.15em 0.35em 0 rgba(45, 43, 43, 0.715);}
.x-boxed-layout-active article.comment {border: 1px solid rgba(0,0,0,0.1)}
.entry-thumb:before, .x-pagination span.current, .flex-direction-nav a, .flex-control-nav a:hover, .flex-control-nav a.flex-active, .mejs-time-current, .x-dropcap, .x-skill-bar .bar, .x-pricing-column.featured h2, .h-comments-title small, .x-entry-share .x-share:hover, .x-highlight, .x-recent-posts .x-recent-posts-img:after
{background-color: rgb(43, 43, 43);}
.h-comments-title small {box-shadow:inset 0 2px 3px rgba(0,0,0,0.3), 0 2px 1px #252323;}

.x-pagination a, .x-pagination span {background-color: #232222;box-shadow: inset 0 0 0 transparent, 0 0.1em 0.45em 0 rgba(45, 43, 43, 0.715);text-shadow: 0 1px 1px rgba(0, 0, 0, 0.85);}
.x-pagination span.current {box-shadow: inset 0 0.1em 0.35em rgba(0,0,0,0.65), 0 1px 0 0 rgba(45, 43, 43, 0.715);}

/* font colors */
body {color: hsl(0, 13%, 95%);}
.x-brand {color:#b1b0b0}
.x-brand:hover, .x-brand:focus {color: hsl(0, 0%, 100%);}
.x-topbar .p-info, .x-topbar .p-info a, .x-navbar .desktop .x-nav>li>a, .x-navbar .desktop .sub-menu a, .x-navbar .mobile .x-nav li>a, .x-breadcrumb-wrap a, .x-breadcrumbs .delimiter 
{color: hsl(0, 0%, 52%);}
.x-navbar .desktop .x-nav>li>a:hover, .x-navbar .desktop .x-nav>.x-active>a, .x-navbar .desktop .x-nav>.current-menu-item>a, .x-navbar .desktop .sub-menu a:hover, .x-navbar .desktop .sub-menu .x-active>a, .x-navbar .desktop .sub-menu .current-menu-item>a, .x-navbar .desktop .x-nav .x-megamenu>.sub-menu>li>a, .x-navbar .mobile .x-nav li>a:hover, .x-navbar .mobile .x-nav .x-active>a, .x-navbar .mobile .x-nav .current-menu-item>a 
{color:#b1b0b0}
a, h1 a:hover, h2 a:hover, h3 a:hover, h4 a:hover, h5 a:hover, h6 a:hover, .x-topbar .p-info a:hover, .x-breadcrumb-wrap a:hover, .widget ul li a:hover, .widget ol li a:hover, .widget.widget_text ul li a, .widget.widget_text ol li a, .widget_nav_menu .current-menu-item>a, .x-widgetbar .widget ul li a:hover, .x-accordion-heading .x-accordion-toggle:hover, .x-comment-author a:hover, .x-comment-time:hover, .x-recent-posts a:hover .h-recent-posts
{color:rgb(142, 140, 140);}
.comment-form-author label, .comment-form-email label, .comment-form-url label, .comment-form-rating label, .comment-form-comment label, .widget_calendar #wp-calendar th, .p-landmark-sub strong, .widget_tag_cloud .tagcloud a:hover, .widget_tag_cloud .tagcloud a:active, .entry-footer a:hover, .entry-footer a:active, .x-breadcrumbs .current, .x-comment-author, .x-comment-author a
{color:#696666;}
.x-colophon.bottom {color:#b1b0b0}
h1, h2, h3, h4, h5, h6, .h1, .h2, .h3, .h4, .h5, .h6, h1 a, h2 a, h3 a, h4 a, h5 a, h6 a, .h1 a, .h2 a, .h3 a, .h4 a, .h5 a, .h6 a, blockquote
{color:rgb(255, 255, 255);}
.widget ul li a, .widget ol li a, .x-comment-time {color:hsl(0, 1%, 49%);}
a:hover, .widget.widget_text ul li a:hover, .widget.widget_text ol li a:hover, .x-twitter-widget ul li a:hover {color:rgb(181, 181, 181)}

.x-main {width:100%}
.x-sidebar {display:none}
.ai-viewport-1 {display:none!important}
}
