@-moz-document domain("web.roblox.com"), domain("www.roblox.com") {
/* Roblox Modern, by SnivyDroid 
/* https://web.roblox.com/users/94054263/profile */
/* This code is open source! Use it to build a foundation for your own theme! */
/* Buttons Before Hover */
/* BTN-PRIMARY (Catalog/Trade "Buy Robux" Button) */

.btn-primary,
.btn-primary:link,
.btn-primary:active,
.btn-primary:visited {
    background-image: none!important;
    background-color: #0074BD;
    border: none;
    border-radius: 3px;
}
/* BTN-CONTROL (Search Button, Forum Post Button,Develop Buttons) */

.btn-control.btn-control-large {
    background-image: none!important;
    background-color: #0074BD;
    border: none;
    border-radius: 3px;
    color: white;
}
/* BTN MEDIUM (Other Group Buttons) */

.btn-control.btn-control-medium {
    background-image: none!important;
    background-color: #0074BD;
    border: none;
    border-radius: 3px;
    color: white;
}
/* BTN-SMALL (Enable 3D,) */

.btn-control.btn-control-small {
    background-image: none!important;
    background-color: #0074BD;
    border: none;
    border-radius: 3px;
    color: white;
}
/*BTN NEUTRAL (Avatar Wear/Remove Button) */

.btn-neutral,
.btn-neutral:link,
.btn-neutral:active,
.btn-neutral:visited {
    background-image: none!important;
    background-color: #0074BD;
    border: none;
    border-radius: 3px;
    color: white;
}
/* BTN-CONTROL-MEDIUM (Forum Buttons) */

.btn-control.btn-control-medium {
    background-image: none!important;
    background-color: #0074BD!important;
    border: none!important;
    border-radius: 3px!important;
    color: white;
}
/*	BTN-NEGATIVE (Group Leave Confirmation) */

.btn-negative,
.btn-negative:link,
.btn-negative:active,
.btn-negative:visited {
    background-image: none!important;
    background-color: #0074BD!important;
    border: none!important;
    border-radius: 3px!important;
    color: white;
}
/* Buttons After Hover */
/* BTN-PRIMARY (Group Create Button, Catalog/Trade "Buy Robux" Button, Develop Buttons) */

.btn-primary:hover,
.btn-primary:focus,
.btn-primary:active,
.btn-primary.active,
.open>.btn-primary.dropdown-toggle {
    background-image: none;
    background-color: #32B5FF;
    border: none;
    border-radius: 3px;
}
/* BTN-CONTROL (Search Button, Group Post Button, Other Group Buttons, Enable 3D, Forum Buttons) */

.btn-control:hover {
    background-image: none;
    background-color: #32B5FF!important;
    border: none;
    border-radius: 3px;
}
/* BTN-SMALL (Avatar Wear/Remove Buttons) */

.btn-small:hover {
    background-image: none;
    background-color: #32B5FF;
    border: none;
    border-radius: 3px;
}
/* BTN-LARGE (Group Leave Confirmation) */

.btn-large:hover {
    background-image: none;
    background-color: #32B5FF!important;
    border: none;
    border-radius: 3px;
}
/* Tabs (Trade Page, Develop Page,Group Page) */
/* Tabs Of Trade Page */
/* SquareTabGrey (Induvidual Tab Color when a mouse is not hovered over it, does not include text.) */

.SquareTabGray {
    background-color: #0074BD!important;
    border: none;
    border-top-right-radius: 3px;
    border-top-left-radius: 3px;
}
/* STG:hover (Tab When Hovered) */

.SquareTabGray:hover {
    background-color: #32B5FF!important;
}
/* SquareTabGrey a (Controls Text within tabs) */

.SquareTabGray a {
    color: white
}
/* STG a:hover (Text When hovered. Keep BG Propertiety.) */

.SquareTabGray a:hover {
    background-color: #32B5FF!important;
}
/* Tabs Of Develop/Group Page */
/* Tabs When Active */

.redesign .tab_white_31h_container .ajax__tab_active .ajax__tab_inner,
.tab.active,
.tab-container>.tab-active,
.tab-container>.tab-active:hover {
    background-color: #0074BD!important;
    border: none;
    border-top-right-radius: 3px;
    border-top-left-radius: 3px;
    color: white;
}
/* Tabs When inactive */

.redesign .tab_white_31h_container .ajax__tab_inner,
.tab,
.tab-container>div {
    background-color: #0074BD!important;
    border: none;
    border-top-right-radius: 3px;
    border-top-left-radius: 3px;
    color: white;
}
/* Tabs When Hovered */

\.redesign .tab_white_31h_container .ajax__tab_hover .ajax__tab_inner,
.tab:hover,
.tab-container>div:hover {
    background-color: #32B5FF!important;
    border: none;
    border-top-right-radius: 3px;
    border-top-left-radius: 3px;
    color: white;
}
/* Siggy (CANNOT REMOVE) */
.text-footer {
    color:transparent;
    background:url(http://i.imgur.com/r82iN4e.png) no-repeat;
    width:350px;
    height:50px
}
}
