/*background*/
.identity {
    background: url('http://i.imgur.com/QUdrjQu.jpg') no-repeat center center fixed;
    background-size: cover;
}
.l-container.l-container--two-column-dashboard .l-content, .l-container.l-container--two-column .l-content, .l-container.l-container--two-column-dashboard .left_column, .post_avatar, .l-container.l-container--two-column-dashboard .right_column, .l-container.l-container--two-column .right_column, toastr * {
    background: transparent;
}
/*header*/
.l-header-container--refresh {
    background: #f3d0d0;
}
.tab-notice--outlined {
    border-color: #f3d0d0;
}
.tab-bar-container .tab_notice, .tab_notice .tab_notice_value, .search_control.post_layout .icon {
    color: #f3d0d0;
}
.tab_notice, .selected .tab_notice {
    background: #da9595;
}
.compose-button {
    fill: #da9595;
}
/*sidebar colours*/
.identity .controls_section li.account_header, .identity .controls_section li.contributors_header, .identity .controls_section li.editors_header, .identity .controls_section li.section_header, .identity .controls_section .control-anchor {
    color: #da9595;
}
.identity .controls_section li, .notification {
    background-color: rgba(255,255,255,0.5);
    border-radius: 10px;
    margin-bottom: 3px;
}
.identity .controls_section .selected {
    background-color: rgba(255,255,255,0.8);
}
.identity .controls_section li:not(.section_header):not(.selected_blog):active, .identity .controls_section li:not(.section_header):not(.selected_blog):hover {
    background-color: rgba(242, 206, 206, 0.8);
}
/*sidebar headings*/
.identity .controls_section li.section_header, .discover .discover-section-title {
    border-bottom: 2px solid rgba(130, 42, 42, 0.34);
    border-radius: 0;
    margin-bottom: 6px;
}
.identity .controls_section .delete_all_messages {
    color: hsl(0, 48%, 72%);
    line-height: 36px;
}
/*sub text*/
.identity .controls_section a .count, .identity .right_column .small_links a, .identity .right_column .small_links a:hover, .well {
    color: #c38a8a;
}
a#xkit-outbox-button > div {
    color: #000!important;
}
.identity .right_column:after {
    background: none;
}
/*footer text*/
.l-footer .footer_links .footer_link a {
    color: #c38a8a;
}
/*asks*/
.post-composer_note-post .note_item, .post_full.is_note .post-body .note_item, .post_full.is_note .post_body .note_item {
    background-color: #fcf4f4;
    border: 1px solid #fcf4f4;
}
.post_full.is_note .nipple, .post-composer_note-post .nipple {
    border-left: 8px solid #fcf4f4;
}
.post-composer_note-post .answerer .name, .post-composer_note-post .asker .name, .post_full.is_note .answerer .name, .post_full.is_note .asker .name, .post-composer_note-post .asker, .post-composer_note-post .answerer, .post_full.is_note .asker, .post_full.is_note .answerer, .identity .controls_section.user_list li .follow_list_item_blog .tumblelog_title, .identity .controls_section.user_list li .follow_list_item_blog, .identity .controls_section.user_list li .follow_list_item_blog .tumblelog_name, .reblog_follow_button .follow-text, .worded-follow-button .follow-text, .discover .discover-section-title, .discover .tags-and-tumblelogs .discover-tumblelogs .show-more-blogs, .elevator, .discover-controls-wrapper .types-tabs>.content-control, .messaging-inbox .inbox-compose-toggle .compose-start, .messaging-inbox .inbox-conversations .is-unread.inbox-conversation .name-container, .identity .controls_section .open_blog .currently_selected_blog, .identity .controls_section .open_blog .open_blog_link, .identity .controls_section .sub_control, .radar_footer .has_follow_button, .radar .radar_tumblelog_label .name, .radar .radar_tumblelog_label .subheading, .plus-follow-button, .sidebar_nav, .sidebar_link, .post_full .post_avatar .queue .publish_info.day, .post_full .post_avatar .queue .publish_info.time, .dashboard_options_form, .tag_psa, .dashboard_options_form .dashboard_options_form_disclaimer, .dashboard_options_form .dashboard_options_form_disclaimer a, .pages_actions_api_docs #table_of_contents a, .pages_actions_custom_themes #table_of_contents a, .pages_actions_api_docs #left_column a, .pages_actions_custom_themes #left_column a, .tab-post-type .post-type-name, .notification .notification_sentence .username, .notification .notification_sentence .banner_text, .notification, .notification .notification_sentence a, .notification blockquote, .search_sub_header .search_term_heading, .search_controls, .search_controls .search_control_button, .search_controls .search_control_link, .search_control_items .launch_button .icon_arrow_carrot_down, .search_control_items .launch_button:active, .search_control_items .launch_button:active .icon_arrow_carrot_down, .search_control_items .open .launch_button, .search_control_items .open .launch_button .icon_arrow_carrot_down, .search_control.post_layout .active, .results_end_body .search_query_highlight, .results_end_body, .tag_controls .tag, .tag_controls .track_tag_wrapper a, .multi-toasts, .multi-toasts a, .rapid-recs-container .rapid-recs .rapid-rec-title {
    color: #da9595;
}
.dashboard_options_form, .tag_psa, .notification blockquote {
    border-color: #da9595;
}
.chrome, .tx-button {
    border-color: #f3d0d0;
    background: #f3d0d0;
}
.chrome.blue, .post-form--controls .controls-container .post-form--save-button .split-button, .post-form--controls .controls-container .post-form--save-button .split-button .button-area.disabled, .post-form--controls .controls-container .post-form--save-button .split-button .button-area.disabled.active, .post-form--controls .controls-container .post-form--save-button .split-button .button-area[disabled], .post-form--controls .controls-container .post-form--save-button .split-button .button-area[disabled]:active, .post-form--controls .controls-container .post-form--save-button .split-button .dropdown-area.disabled, .post-form--controls .controls-container .post-form--save-button .split-button .dropdown-area.disabled.active, .post-form--controls .controls-container .post-form--save-button .split-button .dropdown-area[disabled], .post-form--controls .controls-container .post-form--save-button .split-button .dropdown-area[disabled]:active, .post-form--controls .controls-container .post-form--save-button .split-button .button-area, .post-form--controls .controls-container .post-form--save-button .split-button .dropdown-area, .binary_switch input[type="checkbox"]:checked ~ .binary_switch_track {
    border-color: #da9595;
    background: #da9595;
}
.identity .controls_section.user_list li .follow_list_item_blog:before, .identity .controls_section.user_list li:active .follow_list_item_blog:before, .identity .controls_section.user_list li:hover .follow_list_item_blog:before, .radar .radar_footer .radar_avatar::before {
    background: transparent;
    border-color: transparent;
}
.discover .controls_section.follow_list {
    max-height: 228px;
}
#discover_actions_discover .elevator, #search_actions_search .elevator, .plus-follow-button, .dashboard_options_form, .tag_psa, .notification {
    background: rgba(255,255,255,0.5);
}
.ui_peepr_glass, .post-forms-glass, .blue#glass_overlay, .blue#hello_glass, .blue.glass {
    background-color: rgba(236, 216, 216, 0.9);
}
#postlim_control .hide_overflow {
    display: inline;
}
.hide_overflow {
    color:#da9595!important;
}
.notifications_page .section.divider {
    background: none;
}
.highcharts-series > path:first-of-type {
    fill: #f3d0d0;
}
.highcharts-series > path:last-of-type {
    stroke: #da9595;
}
.highcharts-markers > path {
    stroke: #da9595;
}
.ui_notes .ui_note.is_friend {
    color: #da9595;
    background-color: rgba(243, 208, 208, 0.19);
}


.blog-sub-nav-details .blog-sub-nav-item.currentItem, .blog-sub-nav-details .blog-sub-nav-item.selected, .blog-sub-nav-details .blog-sub-nav-item:hover {
    box-shadow: -4px 0 0 #fafafa, 10px 0 0 #fafafa;
    background: #fafafa;
}
.blog-list-item.blog-list-item--on-light:hover, .popover--account-popover .popover .popover_menu_item:hover {
    background: #fafafa;
}
.post_avatar .post_avatar_link, .l-container.l-container--flex .l-content {
    background-color: transparent;
}
.multi-toasts *, .rapid-recs-container .rapid-recs {
    background: transparent!important;
}
}
