@-moz-document domain(vozforums.com) {
  
/* Hiệu ứng trong suốt */  
  :not(fuck)  {background-color:transparent!important}
  tbody *, .tab_list *, .thead.block_row , .tborder.tcat.tab_header, .thead.block_title, [name="vbform"] .tcat {background: transparent !important}
  #vB_Editor_001_controls, #vB_Editor_QR_controls {background: rgba(255,255,255,0.4) !important}  
  
/* Nền vOz */
  html {background:url(http://i.imgur.com/VdnSb76.png) fixed!important; background-size:100% 100%!important}
  
  FONT[color="blue"], FONT[color="purple"], FONT[color="navy"] {color:orange !important}
  
/* Hạ độ sáng nền */  
   html:after {content:"";position:fixed;top:0;bottom:0;left:0;right:0;background:#000;z-index:-2;opacity:.38}
  .alt1, [class^="alt1Active"], .alt2, .alt2Active, .panelsurround {background: rgba(0,0,0, 0.15) !important}

/* Fix lỗi tên nick đụng time */
  [style*=wrap] > STRONG {margin-right:100px} 
  
/* Fix vụ tràn hình  */
 IMG:not(.fancybox-image)  {max-width: 900px !important}
  
/* Bỏ khung phải vOz */
  TD[width="160"] {display:none !Important}

/* Bỏ viền cho thông thoáng */
  .panel ,  .fieldset,  .vBulletin_editor, .button, [id$=smiliebox] { border:none}

/* Banh rộng khung soạn thảo */
  DIV[style="width:640px"], [type="text/javascript"] + TABLE, #vB_Editor_001_textarea {min-width:100% }
  
/* Màu chữ */
  a , :not(FONT) {color:rgba(255,255,255,0.86) !important}   
  [id^=post_message] > a, .pagenav STRONG, .voz-bbcode-quote a , FIELDSET a , .vozsticky {color: orange !important}
  


/* Nền đen cho menu */
  [id^="post"] + [id^="postmenu"], [id*="_menu"] {background:rgba(0,0,0,0.8) !important}
  
/* Nền đen cho Forum Jump, Sorted By, Sorted Order, From the ... */  
