.indented0:hover .aft-commentauthorage,
#comments >.aft-postcontent:hover .comment_date,
.indented1:hover .comment_date,
.indented2:hover .comment_date,
.indented0:hover .comment_date,
#comments >.aft-postcontent:hover .permalink_img,
.indented1:hover .permalink_img,
.indented2:hover .permalink_img,
.indented0:hover .permalink_img,
#comments >.aft-postcontent:hover .memorize_img,
.indented1:hover .memorize_img,
.indented2:hover .memorize_img,
.indented0:hover .memorize_img
    {visibility: visible}
#comments >.aft-postcontent:not(:hover) .new,
.indented1:not(:hover) .new
    {opacity:.17}
#comments >.aft-postcontent:hover .new,
.indented1:hover .new,
.indented2:hover .new,
.indented0:hover .new
    {opacity:1}
/*под шапкой коммента*/
.aft-comment-inner{position: relative; margin-bottom:0}
.aft-comment-content.content{margin: 4px 0 2px}
.aft-comment-content.content .user-signature{
    position:absolute;
    bottom:0;
    left:-15px;
    z-index: 1;
    overflow: hidden;
    width:1.1em;
    height:1.9em;
    white-space: nowrap;
    background-color: rgba(214, 148, 81, 0.5);}
.aft-comment-content.content .user-signature .field-label{background-color: transparent; color: rgb(112, 88, 14);}
.aft-comment-content.content .user-signature .field-item{padding-left: 7px; background-color: rgb(190, 136, 97);}
.aft-comment-content.content .user-signature:hover{
    padding-left: 12px;
    width:auto;
    height:auto;
    white-space: initial;
    background-color: transparent;}
.aft-comment-content.content .user-signature:hover .field-label{padding-left: 7px; background-color: rgba(201, 196, 185, 0.5); color: rgb(164, 146, 111)}
.comment{margin-bottom:0; padding: 0 0 2px 5px}
.comment span.new{padding: 0 3px; font-size: 12px; background-color: #cfa57c; color: #ffe}
.comment p:first-child{margin-top:0}
.comment p:last-child{margin-bottom:0}
p >img[src*="smile"],
img[src^="https://aftershock.news/sites/all/libraries/ckeditor/plugins/smiley/"],
img[src^="https://aftershock.news/sites/all/modules/ckeditor/plugins/hkemoji/"]
    {max-width:16px!important; margin:-3px 0 -1px!important}
.aft-comment-footer{position:absolute; z-index:1; top:-.1em; right:0}
blockquote +.aft-comment-footer{position:static}
.aft-comment-footer ul.links{position:relative; overflow: inherit; width: 1.2em}
.aft-comment-footer ul.links >li{
    overflow: hidden;
    display:block;
    width: 1.2em;
    margin-right:-1.1em;
    line-height:.9em;
    white-space: nowrap;
    background-color:rgba(255,255,255,.7);}
.aft-comment-footer ul.links > li.first{width: 1.5em}
.aft-comment-footer ul.links > li.first.last{margin:.8em -1.4em 0 .2em}
.aft-comment-footer ul.links > li.first.last:hover
    {width: 5.2em; margin-right: -4.9em}
.aft-comment-footer ul.links >li{border:1px solid transparent}
.aft-comment-footer ul.links >li:hover{width:5.2em; margin-right:-5.1px; border:1px solid #999}
/*футер*/
.node-teaser:not(:last-child){border-bottom: 1px solid rgb(204, 204, 204);}
.aft-pager{position:static; z-index: 1; display:inline-block; margin: 0 7px;}
.aft-pager ~.aft-pager,
.indented1 +h2 +.aft-pager,
.ctools-collapsible-content +h2 +.aft-pager,
.aft-postcontent +h2 +.aft-pager,
.ctools-collapsible-container +h2 +.aft-pager
    {position: fixed; display: block; left:-7px; bottom:8px; height: 26px; padding:0 0 8px}
.aft-pager ~.aft-pager:hover,
.indented1 +h2 +.aft-pager:hover,
.ctools-collapsible-content +h2 +.aft-pager:hover
    {height: 29px; padding:0}
.aft-pager:hover{margin-top: 6px; background-color: rgba(215,215,221,.55)}
.aft-pager >span.more{opacity:0}
.aft-pager >span,.aft-pager >a{padding: 1px 7px 7px; opacity:.4}
.aft-pager:hover >span:not(.more),.aft-pager:hover >a{padding: 1px 7px 7px; opacity:.85}
div.aft-footer-inner{padding:0 8px}
.comment-by-node-author .aft-comment-header {background-color: rgba(234, 226, 215, .55)}
}/* ======= m.aftershock.news: 2 cols ==== */@-moz-document domain('m.aftershock.news'), regexp('https?://m.aftershock\.news\.https\.*'){
.aft-layout-wrapper{margin-top:0}
#block-system-main{border-bottom: 7px solid #eee; margin-bottom: -7px}
article.aft-article{columns: 430px}
.view-user-comments .view-content,
#comments{columns: 570px}
article.aft-article h1{padding-top: 14px}
#var1{
    display:block;
    position: fixed;
    background: rgba(255,255,255,.7);
    z-index: 1;
    height:22px;
    min-height: 20px;
    width: 1px;
    border: 1px solid #37607b;}
 #block-system-main-menu, #block-user-login, #block-system-user-menu{left:-11px; min-width:18px}
#block-system-main-menu:hover, #block-user-login:hover, #block-system-user-menu:hover{left:7px}
#block-system-main-menu .aft-block, #block-user-login .aft-block, #block-system-user-menu .aft-block
    {width:1.4em!important}
.node .field-name-field-teaser-image a,.user-picture img{max-width:20px!important; margin-top:-7px}
.aft-comment-inner{margin-bottom:0}.aft-comment-content.content{margin:2px 0}
#comments >.aft-comment .aft-comment-avatar, .aft-comment-avatar{margin:0}
.comment p:first-child{margin-top:0}.comment p:last-child{margin-bottom:0}
.aft-comment:not(:hover) .aft-comment-footer,
        #block-printing-todo-items .aft-blockcontent{display: none}
.aft-comment-footer ul.links{width: 2.2em!important;}
.aft-comment-footer ul.links >li{
    position: relative;
    width: 2.2em!important;
    padding: 0;
    list-style-type: none;}
.aft-comment-footer ul.links >li:hover{
    left:-3.4em;
    width:6.5em!important;
    margin-right:-6.4px!important;
    border:1px solid #999;}
.aft-comment-footer ul.links >li a{font-weight: bold!important;}
.aft-comment-footer ul.links >li a:hover{font-weight: bold!important; color: #f00!important}
p >img[src*="smile"]
,img[src^="https://aftershock.news/sites/all/libraries/ckeditor/plugins/smiley/"]{max-width:16px!important; margin:-3px 0 -1px!important}
}/* ======= youtube.com/embed: стало плохо читать заголовки... ======= */@-moz-document domain("www.youtube.com"){
    .ytp-chrome-top-buttons,
        .ytp-watch-later-button, .ytp-share-button{display:none}
    .ytp-title-text{white-space:normal}
}
