css:
@-moz-document domain("yuruyuri.family") {
:root {
  --kyoko: #EFD8B5;
  --yui: #7E687B;
  --china: #EFC2D6;
  --akari: #DA878D;
  --kv-star: #329CE7;
  --kv-text: #FCF42D;
  --kv-bg: #E976AB;
  --uniform-line: #735F61;
  --uniform-base-shadow: #8B5C65;
  --uniform-base: #B67A83;
  --uniform-shadow: #DFC7C3;
  --uniform: #F7F5F4;
  --tatami: hsla(61, 37%, 75%, 1); /*#D0CF99;*/
  --tatami-edge: #48545F;
  --wood: #DEC39A;
  --wood-dark: #AD8F6C;
  --wood-light: hsla(40, 45%, 83%, 1); /*#E6D2A9;*/
  --wall-white: #F2EFDE;
  --wall-green: #d8e4bf; /*#DBE4C9;*/
}

/* layout */
.column { flex-grow: 1; }
/* main */
.ui {
  background: var(--wall-white);
}
.column>.scrollable {
  background: var(--tatami);
}
body,
.notification__message,
.account .account__display-name{
  color: var(--tatami-edge);
}
.notification__display-name:hover {
  color: unset;
}

/* drawer / composer */

.navigation-bar,
.drawer__tab{
  color: var(--uniform-shadow);
}
.search__input,
.search__input:focus {
  color: var(--uniform-edge); 
  background: var(--uniform-shadow);
}
.search__icon .fa {
  color: var(--uniform-base-shadow);
}
.drawer__header,
.drawer__inner {
  background: var(--uniform-base);
}
.drawer__header a:hover {
  background: var(--uniform-base-shadow);
}
.compose-form__warning {
  color: var(--uniform-line);
  background: var(--uniform-shadow);
   /*background: var(--china); */
  box-shadow: 0 2px 6px rgba(0,0,0,0.2);
}
.autosuggest-textarea__textarea,
.compose-form__modifiers {
  background: var(--uniform);
}
.character-counter {
  color: var(--uniform);
}
.button {
  color: var(--uniform);
  background: var(--uniform-base-shadow);
}
.button:hover,
.button:active {
  background: rgba(0,0,0,0.3);
}
.button:disabled {
  box-shadow: none;
  background: rgba(128,96,96,0.5);
}

/* columns */

.column-icon,
.collapsable-collapsed,
.column-header,
.column-back-button {
  color: var(--tatami-edge);
  background: var(--wall-green);
}

/* status */

.status {
  border-color: var(--tatami-edge);
}

.status__display-name strong, .account__display-name strong {
  color: var(--yui);
}

.icon-button {
  color: var(--tatami-edge);
}
.dropdown--active .dropdown__content>ul,
.dropdown--active .dropdown__content>ul>li>a {
  background: var(--wall-white);
}
.dropdown--active .dropdown__content>ul>li>a:hover {
  color: var(--tatami-edge);
  background: var(--wood-light);
}

/* Column Settings */

.column-settings__outer {
  background: var(--wood-light);
}

.column-settings__section,
.setting-toggle {
  color: var(--uniform-line);
}
.react-toggle-track,
.react-toggle:hover:not(.react-toggle--disabled) .react-toggle-track{
  background: rgba(0,0,0,0.4);
}
.react-toggle-thumb {
  border-color: rgba(0,0,0,0.4);
}
.react-toggle--checked .react-toggle-track,
.react-toggle--checked:hover:not(.react-toggle--disabled) .react-toggle-track{
  background: var(--wood-dark);
}
.react-toggle--checked .react-toggle-thumb {
  border-color: var(--wood-dark);
}

/* Getting Started */

.column-subheading {
  color: var(--uniform);
  background: var(--wood-dark);
}
.column-link {
  color: var(--uniform-line);
  background: var(--wood-light);
}
.column-link:hover {
  background: var(--wood);
}
.getting-started p {
  color: var(--tatami-edge);
}

/* account */

.account__header {
  background: none;
}

.account__action-bar__tab>span,
.account__action-bar__tab strong{
  color: var(--tatami-edge);
}

/* scrollbar */

.ui ::-webkit-scrollbar-track,
.ui ::-webkit-scrollbar-track:active,
.ui ::-webkit-scrollbar-track:hover {
  background: rgba(0, 0, 0, 0.2);
}

.ui ::-webkit-scrollbar-thumb,
.ui ::-webkit-scrollbar-thumb:hover {
  background: rgba(0,0,0,0.3);
}

.scrollable::-webkit-scrollbar-thumb,
.scrollable::-webkit-scrollbar-thumb:hover {
  background: var(--tatami-edge);
}
}
