css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("iichan.hk") {
    * {
        padding: 0;
        margin: 0;
    }

    body, html {
        font-family: sans-serif;
        background: #FAFAFA;
        color: #333;
        min-height: 100vh;
        box-sizing: border-box;
    }

    body {
        position: relative;
        padding: 0 0 140px 0;
    }

    hr, .greeting+br, .news~br {
        display: none;
    }

    a, .logo a, .sublogo a {
        color: #5675B9;
    }

    a:hover, .reflink a:hover, .logo a:hover, .sublogo a:hover {
        color: #000;
    }

    .logo, .postarea, #delform, .theader, .catthreadlist, .greeting, .news, .news~div:not(:last-of-type) {
        width: 95%;
        max-width: 1200px;
        margin: 0 auto;
    }

    .news~div {
        padding: 1em;
    }

    .news {
        margin-top: 1em;
    }

    .logo, .postarea, .theader, .catthreadlist, .greeting, .news, .news~div {
        background: #FFF;
        border: solid #EEE;
        border-width: 0 1px;
        box-sizing: border-box;
    }

    .greeting {
        padding-bottom: 1em;
    }

    .theader, .news {
        border: 1px solid #EEE;
        border-bottom: none;
        border-radius: 5px 5px 0 0;
        text-align: center;
        text-transform: uppercase;
        font-size: large;
        padding: 1em 0;
        color: #333;
    }
    
    .postarea, .catthreadlist {
        border-radius: 0 0 5px 5px;
    }

    .logo, .greeting {
        text-align: center;
        font-size: large;
        padding: 1em 0;
        color: #333;
        border-radius: 5px 5px 0 0;
    }

    #postform {
        border-bottom: 1px solid #EEE;
    }

    .replypage .logo {
        border-radius: 5px;
    }

    .postblock {
        text-align: right;
        padding-right: 1em;
        background: none;
        color: #333;
        font-weight: normal;
    }

    .footer {
        width: 100%;
        position: absolute;
        bottom: 0;
        height: 20px;
        font-size: small;
        padding: 0;
        color: #AAA;
        text-align: center;
    }

    #delform>br {
        display: none;
    }

    #delform>div {
        margin: 1em 0;
        padding: 1em 0;
        background: #FFF;
        border: 1px solid #EEE;
        border-radius: 5px;
        position: relative;
    }

    #delform>div>table, #delform>div>table tbody, #delform>div>table tr, #delform>div>table td {
        display: block;
    }

    #delform>div>table {
        border-top: 1px solid #EEE;
    }

    #delform>div>blockquote {
        margin: .5em 1em;
    }

    #delform>div>label {
        margin-left: 1em;
    }

    .reply>blockquote, .highlight>blockquote {
        margin: .5em 0;
    }

    #delform>div:after, #delform>div>blockquote:after, .reply:after, .highlight:after {
        content: '';
        display: table;
        clear: both;
        font-size: 0;
    }

    .doubledash {
        display: none !important;
    }

    .reply, .highlight {
        padding: 1em;
        color: #333;
        background: none;
        border: none;
    }

    .highlight {
    	box-shadow: 0 0 10px rgba(86, 117, 185, .5);
    }

    .filesize {
        margin-left: 1em;
    }

    .filesize, .omittedposts, .abbrev, #delform>div>label, .reply>label, .highlight>label {
        font-size: x-small;
        color: #AAA;
    }

    .reply .filesize, .highlight .filesize {
        margin: 0;
    }

    .omittedposts, .abbrev {
        display: block;
        text-align: center;
        margin: .5em 0;
    }

    .filetitle, .replytitle {
        font-size: medium;
        color: #000;
        font-weight: bold;
        margin-right: .5em;
    }

    .postername, .commentpostername {
        font-size: medium;
        color: #333;
        font-weight: normal;
        margin-right: .5em;
    }

    .userdelete {
        float: none;
        margin: 1em auto;
    }

    .userdelete br {
        display: none;
    }

    .unkfunc {
        color: #888;
    }

    .reflink {
        margin-left: .5em;
    }

    .reflink a {
        color: #AAA;
    }

    .thumb, .nothumb {
        float: left;
        margin: 2px 1em;
    }

    .thumb {
        border: none;
        box-shadow: 0 0 5px rgba(0,0,0,.2);
    }

    .reply .thumb, .reply .nothumb, .highlight .thumb, .highlight .nothumb {
        margin-left: 0;
    }

    ol, ul {
        padding-left: 2em;
    }

    .rules {
        font-size: xx-small;
    }

    .rules>table {
        margin: 0 auto;
    }

    .banner {
        margin: 0 0 1em 0;
    }

    .spoiler {
        background: #EEE;
        color: #EEE;
    }

    .spoiler:hover {
        background: #EEE;
        color: #333;
    }

    input[type=submit], #delform~table td:nth-child(2)>a, .news~table td:nth-child(2)>a {
        display: inline-block;
        padding: .5em;
        background: #EEE;
        color: #333;
        border-radius: 5px;
        cursor: pointer;
        border: none;
        font-size: small;
    }

    input[type=submit]:hover, input[type=submit]:focus, #delform~table td:nth-child(2)>a:hover, .news~table td:nth-child(2)>a:hover {
        background: #5675B9;
        color: #FFF;
    }

    input[type=submit]:active {
        background: #333;
        color: #FFF;
    }

    input[type=text], input[type=password], textarea {
        border: 1px solid #EEE;
        padding: .5em;
        min-width: 300px;
        background: #FFF;
        border-radius: 5px;
    }

    input[type=text]:focus, input[type=password]:focus, textarea:focus {
        border-color: #5675B9;
    }

    input[type=checkbox] {
        margin-right: .5em;
    }

    input[type=radio] {
        margin: 0 .5em;
    }

    #delform~table, .news~table {
        border: none;
        border-spacing: 5px;
        margin: 1em auto;
    }

    #delform~table td, .news~table td {
        border: none;
    }

    #delform~table td:nth-child(2), .news~table  td:nth-child(2) {
        visibility: hidden;
        font-size: 0;
    }

    #delform~table td:nth-child(2)>a, .news~table td:nth-child(2)>a{
        visibility: visible;
        text-decoration: none;
        font-weight: bold;
        margin: 0 .2em;
    }

    blockquote blockquote, blockquote p {
        margin: .2em 0;
    }

    /*Dirty hacks for "back" button*/
    body {
        visibility: hidden;
        line-height: 0;
    }

    body>* {
        visibility: visible;
        line-height: 1.25;
        line-height: initial;
    }

    .logo+hr+a {
        display: block;
        position: fixed;
        left: 0;
        top: 50%;
        z-index: 50;
        transform: translate(-40px, -50%);
        padding: 1em 1em 1em 50px;
        text-align: center;
        background: #FFF;
        border-radius: 0 5px 5px 0;
        text-decoration: none;
        transition-duration: .2s;
        border: 1px solid #EEE;
    }

    .logo+hr+a:hover {
        transform: translate(0, -50%);
    }

    .replypage .postarea {
        border-radius: 5px 5px 0 0;
    }

    .replypage #delform>div {
        margin-top: 0;
        border-top: none;
        border-radius: 0 0 5px 5px;
    }

    .header {
        background: #EEE;
        color: #333;
    }

    .list {
        background: #FFF;
        border: none;
    }

    .list div a {
        color: #333;
    }

    .list div a:hover {
        background: #5675B9;
        color: #FFF;
    }

    .adminbar {
        display: block;
        padding: 1em;
        box-sizing: border-box;
        float: none;
        visibility: hidden !important;
        text-align: center;
        font-size: 0;
    }

    .adminbar ~ .adminbar {
        width: 100%;
        position: absolute;
        height: 120px;
        bottom: 20px;
    }

    .adminbar a {
        display: inline-block;
        font-size: medium;
        padding: .2em .5em;
        margin: .2em;
        visibility: visible;
        text-decoration: none;
        background: #FFF;
        border-radius: 5px;
        border: 1px solid #EEE;
    }

    .adminbar br {
        display: block;
        margin: .5em;
    }

    .adminbar br~a {
        background: none;
        border-radius: 0;
        text-decoration: underline;
        font-size: small;
        border: none;
    }
}
