css:
@-moz-document domain("trello.com") {
    .body-board-view .list-card-labels .card-label {
        font-weight: bold;
        color: #fff;
        line-height: 14px !important;
        height: auto !important;
        padding: 2px 6px !important;
        margin-right: 5px !important;
        width: auto !important;
        text-shadow: none !important;
        font-size: 10px !important;
        margin-left: 0 !important;
        min-width: 6px !important;
    }
    .body-board-view .list-card-labels {
        display: flex !important;
    }
    .card-short-id {
        display: inline-block !important;
        position: absolute;
        top: 10px;
        right: 10px;
        color: #aaa;
    }
    .card-label {
        text-shadow: none !important;
    }
    .button-link:hover {
        background: #9dbde0;
        border-color: #89b0da;
    }
    .label-list-item.is-active.selected .label-list-item-link,
    .label-list-item.selected .label-list-item-link,
    .pop-over-card-list li.selected a,
    .pop-over-emoji-list li.selected a,
    .pop-over-member-list li.selected a,
    .pop-over-list.navigable li.selected > a,
    .pop-over-list li > a:hover {
        background-color: #9dbde0;
    }
    .member {
        border-radius: 3px !important;
    }
    .board-menu {
       background-color: #fdfdfe;
    }
    .board-menu-navigation-item-link:hover {
        background-color: #edeff0;
    }
    #board,
    .list {
        background: #e2e4e6;
    }
    .list-wrapper {
        width: 24.375%;
        /* 25% */
        
        margin: 0 0.3125%;
        /* 0.625 */
    }
    .list-card-title {
        font-weight: bold;
        font-size: 13px;
    }
    .list-card-labels .card-label {
        opacity: .8;
        height: 2px;
    }
    #trellists .show-all,
    #trellists .hide-all {
        color: #ccc;
    }
    .badge.is-due-now,
    .badge.is-due-now .badge-icon {
        background-color: transparent;
        color: #CF513D;
    }
    .badge.is-due-past,
    .badge.is-due-past .badge-icon {
        background-color: transparent;
        color: #EC9488;
    }
    .badge {
        padding: 0 2px;
    }
    .badges {
        margin-left: -4px;
        margin-top: -2px;
        padding-bottom: 0;
    }
    .badge-text {
        font-size: 11px;
    }
    .badge .icon-sm {
        font-size: 10px;
        width: 14px;
    }
    .member {
        border-radius: 50%;
    }
    .list-card-members .member {
        margin-bottom: 2px;
        margin-top: -4px;
        display: inline-block;
        float: none;
    }
    #board .member,
    #board .member-avatar {
        width: 20px;
        height: 20px;
    }
    .member-initials {
        height: 20px;
        line-height: 20px;
    }
    /* no scrolling lists */
    /*
    body {
        font-family: -apple-system, 'BlinkMacSystemFont', Roboto, Helvetica, Arial, sans-serif;
        font-weight: 300;
        -webkit-font-smoothing: subpixel-antialiased;
    }
    .list {
        max-height: inherit;
    }
    #board {
        overflow-y: auto;
        margin-bottom: 0 !important;
        padding-top: 10px !important;
    }*/
    /* bright style */
    
    body {
        box-shadow: 0 2px 8px 0 rgba(0, 0, 0, 0.11);
    }
    #board,
    .list {
        background: #e6e7eb;
    }
    .icon-lg.light {
        color: rgba(0, 0, 0, .4);
    }
    .list-card {
        border: none;
        box-shadow: 0 1px 1px 0 rgba(0, 0, 0, 0.10);
        margin-bottom: 10px;
    }
    .list-card-title {
        font-weight: 500;
        letter-spacing: 0.2px;
        font-size: 12px;
        margin-top: 2px;
        margin-bottom: 6px;
    }
    .list-card-details {
        padding: 8px 8px 2px 10px;
    }
    .open-card-composer {
        font-size: 11px;
    }
    .open-card-composer {
        border-radius: 3px !important;
    }
    .open-card-composer:hover {
        background-color: rgba(0,0,0,.1) !important;
    }
    .list-wrapper.mod-add.is-idle {
        background: rgba(0,0,0,.05);
    }
    .list-wrapper.mod-add.is-idle:hover {
        background: rgba(0,0,0,.1);
    }
    .list-wrapper.mod-add .placeholder {
        color: #4d4d4d;
    }
    #trellists {
        border: 1px solid #eaeaea;
        background: none;
    }
    #trellists .show-list {
        color: #333;
    }
    .window,
    .pop-over.mod-search-over {
        background-color: #fff !important;
    }
    .action-comment {
        box-shadow: none !important;
    }
    
    
    
    
    
    
    
    
    
    
    
    
    
    
    
    
    
    /* labels order*/
    .list-card-labels,
    .js-card-detail-labels-list {
        display: flex !important;
        flex-direction: row;
        justify-content: flex-start;
        align-items: center;
    }
    .list-card-labels [title="F"],
    .card-detail-item-labels [title="F"] {
        order: 1;
        background: none;
        color: #cc0000 !important;
        font-size: 1.1em !important;
    }
    .list-card-labels [title="B"],
    .card-detail-item-labels [title="B"] {
        order: 2;
        background: none;
        color: #0000bb !important;
        font-size: 1.1em !important;
    }
    .list-card-labels [title="bug"],
    .card-detail-item-labels [title="bug"] {
        order: 3;
        background-color: #db5736 !important;
    }
    .list-card-labels [title="task"],
    .card-detail-item-labels [title="task"] {
        order: 4;
        background-color: #7983ba !important;
    }
    .list-card-labels [title="enhancement"],
    .card-detail-item-labels [title="enhancement"] {
        order: 5;
        background-color: #84ba7a !important;
    }
    .list-card-labels [title="proposal"],
    .card-detail-item-labels [title="proposal"] {
        order: 6;
        background-color: #e5d713 !important;
    }
    .list-card-labels [title="on hold"],
    .card-detail-item-labels [title="on hold"] {
        order: 7;
        background-color: #edb022 !important;
    }
    .list-card-labels [title="discuss"],
    .card-detail-item-labels [title="discuss"] {
        order: 8;
        background-color: #b079ba !important;
    }
    .list-card-labels [title="info"],
    .card-detail-item-labels [title="info"] {
        order: 9;
        background-color: #99999a !important;
    }
    .js-card-detail-labels-list .card-detail-item-add-button {
        order: 9999;
    }
}
