css:
@-moz-document domain("tumblr.com") {
/*
urls on the domain: tumblr.com
*/

	.reblog-avatar {
		display:none;
    visibility: hidden;
    clear:both;
	}
	.reblog-list-item .reblog-tumblelog-name {
		font-weight: normal;
    font-size: 14px;
	}
	.reblog-list-item .reblog-header {
		padding-left: 0;
	}
	.reblog-content {
		border-left: 2px solid #E7E7E7;
    padding-left: 10px;
	}
	.reblog-list-item .original-reblog-content {
	padding: 10px 20px;
	}
	.reblog-list-item {
		padding: 5px 20px;
    border-bottom: none;
	}
	.reblog-list-item .reblog-header {
		margin-bottom: 3px;
	}
	.reblog-list-item.contributed-content {
		border-top: none;
	}
  
}
