css:
@-moz-document domain("bbs.byr.cn") {
body {
    font-family: "Helvetica Neue", Helvetica, Arial, sans-serif;
    font-size: 14px;
    background: #fff;
}
a:hover {
    color: #ff4081;
}
a:visited {
    color: #ff4081;
}
a:focus {
    outline: none;
}
textarea,
input,
.input-text,
select {
    background: #fff;
    border: 1px solid #26C6DA;
}
.input-text:focus,
textarea:focus {
    border: 1px solid #ff4081;
    box-shadow: 0 0 2px #ff4081;
    outline: none;
}
.x-child span.active > a,
.x-child span.active > a {
    font-weight: bold;
    color: #f50057;
}
#main {
    border: 0;
    box-shadow: rgba(0, 0, 0, 0.12) 0px 1px 6px, rgba(0, 0, 0, 0.12) 0px 1px 4px;
    border-radius: 2px;
    margin-left: 176px;
}
#columns .column {
    width: 32.8%;
    padding: 2px;
}
.widget {
    border: 0;
    box-shadow: rgba(0, 0, 0, 0.12) 0px 1px 6px, rgba(0, 0, 0, 0.12) 0px 1px 4px;
    border-radius: 2px;
    z-index: 99;
}
.widget-head {
    height: 42px;
    line-height: 42px;
    border: 0;
    background-color: rgba(0, 0, 0, 0)!important;
    border-bottom: 1px solid #ddd;
}
.widget-content {
    border: 0;
}
.widget-title {
    margin-left: 6px;
}
.widget-title a {
    font-weight: 300;
    font-size: 18px;
    color: #666!important;
}
a.collapse samp {
    width: 15px;
}
.w-list-line li {
    border: 0;
    height: 28px;
    line-height: 28px;
}
.w-list-line li:hover {
    background-color: rgba(0, 0, 0, 0.03);
}
.w-list-line a {
    color: #333;
}
.w-list-line li a:hover {
    background-color: rgba(0, 0, 0, 0);
}
.w-tab-content {
    border: 0;
}
li.tab-normal {
    font-weight: 200;
    color: #26C6DA;
    background: #fff;
    font-size: 12px;
    padding: 8px 12px;
    border-radius: 2px;
    border: 0;
}
li.tab-normal.tab-down {
    color: #fff;
    background: #26C6DA;
}
li.tab-normal:hover {
    color: #fff;
    background: #26C6DA;
}
#u_login,
#top_menu,
#xlist,
#notice {
    font-size: 12px;
    border: none;
}
#top_menu ul li a {
    text-decoration: none;
}
#menu {
    width: 170px;
    box-shadow: rgba(0, 0, 0, 0.12) 0px 1px 10px, rgba(0, 0, 0, 0.12) 0px 1px 10px;
}
#u_login {
    font-size: 14px;
    padding: 0;
    padding-bottom: 4px;
    border-radius: 0;
    border-bottom: 1px solid rgb(224, 224, 224);
}
.u-login-list {
    margin-left: 0;
    margin-top: 0;
}
.u-login-list li {
    font-size: 12px;
    padding: 6px 2px;
    padding-left: 12px;
}
.u-login-list li:hover {
    background-color: #eee;
}
.u-login-list li a {
    color: rgba(0, 0, 0, 0.87);
}
.u-login-id {
    background-color: #26C6DA;
    padding: 16px 0;
    font-size: 16px;
    color: #fff!important;
}
.u-login-id a {
    color: #fff;
}
#xlist {
    padding: 0;
}
#xlist > ul > li {
    min-height: 34px;
    vertical-align: middle;
}
#xlist > ul > li > span {
    height: 36px;
    border-radius: 0;
    border: none;
    vertical-align: middle;
}
#xlist > ul > li > span:hover {
    background-color: #26C6DA;
}
#notice {
    margin-top: -4px;
    background-color: #fff;
}
a.button {
    padding: 4px 8px;
    font-weight: 200;
    color: #fff;
    background: #26C6DA;
    font-size: 12px;
    border-radius: 2px;
    border: 0;
}
.page-normal {
    border: none;
}
.page-normal a {
    margin: 0 2px;
    padding: 2px 6px!important;
    border: none;
    color: #333!important;
}
.page-normal a:hover {
    color: #fff!important;
    background-color: #26C6DA;
}
.page-select a {
    margin: 0 2px;
    padding: 2px 6px!important;
    border: none;
    color: #fff!important;
    background-color: #26C6DA;
}
.b-head {
    color: #333;
    background-color: #fff;
    height: 42px;
}
.b-head .n-left {
    font-weight: 600;
    color: #555!important;
    font-size: 18px;
    padding: 6px;
}
.b-head .n-right {
    color: #333!important;
    margin-top: 6px;
}
.article td {
    background-color: inherit;
}
.a-wrap.corner {
    padding: 10px 8px;
    border: none;
    box-shadow: rgba(0, 0, 0, 0.12) 0px 1px 6px, rgba(0, 0, 0, 0.16) 0px 1px 4px;
    border-radius: 2px;
    margin: 2px 6px;
}
.a-func li:hover {
    background-color: rgba(153, 153, 153, 0.2);
}
.a-func li {
    border-radius: 2px;
    transition: all 0.2s;
    margin-left: 2px;
    padding: 6px 6px;
}
.a-func li a {
    color: #000;
}
span.a-pos {
    color: #555;
}
.a-left {
    width: 192px;
    border: none;
}
.a-nice-comment {
    border-radius: 2px;
}
.article .a-left {
    background-color: rgba(153, 153, 153, 0.06);
}
.a-u-img img {
    border: none;
    border-radius: 2px;
}
.c63f.a-back,
.a-func-like {
    padding: 6px 6px;
    color: #666;
    border-radius: 2px;
}
.a-back:hover {
    background-color: rgba(153, 153, 153, 0.2);
}
.a-status li {
    margin-top: 4px;
}
.a-status li a {
    vertical-align: middle;
    padding: 6px;
    border-radius: 2px;
    color: #000;
}
.a-status li a:hover {
    background-color: rgba(153, 153, 153, 0.2);
}
.button {
    border-radius: 2px;
    color: #fff;
    background: #26C6DA;
    border: none;
}
.ubb-img-tab li div {
    color: #26C6DA;
}
.ubb-img-tab li {
    border: 1px solid #26C6DA;
}
.ubb-img-tab .selected {
    background-color: #26C6DA;
}
.ubb-img {
    border-color: #26C6DA;
}
.tab-down {
    background: #26C6DA;
}
.tab-normal {
    background: #fff;
    color: #26C6DA;
}
.tab-normal:hover {
    background: #26C6DA;
}
.board-list td {
    border-bottom: 1px solid rgb(224, 224, 224);
    padding-top: 8px;
    padding-bottom: 5px;
}
.board-list tr:hover td {
    background-color: #f8f8f8;
}
.bg-odd {
    background-color: transparent;
}
.board-list thead tr th {
    padding: 10px 0;
}
.board-list thead th.title_8 {
    padding-left: 16px;
}
.title_8 {
    width: 31px;
    padding-left: 16px;
}
.board-list thead tr {
    border: 0;
    border-bottom: 1px solid rgb(224, 224, 224);
}
.board-list th {
    padding: 6px 0;
    background-color: #f8f8f8;
}
.b-content {
    border: none;
    padding: 0px;
    margin: 0 6px;
}
.a-comment {
    border: none;
    background-color: transparent;
}
.title_12 a {
    word-wrap: break-word;
    word-break: break-all;
}
.mbar {
    line-height: 24px;
    background-color: #27c5da;
    padding: 0;
}
.mbar a:hover {
    border-radius: 0;
    background-color: #27c5da;
    color: #fafafa;
}
.mbar .selected a {
    color: #fff;
    background-color: #27c5da;
    border-radius: 0;
}
.mbar li.selected {
    position: relative;
    height: 100%;
}
.mbar li.selected:after {
    content: "";
    height: 2px;
    width: 100%;
    position: absolute;
    bottom: 0;
    left: 0;
    background-color: #ff4081;
}
.c-mbar {
    height: 26px;
    border: none;
}
.a-nice-comment-item {
    padding-bottom: 28px;
}
.b-content.corner {
	margin-top: 4px;
}
.t-pre-bottom {
    margin-top: 6px;
    margin-bottom: -4px;
}
.c-mbar li a{
    color: #27c5da;
}
.c-mbar li .select{
    border-radius: 2px;
    background-color: #ff4081;
}
.c-mbar li a:hover {
    border-radius: 2px;
    background-color: rgba(255, 64, 129, 0.84);
}
.c-mbar .ico-pos-dot {
    display: none;
}
.mail-list tbody tr td {
	border-bottom: 1px solid #eee;
    height: 30px;
}
.t-pre {
    line-height: 20px;
    height: 30px;
	border-bottom: 1px solid #eee;
}
.mail-info-bar {
    border-color: #27c5da;
	border-radius: 4px;
}
.mail-info-scroll {
	background-color: #27c5da;
	border-radius: 4px;
}
#c_content {
    border: none;
    padding: 0;
}
.list-block header {
    color: #666;
    border-bottom: 1px solid #eee;
}
.submit {
	background: #fff;
    border: 1px solid #27c5da;

}
.ui-widget-header {
    border: none;
    color: #27c5da;
    border-bottom: 1px solid #eee;
    background: #fff;
    font-weight: bold;
    border-radius: 0;
}
button.ui-state-default.ui-button {
    border: none;
    color: #27c5da;
    background: #fafafa;
    font-weight: bold;
    border-radius: 0;
    height: 28px;
}
button.ui-state-default.ui-button:focus {
	outline:#27c5da solid 1px;
}
.ui-dialog .ui-dialog-buttonpane {
	border: none;
}
.u-title-2 input {
    margin-right: 4px;
}
}
