/* ==UserStyle==
@name           LoveRead
@namespace      USO Archive
@author         misha postnikov
@description    `Стиль для love read`
@version        20180814.13.7
@license        NO-REDISTRIBUTION
@preprocessor   uso
==/UserStyle== */
@-moz-document domain("loveread.ec"),
domain("loveread.me") {
    /*AGENT_SHEET*/
    /* [id="header"],
    td[height="216px"],
    [class="lx_ad"] {
        display: none!important 
    }*/
    [class="table_view_gl"],
    [class="table_gl"] {
        border: 1px dotted #bbb!important;
        padding: 20px!important;
        background: #cc1!important;
        border-radius: 30px!important
    }

    td[align="left"][valign="top"][class="bg_td"],
    [class="table_gl"],
    [class="blHeader"],
    [class="blCenter"],
    [class="blFooter"],
    [class="td_top_color"],
    [class="td_center_color"],
    [class="td_comment_color"],
    [class="letter_nav_bg"],
    [class="letter_nav"],
    [class="blockBook"],
    [class="table_view_gl"],
    [class="MsoNormal"],
    [class="authorBooks"],
    [class="bodyUnit"],
    td[style="padding:4px;"],
    td[background="img/bg_view_global.jpg"],
    div.textBook,
    p {
        max-width: 900px!important;
        margin: 0 auto!important
    }

    [class="MsoNormal"],
    div.textBook,
    p {
        padding-bottom: 10px!important;
        font-family: 'PT Serif', 'Liberation Serif', 'Bookman Old Style', 'Times New Roman', 'Lora', Georgia, 'Palatino Linotype', 'Book Antiqua', Palatino, Times, serif!important;
        line-height: 1.25!important;
        text-align: justify!important;
        -webkit-hyphens: auto!important;
        -moz-hyphens: auto!important;
        -ms-hyphens: auto!important
    }

    div.numberPage {
        margin: 20px auto!important;
        font-family: 'PT Sans', 'Liberation Sans', 'Open Sans', 'Helvetica Neue', Helvetica, Arial, Tahoma, Verdana, sans-serif !important;
        color: #9a9a9a!important
    }

    .textBook h2 a {
        font-family: 'PT Sans', 'Liberation Sans', 'Open Sans', 'Helvetica Neue', Helvetica, Arial, Tahoma, Verdana, sans-serif !important;
        color: #0707f7!important
    }

    h1 {
        font-family: 'PT Sans', 'Liberation Sans', 'Open Sans', 'Helvetica Neue', Helvetica, Arial, Tahoma, Verdana, sans-serif !important;
        text-shadow: 1px 2px 2px #555, 0 0 4px #aaa!important;
        text-align: left!important
    }

    h2 {
        font-family: 'PT Sans', 'Liberation Sans', 'Open Sans', 'Helvetica Neue', Helvetica, Arial, Tahoma, Verdana, sans-serif !important;
        text-shadow: 1px 2px 2px #555, 0 0 4px #aaa!important;
        text-align: center!important
    }

    tr.td_top_color > *,
    tr.td_center_color > *,
    tr.td_center_color > *,
    tr.td_comment_color > * {
        padding: 10px !important;
    }

    h3,
    h4,
    h5,
    h6 {
        font-family: 'PT Sans', 'Liberation Sans', 'Open Sans', 'Helvetica Neue', Helvetica, Arial, Tahoma, Verdana, sans-serif !important;
        text-shadow: 1px 2px 2px #555, 0 0 4px #aaa!important
    }

    hr {
        margin: 20px 0!important;
        height: 1px!important;
        border: none!important;
        background: -webkit-gradient(linear, left top, right top, from(transparent), color-stop(rgba(0, 0, 0, 0.87)), to(transparent))!important;
        background: -webkit-linear-gradient(left, transparent, rgba(0, 0, 0, 0.87), transparent)!important;
        background: -o-linear-gradient(left, transparent, rgba(0, 0, 0, 0.87), transparent)!important;
        background: linear-gradient(to right, transparent, rgba(0, 0, 0, 0.87), transparent)!important
    }
}
