css:
@-moz-document domain("yasinskii.ru") {
/*AGENT_SHEET*/
/* Version 1.9.12 */
/* C фиксом dropdown'а, за него спасибо Randr`у */
* {
    margin: 0;
    padding: 0;
    -webkit-box-sizing: border-box;
    box-sizing: border-box;
    background-color: transparent;
}

[id="book_content"] {
    font-family: inherit !important;
    font-size: inherit !important;
    line-height: inherit !important;
}

html {
    font: normal 16px/1.4 sans-serif;
}

[class="text-edit-btn"],
[class="glyphicon glyphicon-lock"],
[class="glyphicon glyphicon-th-list"],
[class="glyphicon glyphicon-comment"],
[class="btn btn-success btn-omnireader"],
[class="btn btn-primary"],
[class="btn btn-default"],
[class="glyphicon glyphicon-menu-left"] {
    font-family: 'Glyphicons Halflings'!important
}

[class="fa fa-rub"],
[class="fa fa-bell-o"],
[class="fa fa-users"],
[class="fa fa-sign-out"],
[class="fa fa-comments-o"] {
    font-family: FontAwesome !important
}

[class="container"] {
    background-color: transparent!important
}

body {
    background-color: grey!important
}

div[class="content"],
div[class="content dragselectlinktext-textcursor"] {
    background-color: #EBE5D7!important;
    padding: 20px!important
}

[class="footer"] > [class="footer"] {
    background-color: #EBE5D7!important;
    padding: 20px!important
}

div[class="container"] > div[class="row"] > div[class="col-lg-12"] > div[class="content"],
div[class="container"] > div[class="row"] > div[class="col-lg-12"] > div[class="content dragselectlinktext-textcursor"],
div[class="container"] > div[class="row"] > div[class="col-lg-12"] > div[class="footer"] > [class="footer"] {
    -webkit-box-shadow: 0 .25px 1px 0 rgba(0, 0, 0, 0.25)!important;
    box-shadow: 0 .25px 1px 0 rgba(0, 0, 0, 0.25)!important;
    border: 1px solid #000!important
}

[class="meta"],
[class="navbar navbar-default navbar-custom navbar-fixed-top navbar-inverse"] {
    text-shadow: 0 0 4px #aaa!important;
    text-shadow: 1px 2px 2px #555!important
}

[class="grey-text text-darken-4 main-menu_item"],
[class="logout main-menu_item"] {
    text-shadow: none!important
}

[class="site-heading"] {
    padding: 75px 0 0!important
}

[class="small"] {
    display: none!important
}

[class="site-heading"] {
    font-family: 'PT Sans', 'Liberation Sans', 'Open Sans', 'Helvetica Neue', Helvetica, Arial, Tahoma, Verdana, sans-serif !important;
    text-shadow: 0 0 4px #aaa!important;
    text-shadow: 1px 2px 2px #555!important;
    margin-top: 10px!important;
    margin-bottom: 30px!important
}

h1,
h2 {
    font-family: 'PT Sans', 'Liberation Sans', 'Open Sans', 'Helvetica Neue', Helvetica, Arial, Tahoma, Verdana, sans-serif !important;
    text-shadow: 0 0 4px #aaa!important;
    text-shadow: 1px 2px 2px #555!important;
    font-weight: 400!important
}

h3,
h4,
h5,
h6 {
    font-family: 'PT Sans', 'Liberation Sans', 'Open Sans', 'Helvetica Neue', Helvetica, Arial, Tahoma, Verdana, sans-serif !important;
    font-weight: 400!important
}

[style="font-size: 24px; font-weight: 600; font-family: 'Open Sans','Helvetica Neue',Helvetica,Arial,sans-serif"],
[style="color: #333"] {
    font-family: 'PT Mono', 'Liberation Mono', Menlo, Monaco, Consolas, 'Courier New', Courier, monospace !important;
    font-weight: lighter!important
}

[class="category-item"] {
    font-family: 'PT Mono', 'Liberation Mono', Menlo, Monaco, Consolas, 'Courier New', Courier, monospace !important;
    line-height: .9!important
}

[style="font-size: 14px; text-align: center; border: 1px dashed #d0c8c8;"] {
    font-family: 'PT Mono', 'Liberation Mono', Menlo, Monaco, Consolas, 'Courier New', Courier, monospace !important;
    line-height: 1.25!important;
    background-color: #f5f5f5!important;
    border: 1px solid #d0c8c8!important;
    -webkit-box-shadow: 0 .25px 1px 0 rgba(0, 0, 0, 0.25)!important;
    box-shadow: 0 .25px 1px 0 rgba(0, 0, 0, 0.25)!important;
    padding: 10px!important
}

pre {
    font-family: 'PT Mono', 'Liberation Mono', Menlo, Monaco, Consolas, 'Courier New', Courier, monospace !important
}

[style="font-size: 18px;"],
[style="font-size: 24px;"],
[class="wblog-par"],
[class="text-muted"],
[style="font-family: Verdana; font-size: 24px;"] {
    /*font-family: 'PT Serif', 'Liberation Serif', 'Bookman Old Style', 'Times New Roman', 'Lora', Georgia, 'Palatino Linotype', 'Book Antiqua', Palatino, Times, serif!important;
    */
    font-family: 'PT Sans', 'Liberation Sans', 'Open Sans', 'Helvetica Neue', Helvetica, Arial, Tahoma, Verdana, sans-serif !important;
    font-weight: 400 !important;
    text-align: justify !important;
    word-wrap: break-word!important;
    line-height: 1.25 !important;
    text-indent: 20px!important;
}

[style="font-size: 24px; font-weight: bold;"],
[style="font-family: Verdana; font-size: 36px; font-weight: bold;"],
[style="font-family: Verdana; font-size: 24px; font-weight: bold;"],
[style="font-family: Verdana; font-size: 36px;"],
[style="font-size: 36px;"],
[style="font-size: 36px; font-family: Verdana; font-weight: bold;"],
[style="font-size: 24px; font-family: Verdana; font-weight: bold;"] {
    font-weight: 700 !important;
    font-family: 'PT Sans', 'Liberation Sans', 'Open Sans', 'Helvetica Neue', Helvetica, Arial, Tahoma, Verdana, sans-serif !important;
}

html > body > div[class="container"][style="width: 100%; background-color: #09090B"] * {
    background-color: #09090B !important;
    margin: 0;
    padding: 0;
    -webkit-box-sizing: border-box;
    box-sizing: border-box;
}

hr {
    height: 1px!important;
    border: none!important;
    background: -webkit-gradient(linear, left top, right top, from(transparent), color-stop(rgba(0, 0, 0, 0.87)), to(transparent))!important;
    background: -webkit-linear-gradient(left, transparent, rgba(0, 0, 0, 0.87), transparent)!important;
    background: -o-linear-gradient(left, transparent, rgba(0, 0, 0, 0.87), transparent)!important;
    background: linear-gradient(to right, transparent, rgba(0, 0, 0, 0.87), transparent)!important
}

[class="create-reply"] {
    margin-bottom: 15px !important;
}

.navbar-inverse .navbar-nav > .open > a {
    background-color: rgba(0, 0, 0, 0);
}

.navbar-inverse .navbar-nav > .open > a:focus {
    background-color: rgba(0, 0, 0, 0.15);
}

.navbar-inverse .navbar-nav > .open > a:hover {
    background-color: rgba(0, 0, 0, 0.15);
    color: rgba(255, 255, 255, 0.8);
}

div[aria-expanded="false"] > ul[class="nav navbar-nav navbar-right"] {
    display: none !important;
}

.note-air-editor *,
.note-editable *,
[contenteditable="true"] {
    background-color: white !important;
}



.btn-group,
.popover-content {
    padding: 2px !important;
    margin: auto !important;
}

.note-recent-color,
div.note-para.btn-group > button {
    margin: 2px !important;
}
