css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("kino-fox.ru") {
  .timers-zone .timers-out {
    border-radius: 3px 0 3px 3px;
  }
  .timers-zone .phone-out {
    border-radius: 3px 3px 0 0;
  }
  .topfilm-zone .topfilm {
    border-radius: 3px;
  }
  .actions-zone > .container {
    border-radius: 3px;
    background: rgba(0,0,0,0.3);
  }
  .actions-zone > .container .action-out .action {
    border-radius: 3px;
    border: 0;
  }
  .main-zone .container {
    border-radius: 3px;
  }
  .main-zone .head .message {
    border-radius: 3px;
  }
  .film-out {
    border-radius: 3px;
    border: 0;
  }
  .film-out .poster-big .poster {
    border-radius: 3px 0 0 3px;
  }
  .poll-item {
    border-radius: 3px;
  }
  .soon-film .image, .soon-film .image img {
    border-radius: 3px;
  }
  .soon-film .image .meta {
    border-radius: 3px 3px 0 0;
  }
  .socialpages-zone > .container {
    border-radius: 3px;
  }
  body.filmpage .gallery .poster:first-of-type {
    border-radius: 3px;
  }
  .socialpages-zone .socials > a [class*="icon-"] {
    border-radius: 3px;
  }
  .actionbar-zone .actionbar-out .logo-out.riologo-out, .actionbar-zone .actionbar-out .logo-out.gamelogo-out, .actionbar-zone .actionbar-out .logo-out.neologo-out {
    border-radius: 3px 0 0 3px;
  }
  .actionbar-zone .container .phone-out {
    border-radius: 3px 3px 0 0;
  }
  .actionbar-zone .actionbar-out .rioaddr, .actionbar-zone .actionbar-out .gameaddr, .actionbar-zone .actionbar-out .neoaddr {
    border-radius: 0 0 3px 0;
  }
  .main-zone .main-block {
    border-radius: 3px 0 0 3px;
  }
  .news-block {
    border-radius: 3px;
  }
  .poll-block .poll {
    border-radius: 3px;
  }
}
