css:
@-moz-document domain("www.over.gg") {
#thread-list a,
.subforum first a,
.block main mod-forum a,
.forum-container wf-card a{
    background-color: rgba(0, 0, 0, 0);
}
html,
body {
    background: rgb(41, 41, 41) !important;
}
#wrapper {
background: #272727;
}

.event-desc,
.wf-nav,
.wf-module-header,
.wf-card {
    background: rgb(54, 54, 54);
    color: #b1b1b1 !important;
}
.wf-card {
    border: 1px solid #191818;
}
.module-item-title,
.module-header,
.h-match-team-name {
    color: #b1b1b1 !important;
}
#sidebar-left a:hover {
    background-color: rgba(54, 54, 54, 1);
}
.module-item-count {
    background-color: rgba(54, 54, 54, 0.73);
    color: #deded4 !important
}

.team-roster-item-name-alias {
    color: #ddd ;
}
.article-body {
    color: #ddd;
}

div.hc-container div.wf-card a {
    color: #b5b5ab;
    background-color: #212121;
}

div.hc-container .wf-card a:hover{
    background-color: #151515;
}

a.wf-module-item{
    color: #deded4 ;
    background-color: #212121;
}
a.wf-module-item:hover{
 	background-color:#151515;
}

div.wf-nav-item-title{
    color:#ddd
}


input#search-bar{
    background-color:#666;
    border: 1px solid grey;
    color:#deded4;
}

.forum-group-header{
    color:#ddd
}

div.article-meta a  {
    background-color:rgba(0,0,0,0);
}

div.article-meta a:hover{
	color:blue;
}

.article-body .wf-card a{
    color:blue !important;
}

.article-body blockquote {
     background: #292828;   
}

.post-body blockquote {
 	background-color:#2b2b2b;   
}

div#thread-container p{
    color:#ddd;
}

div.post-header.noselect{
    background:#464646;
}

.post-author.group-, .post-num {
    color:#ddd;
}

.post-body q {
    background:#464646;
    color:#ddd;
}

.quote-attr{
    color:#88a7b3 !important;
}

.post-frag-count.positive {
    color:#4dc54d;
}

.post-frag-count.negative {
    color:red;
}

.thread-header-title{
    color:#ddd;
}

.block.main .description {
color:#ccc;
}

.div{
color:#ddd
}
  
.block.main.mod-forum{
  color:#ddd;
}
  
.block.pages .post-count{
    color:#ddd;
}

a:link, a:visited, a:active {
    color: #4da5ca;
}

.wf-module-header{
border-bottom: 1px solid rgb(33, 33, 33);
}

.wf-module-item {
border-top: 1px solid #4e3d3d;
}

.wf-module-item.mod-match{
    border-bottom: 1px solid #4e3d3d;
} 

.match-header, .match-info {
border-bottom: 1px solid #616161;
}

.match-games{
background:#363636;
}

.game-switch-map-name {
 color:#ddd;
}

game-switch.mod-first.mod-active{
font-weight:bold;
}

.match-header-link-name.mod-1, .match-header-link-name.mod-2 {
color:#bbb;
}

.match-header-vs-score-loser{
color:#bbb;
}

#profile-header {
color:#ddd;
}

table.list-table.profile-table{
color:#ddd;
}

table.list-table.profile-table td{
color:#efefef;
}

div.post-body p {
color:#ddd;
}

.form-section, div.form-section, .form-label {
color:#ddd;
}

div.form-label span {
 color:#bbb;
}

a.match-item{
border-top:1px solid #504431;
}

div.wf-card div div div   {
    color:#ccc;
}

.bracket-item-team.mod-winner {
    background:#4aa028;
}

.bracket-item-team.mod-first.mod-bye{
    background:#4795d0;
}

.article-body ul li:before{
    background: #faaf3a;
}

.post-frag-btn.minus.clicked{
color:red;
}
.post-frag-btn.minus.clicked:before {
background:red;
}

.post-frag-btn.plus.noselect.clicked {
 color:#4dc54d;
}

.post-frag-btn.plus.noselect.clicked::before {
 background:#4dc54d;
}

.wf-avatar {
        border: 1px solid #666666;
        background: #363636;
}

.wf-avatar img {
    background: rgba(0, 0, 0, 0.08);
}

textarea#post-editor-text{
  background: #ccc;
}

ul#post-editor-header.noselect{
background: #dadada;
}

#formatting-help {
color:#ddd;
}

div.post-body li {
color:#ddd;
}



.wf-card schedule {
 color:black;   
}

.schedule-row {
    border-top: 1px solid #949494;
}

.schedule-cell {
    border-left:1px solid #949494;
}

.schedule-cell.mod-data .inner, .schedule-cell.mod-data .date,.schedule-cell.mod-data .subseries {
    color:#15151d;
}

.schedule-cell.mod-day, .schedule-cell.mod-day .name, .schedule-cell.mod-event-label {
	color:#dedede;    
}

.schedule-cell.mod-data {
    background-color:#000;
}

.schedule-cell.mod-bg- {
    background-color:rgba(255, 255, 255, 0.34) !important;
}

.schedule-cell.mod-event-label:hover {
  background: #272727;
}

.event-card-title-name, .event-card-desc-value {
    color:#b9b9b9;
}


.wf-nav-item:not(.mod-active):hover {
  background: #232323; }

}
