css:
@-moz-document domain("web.telegram.org") {
body{
  color: #F5F5F5;
  height: 100vh;
}

.footer_empty{ /* footer que no hace nada */
	display: none;
}

.tg_head_split{ /* Barra de arriba */
	max-width: 100%;
}

.im_page_wrap{ /* main */
	max-width: 100%;
  background-color: #212121;
  border: none;
}

/*.btn-group-vertical>.btn-group:after, .btn-group-vertical>.btn-group:before, .btn-toolbar:after, .btn-toolbar:before, .clearfix:after, .clearfix:before, .container-fluid:after, .container-fluid:before, .container:after, .container:before, .dl-horizontal dd:after, .dl-horizontal dd:before, .form-horizontal .form-group:after, .form-horizontal .form-group:before, .modal-footer:after, .modal-footer:before, .nav:after, .nav:before, .navbar-collapse:after, .navbar-collapse:before, .navbar-header:after, .navbar-header:before, .navbar:after, .navbar:before, .pager:after, .pager:before, .panel-body:after, .panel-body:before, .row:after, .row:before {
  display: block
}*/

.im_dialog_photo{ /* Foto lista de chats */
	border-radius: 25%
}

.tg_head_split{
	background-color: #673AB7;
}

.tg_head_btn, .tg_head_btn:hover{
	background-color: #512DA8;
}

.im_dialogs_col_wrap{ /* Barra de chats */
  width: 35%;
  border-right: none;
  padding-bottom: 0;
}

.im_dialog_peer{
	color: #FAFAFA;
}

.im_history_col_wrap{ /* Mensajes */
  width: 65%;
}

.im_message_wrap{ /* Mensaje individual */
  max-width: 90%;
}

.tg_head_peer_title{
	text-overflow: initial;
}

.tg_head_logo_wrap{
	width: 34.9%;
}

.tg_head_main_wrap{
	width: 65%
}

.tg_head_peer_title{
	max-width: 80%;
}

.im_send_panel_wrap{
	max-width: 88%;
}

.im_send_form{
	max-width: 100%;
  right: 0;
  left: 64px;
}

.tg_head_peer_title_wrap{
	text-align: center;
}

.audio_player_title{
  max-width: 100%;
}

.audio_player_title_wrap{
  max-width: 100%;
  width: initial;
}

.im_message_author{ /* nombres en el chat */
	font-size: 1.2em;
  line-height: 150%;
}

.im_dialog_message{
  overflow: visible;
}

.im_message_text{ /* mensaje */
	font-size: 1.1em;
  line-height: 116%;
}

.im_content_message_wrap{
	padding: 0;
}

.im_history_typing{
	max-width: 100%;
  text-align: center;
}

.im_dialogs_scrollable_wrap .active a.im_dialog{
	background-color: #673AB7;
}

.im_dialogs_scrollable_wrap .active a.im_dialog:hover, .im_dialogs_scrollable_wrap .active a.im_dialog_selected{
	background-color: #7E57C2;
}

.im_dialogs_scrollable_wrap a.im_dialog:hover, .im_dialogs_scrollable_wrap a.im_dialog_selected{ /* chat no seleccionado y hovereado */
	background-color: #424242;
  transition: background-color 100ms ease-out;
}

.im_dialog{
  transition: background-color 200ms ease-out;
}

.im_dialogs_col .nano > .nano-pane > .nano-slider{ /* scrollbar chats */
	background: #FFFFFF;
}

.user_modal_wrap{ /* Perfil */
  color: black;
}

.im_history_col .nano > .nano-pane > .nano-slider, .contacts_modal_col .nano > .nano-pane > .nano-slider, .sessions_modal_col .nano > .nano-pane > .nano-slider, .stickerset_modal_col .nano > .nano-pane > .nano-slider, .im_dialogs_modal_col .nano > .nano-pane > .nano-slider{ /* scrollbar mensajes */
  background: white;
}

.im_message_selected .im_message_outer_wrap{ /* Mensaje seleccionado */
	background-color: #616161
}

.im_history_select_active .im_message_outer_wrap:hover{ /* Hover de mensajes cuando se selecciona uno */
	background-color: #424242;
}

.im_grouped_short .icon-select-tick, .im_grouped_short .im_message_outer_wrap:hover .icon-select-tick{ /*tick seleccion de mensaje */
  margin-top: -4px;
}

.composer_rich_textarea:focus, .composer_textarea:focus{ /* Caja para escribir mensajes */
	box-shadow: 0 2px 0 0 #673AB7;
}

.im_submit{ /* Botón de send */
  display: none;
}

.composer_emoji_panel{ /* Emojis abajo de la caja de mensaje */
  max-width: 100%;
}

.im_media_attach{
  margin-right: 38px;
}

.composer_emoji_panel a.composer_emoji_btn{ /* Emoji */
  margin:0;
  border-radius: 50%;
  transition: background-color 200ms ease-in;
}

a.composer_emoji_btn:hover{ /* Hover de emoji */
  background-color: #424242;
  border-radius: 50%;
  transition: background-color 200ms ease-in;
}

.composer_rich_textarea, .composer_textarea{ /* Fix borde blanco messagebox */
	overflow-y: auto;
}

.md_modal_head{ /* Perfil header */
  background: #673AB7;
}

a.im_dialog .im_dialog_chat_from_wrap, a.im_dialog .im_short_message_media, a.im_dialog .im_short_message_service{ /* Nombre de emisor en los grupos en la lista de chats */
	color: white;
}

a.im_dialog:hover .im_short_message_text, a.im_dialog_selected .im_short_message_text{ /* Mensaje cuando se hoverea en la lista de chats */
	color: white;
}

.im_panel_peer_photo{ /* Foto del grupo/usuario*/
  position: fixed;
  top: 3px;
  right: 58%;
  width: 42px;
  height: 42px;
  z-index: 5;
  border-radius: 50%;
  display: none;
}

.md_modal_body{
  color: black;
}

.dropdown.open .tg_head_btn, .tg_head_btn:hover{
  background: #4527A0;
  transition: background 300ms ease-out; 
}

a{ /* Links */
  color: #9575CD;
}

a:focus, a:hover{
  color: #D1C4E9;
}
  
.im_message_unread_split{ /* Linea unread */
  background-color: #9E9E9E;
  color: white;
  font-weight: bold;
}

.composer_emoji_tooltip{ /* Emoji y stickors tooltip */
	left: initial !important;
  right: 4.8%;
}

.composer_emoji_tooltip_tail{ /* Colita emoji y stickors tooltip */
  left: initial;
	right: -2px;
  bottom: -13px;
}

.im_dialogs_modal_col{ /* Background ventana modal fw */
  background: #212121;
}

.md_modal_footer, .md_modal_footer_empty{ /* Footer vacío ventana modal */
  display: none;
}

.im_panel_peer_photo, .im_panel_own_photo{
  margin-right: 0;
}

.im_message_webpage_wrap{ /* Linea en los previews de los links */
  border-left: 2px solid white;
}

.im_message_reply_border{ /* Linea de replies */
  background: white;
}

.icon-message-status, .im_dialog_unread, .active .im_dialog_unread{ /* Puntito de estado del mensaje */
  background-color: white;
}

.btn-primary{
  background-color: #673AB7;
}

.btn-primary:hover{ /* Botones */
  background-color: #7E57C2;
}

.btn-md-primary, .btn-md-primary:focus, .btn-md-primary:hover, .md_simple_modal_footer .btn, .btn-md, .btn-md:focus, .btn-md:hover{ /* Cancel */
  color: #673AB7;
}

.confirm_modal_description{ /* Mensaje modal */
  color: black;
}

.im_message_author, .im_message_fwd_author{ /* Color nombre en mensajes individuales */
  color: white;
}

/* ———————————— User colors para los grupos ———————————— */

.user_color_1, .user_color_1:hover{
	color: #CE6C47;
}

.user_color_2, .user_color_2:hover{
	color: #F26430;
}

.user_color_3, .user_color_3:hover{
	color: #F4442E;
}

.user_color_4, .user_color_4:hover{
	color: #FEC0AA;
}

.user_color_5, .user_color_5:hover{
	color: #FFD046;
}

.user_color_6, .user_color_6:hover{
	color: #009DDC;
}

.user_color_7, .user_color_7:hover{
	color: #8BC34A;
}

.user_color_8, .user_color_8:hover{
	color: #FF9800;
}

.user_color_9, .user_color_9:hover{
	color: #607D8B;
}

/* ———————————— ———————————— */

.md_modal_footer{ /* Fix no poder crear grupos bug */
  display: initial;
}

.tg_checkbox.tg_checkbox_on i.icon-checkbox-inner{ /* Checkbox */
  background: #673AB7;
}

.tg_checkbox.tg_checkbox_on span.icon-checkbox-outer{ /* Checkbox */
  background: #B39DDB;
}

.contacts_modal_members_list .active a.contacts_modal_contact, .contacts_modal_members_list .active a.contacts_modal_contact:hover{ /* Modal de contactos */
  background: #7E57C2;
}

.im_dialogs_scrollable_wrap a.im_dialog_searchpeer, .im_dialogs_scrollable_wrap a.im_dialog_searchpeer:hover, .im_dialogs_scrollable_wrap a.im_dialog_searchpeer_selected{ /* Search in this chat */
  background: #4527A0;
}

.im_bot_intro_message_wrap{ /* bot welcome message */
  color: white;
}
}
