css:
@-moz-document domain("imagez.to") {
body {
	background-color:#222222;
    color:#EEEEEE;
}
.block {
	background-color:#303030;
    border: 1px solid #444444;
}
.block-image {
	background-color:#464545;
}
input, .form-control, .qq-uploader {
    background-color:#464545;
    border: 1px solid #666666;
    color:#EEEEEE;
}
input:focus, .form-control:focus {
    border-color: #888888;
    outline: 0;
}
.form-control::-webkit-input-placeholder {
	color:#777777;
}
.form-control::-moz-placeholder {
	color:#777777;
}
.qq-upload-list li {
    color: #EEEEEE;
    background-color: #303030;
    border-top: 1px solid #464545;
    border-bottom: 1px solid #464545;
}
.qq-upload-list li.qq-upload-success {
    background-color: #0C8667;
    color: #EEEEEE;
    border-top: 1px solid #464545;
    border-bottom: 1px solid #464545;
}
.qq-upload-list li.qq-upload-fail {
    background-color: #A13D33;
    color: #EEEEEE;
    border-top: 1px solid #464545;
    border-bottom: 1px solid #464545;
}
.qq-edit-filename-icon, .qq-upload-spinner {
	filter: invert(100%);
}
.help-block {
	color:#999999;
}
.dropdown-menu {
	background-color: #303030;
}
.dropdown-menu > li > a, .dropdown-menu > li {
    color: #ebebeb;
}
.dropdown-menu .divider {
    background-color: #464545;
}
.panel {
    background-color: #303030;
  	box-shadow: 0 1px 1px rgba(0, 0, 0, 0.05);
}
.panel-default {
    border-color: #464545;
}
.panel-default > .panel-heading {
    background-color: #464545;
}
.panel-default > .panel-heading {
    color: #ffffff;
    border-color: #464545;
}


.btn-default {
  color: #ffffff;
  background-color: #464545;
  border-color: #464545;
}
.btn-default:focus,
.btn-default.focus {
  color: #ffffff;
  background-color: #2c2c2c;
  border-color: #060606;
}
.btn-default:hover {
  color: #ffffff;
  background-color: #2c2c2c;
  border-color: #272727;
}
.btn-default:active,
.btn-default.active,
.open > .dropdown-toggle.btn-default {
  color: #ffffff;
  background-color: #2c2c2c;
  border-color: #272727;
}
