css:
@-moz-document domain("www.clien.net"), domain("new.clien.net"), domain("clien.xyz") {
/* layout */

body {
    position: relative;
    width: 100%;
    height: 100%;
    font-family: Tahoma, 굴림;
}
pre {
    font-family: Tahoma, 굴림;
}
.back-to-top {
    display: none;
}
.nav {
    position: fixed;
    float: left;
    width: 150px;
    height: 100%;
    overflow-y: scroll;
    overflow-x: hidden;
    background-color: #fff;
}
.nav .nav-logo {
    width: 144px;
    float: none;
    padding: 12px 10px 8px 16px;
    border-left: none;
}
.nav .dropdown-menu {
    position: relative;
    box-shadow: none;
}
.nav .dropdown-toggle {
    display: none;
}
.nav .container {
    width: 100%;
}
.nav .nav-menu {
    float: none;
    width: 100%;
    margin-top: 44px;
}
.nav .nav-container .container {
    margin: 0;
    margin-left: 150px;
}
.nav .menu-toggle li {
    display: none;
}
.nav .sub-menu {
    display: block;
}
.nav .sub-menu ul {
    border-left: none;
    min-height: 0;
    float: none;
    width: 100%;
}
.nav .sub-menu ul .view-all {
    display: none;
}
.nav .sub-menu ul .none,
.sub-menu ul .block {
    height: 0;
}
.nav .sub-menu ul:hover {
    background: none;
}
.nav .sub-menu ul li a {
    width: 100%;
}
.nav .sub-menu .extras .button-promo-board {
    float: none;
    padding-top: 10px;
}
.nav .nav-group {
    float: none;
    width: 100%;
}
.nav .group-menu {
    display: block;
    width: 100%;
}
.nav .group-menu ul {
    float: none;
}
.nav .group-menu ul li {
    width: 100%;
}
.nav .group-menu ul li a {
    width: 100%;
}
.nav .group-menu .group-sub-menu .group-menu-action {
    width: 100%;
}
.nav .group-menu .group-sub-menu .group-menu-action a {
    float: none;
    width: 100%;
}
.nav .group-menu .group-sub-menu .group-menu-action .button-list {
    border-right: none;
}
.nav .group-menu-inner {
    margin-top: 80px;
}
.preview .preview-list a {
    font-size: 12px !important;
}
.main-primary .primary-news li a {
    font-size: 12px;
}
.side-account .sign-in .button-region a,
.side-account .sign-in .button-region .button-sign-up {
    text-align: left;
    width: 100%;
}
/* layout - search  */

.nav .contents-title {
    position: absolute;
    left: 0;
    top: 44px;
    width: 100%;
    height: 44px;
    float: none;
    z-index: 99999;
}
.nav .contents-title .board-search .side-search {
    margin-left: 0;
}
.contents-title .board-search .side-search .search {
    width: 100%;
}
.nav .search .search-input {
    width: 110px;
}
.nav .search .search-input::placeholder {
    color: rgba(0, 0, 0, 0);
}
/* layout - sidebar */

.sidebar {
    float: none;
    position: absolute;
    left: 1045px;
}
/* view */

h4.post-title,
.post-title .title-subject {
    font-weight: bold;
}
.post-title .title-symph span:after {
    font-family: Tahoma, 굴림;
    font-weight: bold;
}
.nick {
    font-family: Tahoma, 굴림;
    font-weight: bold;
}
.post .post-content .post-article {
    font-family: Tahoma, 굴림;
    font-size: 12px;
}
.post-comment .post-button-region .button-md,
.post-comment .post-button-region .button-md a:after,
.add-comment.open .comment-open,
.add-comment.open:after,
.related-list .list-symph,
.subject-status,
.subject-category,
.subject-soldout,
.subject-shortname {
    font-size: 12px;
}
/* list */

.board-list .list-title .list-subject {
    font-size: 12px;
    height: 18px;
}
.board-head .board-title a {
    font-weight: bold;
}
.board-foot .button-region .button-md .action-icon:after {
    font-size: 12px;
}
.nav-container,
.footer-nav,
.footer {
    padding-left: 150px;
}
.nav-container {
    min-width: 1150px;
}
.container {
    width: 1225px;
}
/* post */

.fr-view,
.post-article {
    font-family: Tahoma, 굴림;
    font-size: 12px;
}
@media (max-width: 1400px) {
    .content {
        width: 900px;
    }
    .action {
        margin-left: 868px;
    }
    .container {
        width: 1080px;
    }
    .sidebar {
        left: 915px;
    }
    .sympathyList .board-list .list-subject,
    .recentlyList .board-list .list-subject {
        max-width: 300px;
    }
    .sympathyList,
    .recentlyList {
        width: 414.5px;
    }
    .list-foot {
        width: 900px;
    }
    .mypage-layout {
        width: 600px;
    }
}
@media (max-width: 1300px) {
    .content {
        width: 800px;
    }
    .action {
        margin-left: 768px;
    }
    .container {
        width: 980px;
    }
    .sidebar {
        left: 815px;
    }
    .sympathyList .board-list .list-subject,
    .recentlyList .board-list .list-subject {
        max-width: 250px;
    }
    .sympathyList,
    .recentlyList {
        width: 364.5px;
    }
    .list-foot {
        width: 800px;
    }
    .mypage-layout {
        width: 500px;
    }
}
.nav::-webkit-scrollbar {
    width: 5px;
}
.nav::-webkit-scrollbar-track {
    background-color: rgba(0, 0, 0, 0.09);
    border-radius: 10px;
}
.nav::-webkit-scrollbar-thumb {
    border-radius: 10px;
    background-color: rgba(0, 0, 0, 0.23);
}
}
