css:
@-moz-document domain("minds.com") {
/* Userstyle for Minds.com. Created by: 2E0PGS | Modified by: ChaosWasTaken */
/* Repository: https://bitbucket.org/2E0PGS/userstyles/overview */
/* Licence: GNU v3 */
/* Userstyle URL: todo */
/* Version 1.0.4 08/05/2017 21:33 */
/* All credit goes to the original creator. I just tweaked some colours and made it more personal to myself. */

/* ---Background--- */
.mdl-color--grey-100, .minds-avatar-hovercard, .mdl-color--blue-grey-600, minds-search-bar .mdl-textfield input {
	background-color: #333333 !important;
}

/* ---General Text Colouring--- */
h1, h2, h3, h4, h5, h6, h6, p, html, .mdl-card__title, .mdl-tabs__tab, minds-activity.mdl-card .mdl-card__supporting-text.message, minds-activity .mdl-card__supporting-text.message {
	color: #ffffff;
}

.mdl-color-text--blue-grey-100 {
    color: #ffffff !important;
}

.mdl-color-text--blue-grey-500 {
    color: #ffffff !important;
}
.mdl-color--blue-grey-400 {
    background-color: #595959 !important;
}
.material-icons.mdl-color-text--blue-grey-400 {
    color: #ffffff !important;
}

.mdl-color-text--green-400 {
    color: #ffd54f !important;
}

.mdl-button--accent.mdl-button--accent.mdl-button--raised, .mdl-button--accent.mdl-button--accent.mdl-button--fab {
    background-color: #333333 !important;
}

/* ---Top Nav Bar--- */
.mdl-color--white {
	background-color: #1b1b1b !important;
}

/* ---Nav Bar Search Results List--- */
minds-search-bar-suggestions .m-search-bar-suggestions-list {
	background: #222;
	border: none;
}

minds-search-bar-suggestions .m-search-bar-suggestions-list .m-search-bar-suggestions-suggestion {
	border-bottom: 1px solid #353535;
}

/* ---Links--- */
a, .mdl-color-text--blue-grey-900 {
	color: #4690d6;
}

.pseudo-link.mdl-color-text--blue-grey-400 {
    color: #4690d6 !important;
}
.mdl-card__supporting-text {
    color: #ffffff !important;
}

.mdl-card__actions {
    color: #4690d6 !important;
}

.mdl-textfield__input {
	color: white;
}

/* ---Status Update Box--- */
.mdl-card, .minds-avatar {
	background: #1b1b1b;
}
.minds-subscribe-button {
    color: #ffd54f !important;
    background-color: #404040 !important;
    border: #404040 !important;
}

.mdl-card__supporting-text textarea {
	color: rgba(255, 255, 255, 0.87);
}

/* ---Posts--- */
.mdl-card .m-owner-block {
	background-color: #1b1b1b;
}

.m-owner-block a {
	color: #ffffff;
}

.mdl-card__title-text {
    color: #ffffff;
}

.m-blurb {
    color: #bfbfbf !important;
}

.mdl-button {
    color: #4690d6 !important;
    
}

.mdl-button--raised mdl-button--accent {
    background-color: rgb(66,66,66) !important;
}

.mdl-color-text--white {
    color: #ffffff !important;
}

.m-mature-message .m-mature-message-content {
	color: #ffffff;
}

/* ---Load more posts button--- */
.mdl-color--blue-grey-200 {
	background-color: #333333 !important;
}

/* ---Posts Like / Comment / Share Button toolbar--- */
.mdl-color--blue-grey-50 {
	background-color: #1b1b1b !important;
}

/* ---Posts Comment Box---*/
minds-textarea {
	border: none;
}

minds-comments .m-comments-composer form minds-textarea, minds-comments .minds-editable-container textarea {
	background: #404040;
}

/* ---Posts ReMinds Comment Box--- */
m-modal-remind-composer .m-modal-remind-composer textarea {
	border: 1px solid #1b1b1b;
	background-color: #222222;
}

minds-activity.mdl-card .is-boosted a, minds-activity .is-boosted a {
    color: #ffffff;
}

.positive {
    color: #ffd54f !important;
}

/* ---Post Views Counter--- */
minds-activity.mdl-card .impressions-tag, minds-activity .impressions-tag {
	background-color: #4690d6;
}

/* ---Comment Section Like and Dislike--- */
minds-activity.mdl-card minds-comments .mdl-card__menu, minds-activity minds-comments .mdl-card__menu, .m-ribbon {
	background: #1b1b1b;
}

/* ---Views Counter on right--- */
.mdl-card__supporting-text {
	color: #4690d6;
}

/* ---Left Nav Bar Darker--- */
.mdl-color--blue-grey-800 {
	background-color: #1b1b1b !important;
}

h3.mdl-color-text--blue-grey-800 {
 color: #ffffff !important;
}

.minds-wallet-points h2 {
    color: #ffd54f !important;
}

.mdl-color-text--green {
    color: #ffd54f !important;
}

.mdl-color-text--blue-grey-300 {
    color: #ffffff !important;
}

.mdl-color-text--blue-grey-600 {
 color: #4690d6!important;
}

.graph-label.mdl-color-text--blue-grey-300 {
    color: #bfbfbf !important;
}

/* ---Trending Images--- */
.m-title-block, .m-title-block-fixed {
	background-color: #1b1b1b;
}

.mdl-color-text--blue-grey-700 {
	color: #4690d6 !important;
}

/* ---inline search boxes--- */
.m-search-inline input {
	background-color: #222222;
	border: none;
	color: white;
}

.m-search-inline .m-search-inline-item:hover {
	background: #222;
}

/* ---rich-embed-preview--- */
minds-comments .m-comment-attachment .m-rich-embed, minds-comments .m-comments-composer .m-rich-embed, minds-comments .m-editable-attachment-container .m-rich-embed {
	background: #222;
	border: none;
}

/* ---Edit Post Text Container--- */
.minds-editable-container textarea {
	background-color: #333333;
	border: none;
}

/* ---Drop Down Menus--- */
.minds-dropdown-menu {
	background-color: #1b1b1b;
}

.mdl-menu__item:hover {
	background-color: #222;
}

.mdl-menu__item {
	color: white;
}

.mdl-menu__outline {
	background: #1b1b1b;
}
}
