css:
@-moz-document
  domain("bingnovels.wordpress.com")
{
header .container {min-height: 117px;}
header .logo-text h1 {font-size:1.78571em}

.main-container .container {border:none;}
.main-container .container:after {display:none;}
.row {margin-left:0;}
.span4 {display:none;}
.span8 {width:100%;}
.span1, .span2, .span3, .span4, .span5, .span6, .span7, .span8, .span9, .span10, .span11, .span12 {margin-left:0;}

p {margin: 0 0 5px;}

/* backgrounds/borders */
body.custom-background {background:#2f2e2e;}
.main-container .container {background:#444343}
nav a, div.menu a {margin-bottom: -1px;margin-top: 1px;}
nav ul, div.menu ul {border-bottom: 1px solid #252525;}
.pagetitle {border-bottom: 1px solid #252525;}
hr {border-top: 1px solid #252525;border-bottom: 1px solid #252525;}
.comments-area .author-info {border-bottom: 1px solid #252525;}
#respond #comment-form-comment, #comment-form-share-text-padder {background:rgba(82, 79, 79, 0.85);}
#respond #comment-form-comment.active, #comment-form-share-text-padder.active {background:rgba(99, 96, 96, 0.85);}
div#actionbar {background: #444343;border: solid 1px #c8d7e1;}

/* fonts */
body {font-size:14px;}
body, div#actionbar li.actnbr-btn > a {color: #e8e6e6}
#respond #comment-form-comment textarea, #comment-form-share-text-padder textarea {color: #e8e6e6}
.event-text p {font-size: 14px;}
p span {font-family: "Raleway", Helvetica, Arial, sans-serif!important;color:#e8e6e6!important;font-size:14px!important;}

#jp-post-flair {display:none;}
}
