css:
@-moz-document
  domain("shiroyukitranslations.com")
{
#sidebar {display:none;}
#primary {width:100%}
#main {padding:1.5em 15px;}
#page {margin-top:0}
#page:before {height: 15px;}
.sharedaddy {display:none;}
body.custom-background{background:#1b1919;}
#nav-wrapper .ribbon-left, #nav-wrapper .ribbon-right {display:none;}
p {margin-bottom: 1em;}

#page {background:#1b1919}
#main {background:#2f2e2e;}
.entry-meta span {background:#2f2e2e;}
#header, #page:before, #page:after, nav#main-nav>ul:before, nav#main-nav>ul:after, .entry-meta {background:none;}
nav#main-nav ul ul li:hover, nav#main-nav ul ul li {background-color:#231f1f;border-color:#1b1818;}
input[type="text"], input[type="search"], input[type="password"], input[type="url"], input[type="email"], textarea, select {background-color:#231f1f;border-color:#3a3838}

body {color:#e8e6e6}
nav#main-nav>ul>li>a {text-shadow: 1px 1px 0 #252423;}
h1, h2, h3, h4, h5, h6 {color:#e8e6e6;}

}
