css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("latale.wikiwiki.jp") {
  
	body {
		color: #fff;
		background-color: #191B22;
		background-image: url(https://goo.gl/HNyhnJ);
    background-repeat: no-repeat;
    background-attachment: fixed;
    background-position: right bottom, left bottom;
		font-family: 'メイリオ';
	}

	#backframe {
		background-color: #282C37;
    position:relative;
    padding-bottom:32px;
	}
  
/* change position */
	#title {
		padding: 0 !important;
	}
  #footframe{
    position: absolute;
    bottom: -20px;
    padding-right: 10px;
    padding-left: 10px;
    margin-left: -1px;
    margin-bottom:0;
  }
	#naviframe{
    position: absolute;
    bottom: 0px;
    padding-right: 9px;
    padding-left: 10px;
    margin-left: 0px;
  }
  #naviframe3{
    position:fixed;
    top:0;
    right:0;
    background-color:#000;
    color:#fff;
  }
/* change position */
  
/* link */
  a {
		color: #fff;
	}
	a:visited {
		color: #aaa;
	}
/* link */
  


/* table */
	td {
		color: #fff;
	}
	td.ltable {
		background-color: #444B5D;
	}
	.style_table {
		background-color: #444B5D;
		border-collapse: collapse;
    color:#000;
	}
	.style_th {
		background-color: #313543 !important;
		border-right: #fff 1px solid;
		border-bottom: #fff 1px solid;
    color:#fff !important;
	}
	.style_td {
		background-color: #444B5D !important;
		border: solid 1px #fff;
    color:#fff !important;
	}
/* table */
  
	dt {
		color: #22E6B5;
		font-size: 120%;
	}

/* change white */
	h3 {
		background-color: #fff;
	}
	h4 {
		color: #fff;
	}
	#topicpath {
		color: #fff !important;
	}
  img[title="宝石箱b.gif"] {
		background-color: #fff !important;
	}
/* change white */
  
/* change black */
  a[href="./?cmd=search"]{
    color:#fff !important;
  }
/* change black */
  
/* change color */
	span.noexists {
		background-color: inherit;
	}

	span[style="color:Blue"], span[style="color:blue"], span[style="color:#0000ff"], .diff_added{
		color: #2B90D9 !important;
	}

	span[style="color:Red"], span[style="color:red"], span[style="color:#ff0000"], .diff_removed{
		color: #DF4643 !important;
	}

	font[color="#973D21"] {
		color: #A63F10;
	}

	span[style="color:Maroon"], span[style="color:maroon"] {
		color: #ff0a0a !important;
	}
  span[style="color:Purple"], span[style="color:purple"] {
		color: #ff81ff !important;
	}
  span[style="color:Darkorchid"], span[style="color:darkorchid"] {
		color: #DF8FDA !important;
	}
	span[style="color:Black"], span[style="color:black"] {
		font-size: 150%;
		color: #fff !important;
	}
/* change color */
}
