css:
@-moz-document domain("eternitytower.net") {
body, html, .home-page-content > div, .live-feed-content > div {
    background-color: #303030;
    color: #FFF;
}
.body-content {
    margin-bottom: 0;
}
nav {
    background-color: #212121 !important;
}
.nav-link, .side-link-item {
    color: #FFF !important;
}
.side-link-item:hover {
    background-color: #212121 !important;
}
.nav-link.active {
    background-color: #303030 !important;
}
.text-muted {
    color: #636c72 !important;
}
.home-page-content > div, .live-feed-content > div, .adventure-item-container, .icon-box-new, .icon-box-new > div {
    border-color: #212121 !important;
}
.navbar-toggleable-md .navbar-nav .nav-link.active {
    background-color: #2699fc !important;
}
.my-tooltip-inner {
    border: 1px solid rgba(255, 255, 255, 0.2) !important;
}
.popover-title {
    background-color: #303030 !important;
    border-bottom: 1px solid rgba(255, 255, 255, 0.2) !important;
    color: #FFF !important;
}
.my-tooltip-inner {
    background-color: #424242 !important;
    color: #FFF !important;
}
table {
    border-color: #424242 !important;
}
.icon-box {
    background-color: #424242 !important;
    border-color: #212121 !important;
}
.icon-box.inactive {
    background-color: #202020 !important;
}
.farm-space-container.inactive > .icon-box {
    background-color: #202020 !important;
}
.icon-box.not-learnt {
	border: 3px solid black;
}
.progress-bar {
    background-color: #005CBF !important;
}
.progress-bar.bg-warning {
    background-color: #f0ad4e !important;
}
.progress-bar.bg-danger {
    background-color: #d9534f !important;
}
.select-skill-active {
    background-color: #005CBF !important;
}

/* chat */
.chat-container {
    background-color: #212121 !important;
    color: #000 !important;
    border-color: #000 !important;
}
.chat-container .nav-link {
    color: #FFF !important;
}
.chat-minimized {
    color: #000 !important;
}
.box-body {
    background-color: #424242 !important;
}
body .direct-chat-msg>.direct-chat-text.chat-LFG, .chat-LFG {
    color: #5bc0de !important;
}
body .direct-chat-msg>.direct-chat-text.chat-General, .chat-General {
    color: rgba(255, 255, 255, 0.7) !important;
}
body .direct-chat-msg>.direct-chat-text.chat-Game, .chat-Game {
    color: #d9534f !important;
}
body .direct-chat-msg>.direct-chat-text.chat-Party, .chat-Party {
    color: #347FBF !important;
}
body .direct-chat-msg>.direct-chat-text.chat-Announcements, .chat-Announcements, .chat-Dev {
    color: #06a956 !important;
}
body .direct-chat-msg>.direct-chat-text.chat-Server, .chat-Server {
    color: rgba(128, 128, 128, 1) !important;
}
body .direct-chat-msg>.direct-chat-text.chat-Help, .chat-Help {
    color: #EC407A !important;
}
.chat-container .box-footer {
    background-color: #212121 !important;
    color: #000 !important;
    border-color: #000 !important;
}
.chat-container .box-footer input {
    background-color: #303030 !important;
    color: #FFF !important;
}
.chat-container .box-footer input::-webkit-input-placeholder {
    color: #FFF !important;
}
.dropdown-menu {
    background-color: #424242 !important;
    color: #FFF !important;
}
.dropdown-item:hover {
    background-color: #212121 !important;
}

/* hide LINKS on homepage */
.mr-3.hidden-md-down .text-muted.mb-1 {
    display: none;
}

/* Firefox 18- */
.chat-container .box-footer input:-moz-placeholder {
    color: #FFF !important;
}

/* Firefox 19+ */
.chat-container .box-footer input::-moz-placeholder {
    color: #FFF !important;
}
.chat-container .box-footer input:-ms-input-placeholder {
    color: #FFF !important;
}
.chat-container .box-footer .input-group .input-group-btn button {
    background-color: #424242 !important;
}
.chat-container .box-footer .input-group .input-group-btn .dropdown-menu {
    background-color: #424242 !important;
}
img {
    fill: #FFF !important;
}
.modal-content {
    background-color: #424242 !important;
}

.attackSkill-tooltip-container .progress .progress-bar {
    background-color: #0275d8 !important;
}
.defenseSkill-tooltip-container .progress .progress-bar {
    background-color: #5cb85c !important;
}
.healthSkill-tooltip-container .progress .progress-bar {
    background-color: #d9534f !important;
}
.magicSkill-tooltip-container .progress .progress-bar {
    background-color: #5bc0de !important;
}

/* Make the "Loadout" bar same as background */
.select-battle-container {
    border-top: none;
    background-color: #303030 !important;
}
.recipe-tooltip {
    color: #636c72;
}
.uncraftable {
    background-color: #424242 !important;
    color: #fff !important;
    border: 1px solid #000 !important;
}
.single-craft {
    background-color: #303030 !important;
    color: #fff !important;
}
.quick-craft {
    background-color: #303030 !important;
    color: #fff !important;
}

/* Shop cards */
.card {
    background-color: #303030 !important;
    border: 1px solid #000 !important;
}

/* small screens get a slight size bump */
@media only screen and (min-width: 1280px) {
    .icon-box.mine-space-container {
        width: 96px;
        height: 96px;
    }
    .icon-box.mine-space-container .ore-icon {
        width: 64px;
        height: 64px;
    }
    .ore-list-item .icon-box.small-icon {
        width: 48px;
        height: 48px;
    }
    
    /* supposed to target the Mining tab mining pit flex div */
    #content > div.d-sm-flex.flex-grow > div.flex-grow > div.row > div.col-md-6:nth-child(1) > div {
        max-width: 640px !important;
    }
    
    /* supposed to target the Mining tab ores flex div */
    #content > div.d-sm-flex.flex-grow > div.flex-grow > div.row > div.col-md-6:nth-child(2) > div {
        max-height: 480px !important;
    }
}

/* in-betweeners just need a larger width on the mining pit to keep the 4x4 grid */
@media only screen and (min-width: 1440px) {
    #content > div.d-sm-flex.flex-grow > div.flex-grow > div.row > div.col-md-6:nth-child(1) > div {
        max-width: 480px !important;
    }
}

/* Large Devices, Wide Screens, Best Value */
@media only screen and (min-width: 1920px) {
    .icon-box.mine-space-container {
        width: 144px;
        height: 144px;
    }
    .icon-box.mine-space-container .ore-icon {
        width: 96px;
        height: 96px;
    }
    .ore-list-item .icon-box.small-icon {
        width: 64px;
        height: 64px;
    }
    /* supposed to target the Mining tab mining pit flex div */
    
    #content > div.d-sm-flex.flex-grow > div.flex-grow > div.row > div.col-md-6:nth-child(1) > div {
        max-width: 640px !important;
    }
    /* supposed to target the Mining tab ores flex div */
    
    #content > div.d-sm-flex.flex-grow > div.flex-grow > div.row > div.col-md-6:nth-child(2) > div {
        max-height: 640px !important;
    }
}

/* food countdown */
.buff-icon-container.icon-box.medium-icon span, .food-icon-container.disabled .cooldown-text {
    color: #fff !important;
}

.lobby-container .dropdown-item {
    color: #fff !important;
}

.lobby-container .health-bar {
    color: #303030;
}

/* battle abilities in combat */
#content > div.d-sm-flex.flex-grow > div > div:nth-child(1) > div:nth-child(3) {
    background-color: #303030 !important;
}

/* Battle + xp text */
.floating-text {
    background-color: #424242 !important;
}
.floating-text.animated.fadeOutUp {
    border-color: #000 !important;
}

/* Current user */
button.btn.btn-secondary.dropdown-toggle {
    color: #fff;
    background-color: #424242 !important;
    border-color: #000 !important;
}

/* Current user dropdown */
.dropdown-menu.dropdown-menu-right {
    background-color: #424242 !important;
    border-color: #000 !important;
}
.dropdown-menu.dropdown-menu-right .dropdown-item {
    color: #fff;
}
.btn-close-finishedBattle {
    color: #fff;
    background-color: #424242 !important;
    border-color: #000 !important;
}
}
