@namespace html url(http://www.w3.org/1999/xhtml);
@namespace xul url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

@-moz-document url("about:blank"), url-prefix("http://"), url-prefix("https://"), url-prefix("ftp://"), url-prefix("file://") {
/*
 * Drop the below regex, after a comma, just before the opening curly bracket above, to exclude websites from solarization: 
 * ,regexp("https?://(www\.)?(?!(userstyles\.org|docs\.google|github)\..*).*")
 */

scrollbar
{
  -moz-appearance: none !important;
  background: transparent !important;
}

.entry.res-selected, .entry.res-selected,.res, .comments-page, .commentarea, .sitetable, .comments-page, .sitetable .nestedlisting, .comment, .comment .comment, .RES-keyNav-activeElement, .RES-keyNav-activeElement .md-container, .md-container {
    background-color: transparent !important;
}

*
{
font-family: sans-serif !important;
color: #999 !important;
text-shadow: 0 0 3px #000 !important;
box-shadow: none !important;
background-color: transparent !important;
border-color: #444 !important;
border-top-color: #444 !important;
border-bottom-color: #444 !important;
border-left-color: #444 !important;
border-right-color: #444 !important;
}

body
{
background: transparent !important;
}


*, ::before, ::after {
color: #101011 !important;
text-transform: lowercase;
border-color: #474137 !important;
outline-color: #474137 !important;
text-shadow: none !important;
box-shadow: none !important;
background-color: transparent !important;
}

html * {
color: inherit !important;
}


p::first-letter,
h1::first-letter,
h2::first-letter,
p::first-line {

color: inherit !important; 
background: none !important;
}

    html, 
    body,
    li ul,
    ul li,
    table,
    header,
    article,
    menu,
    aside,
    /*
      section,
      nav,
      [class*="nav"],
      div[class*="content"], div[class*="container"],

      div[style*="visibility: visible"],
      div[id*="popup"],
      div[class*="popup"], 
      div[style*="display: block"], 
    */
    [class*="open"],
    [id*="ropdown"],
    [class*="ropdown"], 
    [class*="tooltip"],
    div[class*="menu"],
    div[id*="detail"], div[class*="detail"],
    div[class*="note"], span[class*="note"],
    div[class*="description"],
    div[class*="LayerParent"], div[class*="uiContextualLayer"],
    div[class*="clearfix"] {
    background-color: #C8B4A0 !important;
    }

.widget-pane-visible {
    background-color: #C8B4A0 !important;
    }

iframe, embed, header, nav, footer, label [onclick], nav ul, div[style*="position:"][style*="left:"][style*="visible"], div[style*="z-index:"][style*="left:"][style*="visible"], div[style*="-moz-user-select"], div[role="menu"], div[role="dialog"], span[class*="script"] div, [id*="menu"], [class*="dropdown"], [class*="popup"], [class="title"], ul[style*="display:"], ul[style*="visibility:"] ul, [id*="nav"] ul, [class*="nav"] ul, ul[class*="menu"], a[onclick][style*="display"], a[id*="ghosteryfirefox"], #ghostery-purple-bubble, #translator-popup, .menu, .tooltip, .hovercard, .vbmenu_popup
{
border-radius: 2px;
/* background: rgba(168, 153, 132,.9) !important; */
}

/*
body, *:not(:empty):not(html):not(span):not(a):not(b):not(option):not(img):not([style="display: block;"]):not([onclick*="open"]):not([onclick*="s_objectID"]):not([class*="stars"]):not([id*="stars"]):not([id="rating"]):not([class="rating"]):not([class*="SPRITE"]):not([id*="SPRITE"]):not([class*="item"]):not([id*="item"]):not([class*="thumb"]):not([class*="icon"]):not(.text):not([id*="lbImage"]):not([class*="cc-in"]):not([class*="gr-body"]):not([id*="watch"]):not(#globalsearch):not(.sp),
.r3_hm, .gmbutton2 b, .gtab-i, .ph, .bstab-iLft, .csb, #pagination div, [style*="sprite2.png"], #mw-head-base, #mw-page-base
{
  background-color: rgba(168, 153, 132,0) !important;
}
*/

span:not(:empty):not([class*="icon"]):not([id*="icon"]):not([class*="star"]):not([id*="star"]):not([id*="rating"]):not([class*="rating"]):not([class*="prite"]) {
background-image: none !important;
text-indent: 0 !important;
}


::-moz-selection {
background-color: #c8ebb2 !important;
color: #000000 !important;
}

.RES-keyNav-activeElement, .RES-keyNav-activeElement .md-container {
background-color: #dacba4 !important;
color: #000000 !important;
}

a {
color: #0A2B20 !important;
opacity: 1 !important;
text-indent: 0 !important;
}

a:link         {color: #001b48 !important; background-color: #C8B4A0;}
a:visited      {color: #662C49 !important;}
a:hover        {color: #004830 !important; background-color: #f9efd3 !important;}
a:active       {color: #823D10 !important;}

/* "Top level" div */

body > div {background-color: inherit !important;}


summary, details                   {background-color: inherit !important}
kbd, time, label, .date            {color: #3a2c0a !important}
acronym, abbr                      {border-bottom: 1px dotted !important; cursor: help !important;}
mark                               {background-color: #cc241d !important}
code,pre,samp,blockquote           {background-color: #8ba57f !important}


h1,h2,h3,h4,h5,h6  {
background-image: none !important;
border-radius: 5px !important;
-webkit-border-radius: 5px !important; 
text-indent: 0 !important;
}

table table {background: #9b886c !important;}
th, caption {background: #a89984 !important;}

textarea, button,
select,option,optgroup{
color: #ebdbb2 !important;
background: none #474137 !important;
-moz-appearance: none !important; 
-webkit-appearance: none !important;
}

input,
textarea, 
button {
border-color: #32302f !important;
border-width: 1px !important;
}

/* :::::: Button styling :::::: */

input[type="button"],
input[type="submit"],
input[type="reset"],
button {
background: #474137 !important;
}

input[type="button"]:hover,
input[type="submit"]:hover,
input[type="reset"]:hover,
button:hover {
color: #000000 !important;
background: #ebdbb2 !important;
}

/* Youtube Annotation */
.captions-text {color: #df8b00 !important;}


/* Mozilla addons shrink/expand sections */
.expando a {background: none transparent  !important;}
}

@-moz-document regexp(".*.sh"), regexp(".*.txt") {
body {
background-color: #a89984 !important;
}
}

@-moz-document url("chrome://browser/content/browser.xul") {
/* Browser Background */
browser[type="content-primary"] {
background-color: #a89984 !important;
}
}
