css:
@-moz-document domain(www.clien.net) {

/* font
---------------------------------------------- */
body {font-family:맑은고딕;}
.fr-view, .post-article {font-family:맑은고딕;}
.nick {font-family:맑은고딕;}

.contact-name {font-size:14px;}
.post-comment .comment .comment-row .comment-content {font-size:14px;}

.side-account .sign-in .button-region a {width:auto;}
.side-account .sign-in .button-region .button-sign-up {width:auto;}

/* 본링크 */
.board-list .list-title a:visited {color:#8D969B;}

/* adblock
---------------------------------------------- */
#div-pc-list-top {display:none;}
#div-pc-list-under {display:none;}
#div-pc-side {display:none;}
#div-main-top {display:none;}
#div-main-center {display:none;}
#div-pc-view-center {display:none;}
#naverAd {display:none;}
#hongboInfoList {display:none;}

/* nav
---------------------------------------------- */
.back-to-top {display:none;}
.nav {position:relative; float:left; width:180px; height:100%;}
.nav .nav-logo {width:auto; float:none;}
.nav .nav-menu {width:auto; float:none;}
.nav .container {width:auto;}
.nav .dropdown-toggle {display:none;}
.nav .dropdown-menu {position:relative; display:block;}
.sub-menu {float:none; padding-top:150px;}
.sub-menu ul {float:none; position:static; min-height:inherit;}
.sub-menu li a {width:auto;}
.sub-menu ul .none, .sub-menu ul .block {display:none;}
.sub-menu .view-all a {display:none;}

.sub-menu .extras {padding:0;}
.sub-menu .extras a {display:block; padding:6px 0 5px 10px;}
.sub-menu .extras .button-promo-board {display:block; width:auto; float:none;  padding:6px 0 5px 10px;}

.nav .nav-group {width:auto; float:none;}
.nav .group-menu {width:auto;}
.nav .button-list {display:none;}

.nav-group .dropdown-menu {float:none;}

.group-menu .group-sub-menu .group-menu-action .button-list {width:auto;}
.group-menu .group-sub-menu .group-menu-action {width:auto; position:static;}
.group-menu .group-sub-menu {float:none;}
.group-menu .group-sub-menu li {float:none;}
.group-menu ul {float:none;}
.group-menu li {width:auto;}
.group-sub-menu {float:none;}
.group-sub-menu ul {float:none;}
.group-sub-menu li {float:none;}
.group-menu .group-sub-menu .group-menu-action a {float:none;}
.group-menu-inner {margin-top:0;}

/* search
---------------------------------------------- */
.contents-title {width:auto; float:none;}
.contents-title .board-search .side-search {margin-left:0;}
.search .search-input {width:145px;}

/* member
---------------------------------------------- */
.side-account {position:absolute; top:50px; left:-28px; z-index:9999; border:0;}
.side-account.after {border:0;}

/* 공감
---------------------------------------------- */
.board-list .list-symph {display:none;}
.board-list .list-title {padding-left:7px;}

/* 1024px
---------------------------------------------- */
.nav-container {width:1024px; min-width:1024px;}
.content {width:775px;}
.content .action {margin-left:744px;}
.side-account {left:0;}

.board-list .list-title .list-subject {max-width:480px;}
#sympathyList {display:none;}
#recentlyList {display:none;}
.container {width:1024px;}

.card-view .list-row {width:365px;}
.card-view .card-image img {width:333px;}

.list-foot {width:775px;}
.mypage-nav {float:none;}

.fr-view iframe.videoArticle, .post-article iframe.videoArticle {width:680px; height:480px;}

}
