@-moz-document domain("mubu.com") {
#paper {
    max-width: none;
    margin: 0;
    box-shadow: none;
}
#editor-header,
.view-header {
    background: #3c3c3c;
    color: #c1c1c1;
}
#scroll-content {
    background: #fff;
/*     position: relative; */
/*     z-index: 10; */
    overflow-y: auto;
}
#editor-footer {
    margin: 0 50px;
    padding: 10px 0 20px;
    text-align: left;
    border-top: 1px solid #e5e6e8;
    font-size: 0;
}

