#editor-header .right .operate>.icon,
.view-header .right .operate>.icon {
    color: #bbb;
}
#editor-header .right .operate:hover .icon,
.view-header .right .operate:hover .icon {
    color: #d6d6d6;
}
#editor-header .right .operate.active .icon{
color:#fff;}
#editor-header .back:hover {
    background: #292929;
}

#editor-header .right .search-box input{
    border: 1px solid rgba(255,255,255,0.2);
    background:none;
    color:#bbb;
}
#editor-header .right .search-box input:focus{

    border: 1px solid rgba(255,255,255,0.35);
}
.menu {
    color: #000;
}
#editor-header .action {
    color: #545454;
}
#editor-header .action.active {
    color: #bbb;
}
.view-header .site img {
    filter: invert(1);
}
.view-header .right .main-btn {
    border: 1px solid transparent;
}
#node-wrapper {
    min-height: 0;
}
@media screen and (max-width:890px) {
    #paper {
        max-width: none;
        margin: 0 0 20px 0;
    }
}
