css:
@-moz-document domain("koga-fansub.net") {
/*Background Color*/

body,
#header,
#footer,
#actmenu .act-item,
#usermenu .user-function,
.chatroom-box {
    background: #212121 !important;
}
a:link {
    color: #dfe8f6 !important;
}
a:visited {
    color: #67f7fc !important;
}
a:hover {
    color: #21ff3e !important;
}
a:active {
    color: #d738ff !important;
}
body {
    margin: 0;
    padding: 0;
    color: #ccc;
    position: relative;
}
.this-season-12-2016,
.new-completed-12-2016 {
    margin-bottom: 30px;
    background: #202020;
}
#header,
#footer,
#header a,
.tv-new-12-2016 a,
#footer a,
#home .newmusic a,
#home .tp a,
#home .a-side,
.tv-new-12-2016,
.movie-message-12-2016,
.movie-link-12-2016 {
    color: #dfe8f6;
}
#casing {
    background: #202020;
}
.full-work-list-title-12-2016 {
    border-bottom: 1px solid #202020;
    padding: 5px;
}
body {
    margin: 0;
    padding: 0;
    color: #dfe8f6;
    position: relative;
}
#home .tv-new-entry-name-12-2016,
.full-work-list-title-12-2016,
#home .movie-name-12-2016 {
    font-family: 'Patrick Hand SC', cursive;
    font-size: 24px;
    background-color: #202020;
}
#home .tv-new-entry-12-2016 {
    background: #202020;
    margin-bottom: 30px;
    text-align: center;
}
.movie-12-2016 {
    min-height: 329px;
    background: #202020;
    position: relative;
}
#home .tv-new-entry-name-12-2016 {
    color: #dfe8f6;
}
.full-work-list-12-2016 li,
.chat-topic {
    font-family: 'Patrick Hand SC', cursive;
    font-size: 18px;
    background-color: #202020;
}
.topic,
.pjani-comment {
    margin: 0 auto;
    position: relative;
    line-height: 200%;
    background-color: #202020;
}
#header,
#footer,
#header a,
.tv-new-12-2016 a,
#footer a,
#home .newmusic a,
#home .tp a,
#home .a-side,
.tv-new-12-2016,
.movie-message-12-2016,
.movie-link-12-2016 {
    color: #8a8a8a;
    background-color: #202020;
}
.topic,
.topic-comment,
.topic-ttc,
.ltbox,
.publisher,
.video,
.new-old-form {
    background: #202020;
}
.ltbox {
    padding: 3px;
    border-radius: 3px;
    color: #add;
}
}
