css:
::-webkit-scrollbar {
    width: 16px !important;
    height: 16px !important;
    overflow: hidden !important;
}

::-webkit-scrollbar-thumb {
    background-color: rgba(0, 0, 0, .4) !important;
    border: 0px solid #484848 !important;
    border-radius: 100px !important;
    min-height: 32px;
}

::-webkit-scrollbar-track {
    background-color: #B6B6B6 !important;
}

::-webkit-scrollbar-thumb:hover {
    background: rgba(0, 0, 0, .55) !important;
}

::-webkit-scrollbar-thumb:active {
    background: rgba(0, 0, 0, .65) !important;
}

::-webkit-scrollbar-button {
    height: 16px !important;
    border-radius: 0px !important;
}

::-webkit-scrollbar-button:vertical:start:increment,
::-webkit-scrollbar-button:vertical:end:decrement {
    display: none;
}

::-webkit-scrollbar-button:start:decrement,
::-webkit-scrollbar-button:end:increment {
    display: block;
}

::-webkit-scrollbar-button:vertical:start:decrement {
    background:
        linear-gradient(120deg, #696969 40%, rgba(0, 0, 0, 0) 41%),
        linear-gradient(240deg,	#696969 40%, rgba(0, 0, 0, 0) 41%),
        linear-gradient(0deg, #696969 30%, rgba(0, 0, 0, 0) 31%);
    background-color: #b6b6b6;
}

::-webkit-scrollbar-button:vertical:start:decrement:hover {
    background:
        linear-gradient(120deg, #525252 40%, rgba(0, 0, 0, 0) 41%),
        linear-gradient(240deg, #525252 40%, rgba(0, 0, 0, 0) 41%),
        linear-gradient(0deg, #525252 30%, rgba(0, 0, 0, 0) 31%);
    background-color: #ccc;
}

::-webkit-scrollbar-button:vertical:end:increment {
    background:
        linear-gradient(300deg, #696969 40%, rgba(0, 0, 0, 0) 41%),
        linear-gradient(60deg, #696969 40%, rgba(0, 0, 0, 0) 41%),
        linear-gradient(180deg, #696969 30%, rgba(0, 0, 0, 0) 31%);
    background-color: #b6b6b6;
}

::-webkit-scrollbar-button:vertical:end:increment:hover {
    background:
        linear-gradient(300deg, #525252 40%, rgba(0, 0, 0, 0) 41%),
        linear-gradient(60deg, #525252 40%, rgba(0, 0, 0, 0) 41%),
        linear-gradient(180deg, #525252 30%, rgba(0, 0, 0, 0) 31%);
    background-color: #ccc;
}

::-webkit-scrollbar-button:horizontal:end:increment {
    background:
        linear-gradient(210deg, #696969 40%, rgba(0, 0, 0, 0) 41%),
        linear-gradient(330deg, #696969 40%, rgba(0, 0, 0, 0) 41%),
        linear-gradient(90deg, #696969 30%, rgba(0, 0, 0, 0) 31%);
    background-color: #b6b6b6;
}

::-webkit-scrollbar-button:horizontal:end:increment:hover {
    background:
        linear-gradient(210deg, #525252 40%, rgba(0, 0, 0, 0) 41%),
        linear-gradient(330deg, #525252 40%, rgba(0, 0, 0, 0) 41%),
        linear-gradient(90deg, #525252 30%, rgba(0, 0, 0, 0) 31%);
    background-color: #ccc;
}

::-webkit-scrollbar-button:horizontal:start:decrement {
    background:
        linear-gradient(30deg, #696969 40%, rgba(0, 0, 0, 0) 41%),
        linear-gradient(150deg, #696969 40%, rgba(0, 0, 0, 0) 41%),
        linear-gradient(270deg, #696969 30%, rgba(0, 0, 0, 0) 31%);
    background-color: #b6b6b6;
}

::-webkit-scrollbar-button:horizontal:start:decrement:hover {
    background:
        linear-gradient(30deg, #525252 40%, rgba(0, 0, 0, 0) 41%),
        linear-gradient(150deg, #525252 40%, rgba(0, 0, 0, 0) 41%),
        linear-gradient(270deg, #525252 30%, rgba(0, 0, 0, 0) 31%);
    background-color: #ccc;
}
