css:
@-moz-document domain("forum.worldofwarships.eu") {
html,
body {
	background: #111;
	color: #111;
}
table.ipb_table td {
	padding: 20px;
}
.b-announcements_title a {
	color: #DEDEDE;
}
.b-announcements .ipsBox {
	background: #4B82C9 none repeat scroll 0 0;
	padding: 10px 0 4px;
}
.ipsType_pagetitle {
	color: #4B82C9;
}
.row2 {
	background-color: #34342c;
	color: #f3f3f3;
}
.maintitle {
	background: #34342c none repeat scroll 0
}
.ipsBox_container {
	background: #34342c none repeat scroll 0 0;
}
.col_c_forum a {
	color: #E3E3E3;
	outline: medium none;
}
.col_c_forum a:hover {
	color: #ffffff;
}
.col_c_icon {
	background: url(http://i.imgur.com/8gej60w.png) no-repeat center;
	/*---------  Icons made by <a href="http://www.flaticon.com/authors/madebyoliver" title="Madebyoliver">Madebyoliver</a> from <a href="http://www.flaticon.com" title="Flaticon">www.flaticon.com</a> is licensed by <a href="http://creativecommons.org/licenses/by/3.0/" title="Creative Commons BY 3.0" target="_blank">CC 3.0 BY</a>   --------*/
	
	background-size: 50px 50px;
	visibility: visible;
}
.col_c_icon img {
	visibility: hidden;
}
.last_post ipsType_small,
li {
	color: #f6f6f6;
}
.last_post ipsType_small,
li a {
	color: #DEDEDE;
}
.views {
	color: #777;
}
p.desc {
	color: #999;
}
.last_post_margin,
a {
	color: #888;
	outline: medium none;
	text-decoration: none;
}
.last_post_margin,
a:hover {
	color: #fff;
}
.col_c_post li {
	color: #4B82C9;
}
.col_c_stats ipsType_small,
li {
	color: #4B82C9;
}
.subforums > li {
	background: rgba(0, 0, 0, 0) url("http://i.imgur.com/T2UZPcF.png") no-repeat scroll 5px 6px;
}
#announcements td {
	border-bottom: 1px solid #666;
}
table.ipb_table td {
	border-bottom: 1px solid #666;
}
.desc,
.desc.blend_links a,
p.posted_info {
	color: #999;
}
.topic_title,
a {
	color: #E3E3E3;
}
.desc.lighter {
	color: #999;
}
.mini_pagination a {
	color: #fff;
}
.b-announcements_ico {
	background: url(http://i.imgur.com/ssDpwSA.png) no-repeat center;
	/*--------- Icons made by <a href="http://www.flaticon.com/authors/pixel-buddha" title="Pixel Buddha">Pixel Buddha</a> from <a href="http://www.flaticon.com" title="Flaticon">www.flaticon.com</a> is licensed by <a href="http://creativecommons.org/licenses/by/3.0/" title="Creative Commons BY 3.0" target="_blank">CC 3.0 BY</a>    -------- */
	
	background-size: 22px 22px;
	visibility: visible;
}
.b-announcements_ico img {
	visibility: hidden;
}
.post_wrap {
	background: #34342c;
}
.posted_info {
	background: #34342c;
}
.post_wrap .row2 {
	background: #34342c;
}
.post_body .post {
	color: #DEDEDE
}
.b-author_shadow {
	visibility: hidden;
}
.post_wrap,
p {
	color: #DEDEDE;
}
.post_body,
.post p {
	color: #DEDEDE;
}
.post_block {
	background: none;
	border-bottom: 2px solid #4B82C9;
}
.blockquote,
.quote p {
	color: #222;
}
span.spoiler_title {
	color: #4B82C9;
}
.basic_info .row_title {
	color: #999;
}
.post_count .row_data,
.battles_count .row_data {
	color: #999!important;
	font-weight: bold;
}
/*  chuj */

p.citation {
	color: #222;
}
.author_info .group_title {
	color: #4B82C9;
}
/*  nav next back   */

.pagination .back a,
.pagination .forward a {
	background: #4B82C9 none repeat scroll 0 0;
}
/*  nav pagination number post   */

ul.mini_pagination li a {
	background: #7BA60D;
}
.highlighted,
.highlighted .altrow {
	background-color: #45453f;
}
.preview_col {
	color: #DEDEDE;
	;
}
.preview_info strong {
	color: #4B82C9;
}
.last_post_margin,
a {
	color: #7BA60D;
}
.last_post_margin,
a:hover {
	color: #fff;
}
/*----- linie niebieskie  -----*/

.preview_info {
	border-bottom: 1px solid #4B82C9;
}
.post_block h3 {
	background: #34342c;
}
}
