css:
@-moz-document url-prefix("https://jsfiddle.net/") {
a{color:#ccc;}
h3 {
    color: #ababab;
}
#sidebar .ebCont .avatar img {
    -webkit-filter: grayscale(100%);
    filter: grayscale(100%);
}
indowLabel.active, .windowLabel:hover {
    border: 1px solid #484848;
    text-decoration: none;
}
.CodeMirror-linenumber {
      padding-right: 0px !important;
    color: #464646!important;
    font-size: 10px;
    left: -7px !important;
}
.CodeMirror-gutters {
    background-color: rgba(51, 51, 51, 0.48);
    min-width: 1em;
    border-right: solid 1px rgba(33, 32, 32, 0);
}
#content .windowLabel {
    background: #1f1f1f;
    color: #9a9a9a;
    opacity: 0.5;
}
#sidebar input[type=text], #sidebar input[type=password], #sidebar select, #sidebar textarea {
    border-bottom: solid 1px rgb(58, 58, 58);
}
#sidebar #roadmap a {
    display:none
}
#keep-us-running.show {
    display: none;
}
.twitterCont {
 display: none;
}
#content .top {
    border-bottom: solid 0px #cfd6d9;
}
/* SCROLLBAR */
	::-webkit-scrollbar { width: 4px; }
	::-webkit-scrollbar-button { width: 0px; height: 0px; }
	::-webkit-scrollbar-thumb { background: #666; border: 0px none #ffffff; border-radius: 0px; }
	::-webkit-scrollbar-thumb:hover { background: #777; }
	::-webkit-scrollbar-thumb:active { background: #555; }
	::-webkit-scrollbar-track { background: gba(29, 28, 28, 0); border: 0px none #ffffff; border-radius: 0px; }
	::-webkit-scrollbar-track:hover { background: #666666; }
	::-webkit-scrollbar-track:active { background: #333333; }
	::-webkit-scrollbar-corner { background: transparent; }

	/* HEADER */
	#header { background: #333; border-bottom: 1px solid #222;
    -webkit-filter: grayscale(100%);
    filter: grayscale(100%);
 }
	#header a { color: #ccc; }
	#header a:hover { color: #ddd; text-decoration: none; font-style: italic; }
	#header #HW_badge { background: #333 !important; }
	#header .dropdownCont .dcWrapper  { background: #333; color: #eee; }
	#header .selectCont { background: #666; color: #eee; }
	#header .selectCont option, select { background: #666; color: #eee; }
	#header .dropdownCont .dcWrapper:before, #header .dropdownCont .dcWrapper:after { background: #222 !important; }
	#header #HW_frame_cont #HW_frame_peak { background: #222 !important; }
	#header div.dropdownCont .dcWrapper.menu li.other { border-top: 1px solid #333; }

	/* SIDEBAR */
	#sidebar { background: #333; -webkit-filter: grayscale(100%);
    filter: grayscale(100%); }
	#sidebar h3 { color: #ccc; }
	#sidebar p { color: #ccc; }

	/* CONTENT BOXES */
	#content {  border-left: 0px solid #333 !important; }
	#content div { color: #FFF; }
	#content .windowLabel:hover { opacity: 1.0; border:0;}
	#content .bottom, #content .top { border-right: 1px solid #333; position: absolute; }

	/* HTML-JS-CSS SETTINGS*/
	.windowSettings { background:rgb(70, 70, 70); border: 0px solid #333;  }
	.windowSettings:hover { background:rgb(70, 70, 70); }
	
	.windowSettings .selectCont { background: #666; color: #FFF; }
	.windowSettings .selectCont option, select { background: #666; color: #FFF; }

	/* HTML BOX */
	#content #panel_html { background: #2d2d2d; }

	/* JAVASCRIPT BOX */
	#content #panel_js { background: #2d2d2d; border-right: 1px solid #333; border-top: 1px solid #444; }

	/* CSS BOX */
	#content #panel_css { background: #2d2d2d; border-left: 1px solid #444; }

	/* RESULT BOX */
	#content #result { background: #fff;border-left: 1px solid #444; border-top: 1px solid #333; }

	/* SYNTAX COLORS */
	.cm-s-default .cm-comment { color: #d27b53 !important; }
	.cm-s-default .cm-atom { color: #a16a94 !important; }
	.cm-s-default .cm-number { color: #a16a94 !important; }
	.cm-s-default .cm-property, .cm-s-default .cm-attribute { color: #9c9 !important; }
	.cm-s-default .cm-keyword { color: #f2777a !important; }
	.cm-s-default .cm-string { color: #fc6 !important; }
	.cm-s-default .cm-variable { color: #9c9 !important; }
	.cm-s-default .cm-variable-2 { color: #69c !important; }
	.cm-s-default .cm-def { color: #f99157 !important; }
	.cm-s-default .cm-bracket { color: #ccc !important; }
	.cm-s-default .cm-tag { color: #f2777a !important; }
	.cm-s-default .cm-link { color: #a16a94 !important; }
	.cm-s-default .cm-error { background: #f2777a; color: #6a6a6a !important; }
	.cm-s-default .cm-string-2 {color: #f96 !important; }
	.cm-s-default .cm-qualifier, .cm-s-default .cm-operator, .cm-s-default .cm-builtin { color: #FFF !important; }
	.cm-s-default .cm-meta { color: #c5c5c5 !important; }
}

@-moz-document url-prefix("https://jsfiddle.net/user/") {
/* PROFILES */
	#sidebar { border-left: none; }
	#sidebar .commonNav { border-bottom: none; }
	#sidebar .commonNav a { color: #555; }
	#sidebar .segment { border-bottom: none; color: #FFF; }
	.contentWrap, .sidebarWrap, #sidebar { background: #39464E; }
	ul#main-nav.commonNav, ul#main-nav.commonNav a { background: #39464E !important; color: #2795EE !important; }
	.fiddleList, .item, .item a { background: #333; color: #FFF !important; padding: 3px; }
	.item .latest { color: #FF0000 !important; }
	.paginator { margin-top: 20px; }
	.paginator li { background: #333; color: #FFF; }
	.paginator a:hover { background: #666; color: #FFF; }
}
