css:
@-moz-document domain(play.esea.net) {
    div.module {
    width: 100%;
    padding: 0;
    margin: 0 0 12px 0;
    background-color: #191919;
    box-shadow: 0 2px 5px 0 rgba(0,0,0,0.16), 0 2px 10px 0 rgba(0,0,0,0.12);
    text-align: left;
}

a {
    color: #b2b2b2;
    text-decoration: underline;
}

a.new {
    color: #00abff;
}

body, td, th, select, input, textarea, h1, h2, h3, h4, h5, h6, div, span, label {
    font-family: verdana;
    font-size: 10px;
    color: #cccccc;
}

#layout-navigation a.menu-main {
    display: block;
    height: 25px;
    padding: 5px 0 0 10px;
    margin: 0 0 0 0;
    background-color: #101010;
    font: bold 14px 'tahoma';
    text-shadow: 0px 0px 0px rgba(255, 255, 255, .35);
    text-decoration: none;
    color: #cccccc;
}

thead th, thead td, .sub-header {
    background-color: #212121;
    padding: 3px 3px 3px 3px;
    border-bottom: 1px solid #eaeaea;
}

.row1, .row1 td, .row1 th {
    border-bottom: 2px solid #2a2a2e;
}

.row1 {
    background: #282727;
}

div#module-headlines h1 a.headline {
    display: block;
    color: #00abff;
    font: bold 12px 'verdana';
    text-decoration: none;
    margin: 0 0 4px 0;
}

div#module-headlines h2 a.headline {
    color: #59c4f9;
    font: 12px 'verdana';
    text-decoration: none;
}

div#module-headlines h1 div.preview {
    font-weight: normal;
    margin: 11px 0 0 0;
    font: 11px 'verdana';
    color: #00abff;
}

div#module-headlines h1 a.type {
    color: #59c4f9;
    font: bold 10px 'verdana';
    text-decoration: none;
}

div#module-headlines h1:hover {
	background-color: #272727;
}

html.parent {
	height: 100%;
        background-image:none;
        background-color: #0d0d0d;
	background-repeat: no-repeat;
	background-position: 0 32px;
	background-attachment: fixed;
	background-size: 100%;
}

#network-contents .highlight {
    color: #59c4f9;
    font-weight: bold;
}

div#layout-header input[type=text] {
    float: right;
    width: 189px;
    height: 30px;
    margin: 24px 0 0 0;
    padding: 0 0 0 6px;
    vertical-align: middle;
    background-color: #0f0f0f;
    border: 1px solid #00abff;
    border-radius: 4px 0 0 4px;
    border-right: 0;
    font: normal 13px 'Open Sans';
    color: #00abff;
}

div#layout-header input[type=text]:hover {
    color: #00abff;
}

.tab:hover, .tab.hover, .tab.activeTab {
    color: #00abff;
}

acronym, acronym a, acronym a:hover {
    text-decoration: none;
    color: #44a13f;
    font-weight: bold;
    cursor: help;
    border-bottom: 1px dotted #000000;
}

#upanel-profile .top-items .rank, #upanel-profile .top-items .rank * {
    font: normal 10px 'arial';
    color: #FFFFFF;
    text-decoration: none;
    white-space: nowrap;
    text-align: left;
    padding-left: 2px;
}

.box .rank, .box .rank * {
    font: normal 9px 'arial';
    color: #FFFFFF;
    text-decoration: none;
    white-space: nowrap;
    text-align: center;
}

.box .header th {
    font-weight: normal;
    background-color: #101010;
    padding: 0 3px 1px 3px;
    line-height: 1.8em;
}

.comment-container {
    width: 563px;
    background-color: #222023;
}

small {
    font-weight: normal;
    font-size: 8px;
    color: #cecccf;
}

#layout-navigation a.menu-main:hover:not(.active) {
	background-color: #00abff;
}

#layout-navigation a.menu-main.active {
    background-color: #00abff;
    text-shadow: 1px 1px 1px rgba(0, 0, 0, .35);
    color: #ffffff;
}

.content-text {
    text-align: justify;
    background-color: #222023;
    font: 12px 'arial';
}

.layout-home div#module-events table.calendar * td {
    height: 21px;
    font-size: 11px;
    background-color: #222023!important; 
    
}

table.calendar * th, table.calendar * td {
    text-align: center;
    padding: 0;
    text-shadow: 0px 0px 0px #ffffff;
    border: 1px solid #ffffff;
    color: #00abff;
}

table.calendar * td {
    height: 16px;
    background-color: #ffffff;
    color: #cccccc;
}

table.calendar .cal-highlight a {
    font-weight: bold;
    text-decoration: none;
    color: #59c4f9;
}

table.calendar * th, table.calendar * td {
    text-align: center;
    padding: 0;
    text-shadow: 0px 0px 0px #ffffff;
    border: 1px solid #4c4a4a;
    background-color: #212121;
}

.stats-container h2 {
    display: block;
    float: left;
    clear: both;
    margin: 10px 0 0 0;
    font-weight: bold;
    color: #00abff;
}

div#layout-header a.home img {
  margin: 11px 0 0 0;
  -moz-box-sizing: border-box;
  box-sizing: border-box;
  background: url(http://i.imgur.com/t1GRR9V.png) no-repeat;
  width: 225px; /* Width of new image */
  height: 54px; /* Height of new image */
  padding-left: 225px; /* Equal to width of new image */
}

div#module-headlines h2:hover div.comments {
	color: #00abff;
}

img#displayGraph.left{
    filter: invert(100%);
}

table.calendar .cal-highlight {
    background-color: #212121;
    border-color: #d3e8d0;
}

table.calendar .cal-current-day, table.calendar td:hover {
    background-color: #4c4a4a;
    border-color: #e6e6e6;
    font-weight: bold;
    color: #f5f3f3;
}

#network-contents div.left img {
    vertical-align: -5px;
    margin: 0 6px 0 6px;
     -moz-box-sizing: border-box;
  box-sizing: border-box;
  background: url(http://i.imgur.com/r3aApH8.png) no-repeat;
  width: 87px; /* Width of new image */
  height: 18px; /* Height of new image */
  padding-left: 87px; /* Equal to width of new image */
}

.match-header {
    background-color: #212121;
    border: 1px solid #F0F1F3;
}

.upcoming {
    background-color: #212121;
    border-color: #c0ccd7;
}

div.layout-stats #displayGraph {
    filter: invert(100%);
}

textarea {
    background-color: #212121;
}

input {
    background-color: #212121;
}

select{
    background-color: #212121;
}

div.clip-preview div {
    background-color: #0a0a0a;
}

#layout-status {
    background-color: #00abff;
}

#support-faq div.question a.question {
    color: #00abff;
    background-color: rgba(10, 10, 10, 0);

}

#support-faq h1 {
    background-color: #0c0c0c;
}

#support-faq .row2 {
    background-color: #0a0a0a;
}

.row2 td, .row2 th {
    background-color: #0a0a0a;
}

.row2, .row2 td, .row2 th {
    border-bottom: 1px solid #00ddff;
}

#ladders-rankings .ladder {
    background-color: #0a0a0a;
    
}

#ladders-rankings tr.ladder-header td {
    background-color: #0a0a0a;

}

#ladders-rankings tr.ladder-legend th {
    background-color: #212121;
}

.note2 {
    background-color: #2f2f2f;
}

#sorted {
    background-color: #000000;
}

acronym, acronym a, acronym a:hover {
    color: #00abff;
}

a.black {
    color: #c1e5ff;
}

#recruitment-filter select.focus {
    background-color: #00abff;
}

.account-black {
    color: #00f1ff;
}

#message-header {
    background-color: #545454;
}

.content-details {
    background-color: #191919;
}
}
