css:
@-moz-document
  domain("novelupdates.com")
{
/* Header and footer*/
	.l-subheader, .l-subfooter, .at_top, .at_middle, .at_bottom {
		background-color: #2c3e50 !important;
	}

	/* Content */
	.l-main, .l-submain {
		background-color: #222222 !important;
	}
    .gen_rnd {
        background-color: #333333;
        border-color: #211f1f;
    }
	
	/* Text */
	.w-logo-title, .w-nav-anchor {
		color: #ebebeb !important;
	}
	h1, h2, h3, h4, h5, h6 {
		color: #e8e8e8 !important;
	}
	.l-main, .l-submain, p {
		 color: #bababa !important;
  	}
	a {
		color: #e0e0e0 !important;
	}
	.seriestitlenu {
		color: #bababa !important;
	}

	/* Highlighted text font color */
	.colorme > * > a, .newcolorme > * > a, .readcolor > * a {
		color: #696767 !important;
	}

	/* Selected text */
	::-moz-selection {
		background-color: #2c3e50 !important;
	}
	::selection {
		background-color: #fff !important;
	}
	
	/* Reading list thingy */
	.mt_rl {
		background-color: #282828 !important;
	}
	
	/* Tables */
	thead > tr {
		background-color: #222222 !important;
	}
	thead > tr > *, thead > tr > * > * {
		background-color: #2c3e50 !important;
		color: #dfdfdf !important;
	}
	tbody > tr:nth-child(even) {
		background-color: #232323 !important;
	}
	tbody > tr:nth-child(odd) {
		background-color: #282828 !important;
	}
    .frmlatest, .nuf_bd, #nuf_li, .bdrank, .sfstext, .r_line, .rev_b1, .sf_box {
		border-color: #3e3f40!important;
    }

	/* Tags */
	.genre.odd {
		background-color: #222222 !important;
		color: #c0c0c0 !important;
	}
	
	/* Spoilers */
	.sp-wrap {
		background-color: #2c3e50 !important;
		border-color: #223446 !important;
	}
	.sp-wrap > * {
		color: #e0e0e0 !important;
	}
	.sp-body {
		background-color: #2c3e50 !important;
		border-color: #223446 !important;
	}
	
	/* Series finder and other boxes */
	#rankfilter, .rankfl, .rllocalsettings, .rlnewsettings {
		background-color: #2c2c2c !important;
		border-color: #2c3e50 !important;
	}
	
	/* Pagination */
	.digg_pagination {
		background-color: #333 !important;
	}
	.digg_pagination > * {
		border-color: #808080 !important;
	}
	.digg_pagination > span {
		color: #afafaf !important;
	}
	
	/* Ratings and rankings */
	.userrate {
		background-color: #2c3e50 !important;
		color: #dfdfdf !important;
	}
	
	/* Reading list count / Account activity [series] */
	.clist {
		color: #36485a !important;
	}

	/* Buttons / Input fields / Dropdown menus / checkboxes */
    .button {
        border: 1px solid #211f1f!important;
        text-shadow: 1px 1px 0 #232121!important;
        background-color: #000000!important;
        background-image: -webkit-gradient(linear, 0 0, 0 100%, from(#504d4d), to(#1c2021))!important;
        background-image: -moz-linear-gradient(#504d4d, #1c2021)!important;
        background-image: -ms-linear-gradient(#504d4d, #1c2021)!important;
        background-image: -o-linear-gradient(#504d4d, #1c2021)!important;
        background-image: linear-gradient(#504d4d, #1c2021)!important;
    }
	.breadcrumb_nu {
		background: #272626!important;
    	border-color: #211f1f!important;
	}
	#Breadcrumb_Top dd {
		color: #5d6f92!important;
    }
	.chosen-container-single .chosen-single, .chosen-container-single .chosen-single, .chosen-container-active.chosen-with-drop .chosen-single, .chosen-container-multi .chosen-choices {
        background: -webkit-gradient(linear,50% 0,50% 100%,color-stop(20%,#504d4d),color-stop(50%,#1c2021),color-stop(52%,#252323),color-stop(100%,#504d4d))!important;
        background: linear-gradient(top,#504d4d 20%,#1c2021 50%,#252323 52%,#504d4d 100%)!important;
        background: -moz-linear-gradient(top,#504d4d 20%,#1c2021 50%,#252323 52%,#504d4d 100%)!important;
        background: -o-linear-gradient(top,#504d4d 20%,#1c2021 50%,#252323 52%,#504d4d 100%)!important;
        background: -webkit-linear-gradient(top,#504d4d 20%,#1c2021 50%,#252323 52%,#504d4d 100%)!important;
        box-shadow: 0 0 3px #353333 inset, 0 1px 1px rgba(0,0,0,.1)!important;
        border-color: #211f1f!important;
    }
	.chosen-container .chosen-drop {
		background: #2f2d2d!important;
    	border-color: #211f1f!important;
	}
    .chosen-container .chosen-results li.active-result {
		color: #fff!important;
    }
	.chosen-container-single .chosen-single span {
		color: #fff!important;
	}

    [type="checkbox"]:not(:checked) + label:before, [type="checkbox"]:checked + label:before, [type="checkbox"]:not(:checked) + label:before, [type="checkbox"]:checked + label:before {
        background: #3e3b3b!important;
        border-color: #211f1f!important;
    }
	input[type="text"], input[type="password"], input[type="email"], input[type="url"], input[type="tel"], input[type="number"], input[type="date"], input[type="search"], textarea, select, .w-actionbox.color_alternate, .w-author, .w-blog.imgpos_atleft .w-blog-entry-preview-icon, .w-filters, .w-icon.color_text.type_solid .w-icon-link, .w-icon.color_fade.type_solid .w-icon-link, .w-pricing-item-title, .w-pricing-item-price, .w-progbar-bar, .w-tabs-list, .no-touch .widget_nav_menu .menu-item a:hover, .no-touch #lang_sel a:hover, .no-touch #lang_sel_click a:hover, .woocommerce .quantity .plus, .woocommerce .quantity .minus, .woocommerce div.product .woocommerce-tabs .tabs, .woocommerce table.shop_table .actions .coupon .input-text, .woocommerce #payment .payment_box, #bbp-user-navigation, #subscription-toggle, #favorite-toggle, .bbp-row-actions #favorite-toggle a, .bbp-row-actions #subscription-toggle a, .gform_wrapper .chosen-container-single .chosen-single, .gform_wrapper .chosen-container-multi .chosen-choices, .select2-container a.select2-choice, .smile-icon-timeline-wrap .timeline-wrapper .timeline-block, .smile-icon-timeline-wrap .timeline-feature-item.feat-item {
       background-color: #3e3b3b!important;         
}

.l-submain, .g-cols > div, .w-blog-entry, .w-bloglist, .w-blognav, .w-comments, .w-comments-item, .w-pricing-item-h, .w-profile, .w-sharing.type_simple .w-sharing-item, .w-tabs.layout_accordion, .w-tabs.layout_accordion .w-tabs-section, .w-timeline.type_vertical .w-timeline-section-content, #wp-calendar thead th, #wp-calendar tbody td, #wp-calendar tfoot td, .widget_nav_menu .menu-item a, #lang_sel a, #lang_sel_click a, .woocommerce table th, .woocommerce table td, .woocommerce .login, .woocommerce .checkout_coupon, .woocommerce .register, .woocommerce .cart.variations_form, .woocommerce .commentlist, .woocommerce .commentlist li, .woocommerce .comment-respond, .woocommerce .related, .woocommerce .upsells, .woocommerce .cross-sells, .woocommerce .checkout #order_review, .woocommerce ul.order_details li, .woocommerce .shop_table.my_account_orders, .widget_price_filter .ui-slider-handle, .widget_layered_nav ul, .widget_layered_nav ul li, #bbpress-forums .bbp-body ul.forum, #bbpress-forums .bbp-forums li.bbp-header, #bbpress-forums .bbp-body ul.topic, #bbpress-forums .bbp-topics li.bbp-header, .bbp-replies .bbp-body, div.bbp-forum-header, div.bbp-topic-header, div.bbp-reply-header, .bbp-pagination-links a, .bbp-pagination-links span.current, span.bbp-topic-pagination a.page-numbers, .bbp-logged-in, fieldset, .form_saved_message, .gform_wrapper .gsection, .gform_wrapper .gf_page_steps, .gform_wrapper li.gfield_creditcard_warning, .smile-icon-timeline-wrap .timeline-line, .ult_pricing_table_wrap.ult_design_6 .ult_pricing_table {
	border-color: #3e3f40!important;
}
}
