  img.pending {border: 2px solid #4B4BA3;}
  img.deleted {border: 2px solid #F00;}

  .thumb > a:visited > img {border-color: lightblue;}
  .thumb > a:visited:hover > img {border-color: lightblue !important;}
  .thumb > a:hover > img {border-color: #DDD !important;}
  
  input#tags{transition: ease-in-out;overflow:scroll;}
