css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("tvtime.com") {



body
{
  background-color: rgb(14,0,10) !important;
  background-image: url("https://chara.useed.fr/SB2705/owncloud/index.php/s/Xj72HwvxcZ17CnT/download") !important;
  background-attachment:fixed;
  background-repeat:repeat;
  
}

#content .content-container .left, #content .content-container .right, .page-left > .scrollable, #show-details.show .show-nav, .page-center section, .page-sidebar, #profile, #explore, #shows, #settings, #home, #home-alt, #episode-details #content:not(.display), #episode-details .episode-infos.extended 
{
  background-color: rgb(14,0,10) !important;
  background-image: url("https://chara.useed.fr/SB2705/owncloud/index.php/s/Xj72HwvxcZ17CnT/download") !important;
  background-attachment:fixed;
  background-repeat:repeat;
  
}

#show-details .seasons .episode-wrapper.current
{
  background-color: inherit !important;    
}
  
#show-details .seasons .episode-wrapper.current a i
{
  color: sandybrown !important;  
}
  
#show-details.show .comments, .comment:not(.new-comment) .post, .comment .sub-comment, .comment .spoiler-container, .comment form.post > .top .holder, .fanarts-list > li .fanart-container, #profile .profile-nav, #profile-badges .badges-group, #profile-leaderboards, #profile-stats, #settings section .title, #settings section .form-control 
{
  background: rgba(0,0,0,0.2) !important;
}

.comment form.post > .top .holder textarea, .comment form.reply > .holder, .comment form.reply > .holder textarea 
{
  background: rgba(0,0,0,0.9) !important;
}

.comments-filters li a, #community-feed-container .filters .dropdown, #discussion .filters .dropdown, #seasons .filters .dropdown, #show-details .seasons .episode-wrapper.odd, .comment .other-user-comments a, .comment .spoiler-container .spoiler-text a, #calendar-filters .mode, .nav-tabs > li.active > a, .nav-tabs > li.active > a:focus, .nav-tabs > li.active > a:hover, .nav > li > a:focus, .nav > li > a:hover, #profile .filters .dropdown, .posters-list > li .nav .follow-btn, .filters .dropdown, #meme-wizard .submit-meme .submit-meme-btn, .new-comment .submit-comment .submit-comment-btn, #settings .nav-pills > li.active > a, #settings .nav-pills > li.active > a:focus, #settings .nav-pills > li.active > a:hover, #settings section h2, #settings .login-infos .edit-password-link, #basic-settings .other-infos button[type="submit"], #show-details.show .show-header .follow-btn.active, #show-details.show .show-header .follow-archive-btns .archive-btn, #episode-details #content .not-watched-episode .display-btn, #episode-details .episode-header .watched-btn:not(.watched)  
{
  background: rgba(255,255,255,0.1) !important;
  color: rgb(255,245,215) !important;
}

.comments-filters li a:hover
{
  background: rgba(255,255,255,0.2) !important;
}

#community-feed-container .filters .current, #discussion .filters .current, #seasons .filters .current, #community-feed-container .filters .dropdown-menu > .active > a, #community-feed-container .filters .dropdown-menu > .active > a:focus, #community-feed-container .filters .dropdown-menu > .active > a:hover, #community-feed-container .filters .dropdown-menu > li > a:focus, #community-feed-container .filters .dropdown-menu > li > a:hover, #discussion .filters .dropdown-menu > .active > a, #discussion .filters .dropdown-menu > .active > a:focus, #discussion .filters .dropdown-menu > .active > a:hover, #discussion .filters .dropdown-menu > li > a:focus, #discussion .filters .dropdown-menu > li > a:hover, #seasons .filters .dropdown-menu > .active > a, #seasons .filters .dropdown-menu > .active > a:focus, #seasons .filters .dropdown-menu > .active > a:hover, #seasons .filters .dropdown-menu > li > a:focus, #seasons .filters .dropdown-menu > li > a:hover, #community-feed-container .filters .dropdown-menu a, #discussion .filters .dropdown-menu a, #seasons .filters .dropdown-menu a, h2.title, section .title, .header .menu > li a, .page-left .menu > li a, .header .menu > li a i, .page-left .menu > li a i, #profile .profile-nav .nav-tabs > li .label, #profile h2.small, #explore section h1, #explore section h1 i, #settings section label, .page-column section h1 
{
  color: rgb(255,245,215) !important;
}

.comment .post > .top .holder blockquote p,.comment .reply > .holder blockquote p, .form-control, output, #settings section .no-input, .comment form.reply > .holder textarea, .page-column section h1 .nb-items 
{
  color: #fcebb6 !important;
}

.comment .post > .top .holder .author-label, .comment .post > .top .holder .author-label a, .comment .reply .author-label a, #show-leaderboard .captain .user-infos .user-name a, #show-leaderboard ul li .user-infos .user-name a
{
  color: turquoise !important;
}

.dropdown-menu, select, select > option  
{
  background: rgba(16,0,10,0.9) !important;
}

.dropdown-menu > li > a:hover
{
  background: rgba(32,0,20,0.9) !important;
}

#show-details .seasons .episode-wrapper .infos a, #show-details .seasons .episode-wrapper .infos:hover i.icon-play-circle, .comment .spoiler-container .spoiler-text p.spoiler-title, .comment .spoiler-container .spoiler-text p
{
  color: pink !important;
}

#show-details .basic-infos, #episode-details.episode .followers, #show-details.show .show-nav .followers, .cast .actor-item .content, #episode-details .episode-infos .basic-infos
{
  color: mistyrose !important;
}

.cast .actor-item .content h3
{
  color: lightpink !important;
}


body a, #calendar .day.today .day-header .relative-date, #calendar .day.tomorrow .day-header .relative-date, #calendar .day.yesterday .day-header .relative-date, #calendar .day .day-header .relative-date
{
  color: aliceblue !important;
}

body a:hover
{
  color: white !important;
}

#user-nav .alt-user-nav a .badge
{
  color: cyan !important;
}

#calendar .day .day-header .day-info .day-label, .fanarts-list > li .fanart-details p
{
  color: #c6c6c6 !important;
}
  
}
