css:
@-moz-document domain("www.clien.net") {
/* 글꼴 */

body {
	font-family: 맑은고딕;
}
.fr-view,
.post-article {
	font-family: 맑은고딕;
}
.nick {
	font-family: 맑은고딕;
}
.contact-name {
	font-size: 14px;
}
.post-comment .comment .comment-row .comment-content {
	font-size: 14px;
}
.side-account .sign-in .button-region a {
	width: auto;
}
.side-account .sign-in .button-region .button-sign-up {
	width: auto;
}
/* 광고처리 */

#div-pc-list-top {
	display: none;
}
#div-pc-list-under {
	display: none;
}
#div-pc-side {
	display: none;
}
#div-main-top {
	display: none;
}
#div-pc-view-center {
	display: none;
}
#naverAd {
	display: none;
}
#hongboInfoList {
	display: none;
}
/* nav */

.back-to-top {
	display: none;
}
.nav {
	position: relative;
	float: left;
	width: 180px;
	height: 100%;
}
.nav .nav-logo {
	width: auto;
	float: none;
}
.nav .nav-menu {
	width: auto;
	float: none;
}
.nav .container {
	width: auto;
}
.nav .dropdown-toggle {
	display: none;
}
.nav .dropdown-menu {
	position: relative;
	display: block;
}
.sub-menu {
	float: none;
	padding-top: 150px;
}
.sub-menu ul {
	float: none;
	position: static;
	min-height: inherit;
}
.sub-menu li a {
	width: auto;
}
.sub-menu ul .none,
.sub-menu ul .block {
	display: none;
}
.sub-menu .view-all a {
	display: none;
}
.sub-menu .extras {
	padding: 0;
}
.sub-menu .extras a {
	display: block;
	padding: 6px 0 5px 10px;
}
.sub-menu .extras .button-promo-board {
	display: block;
	width: auto;
	float: none;
	padding: 6px 0 5px 10px;
}
.nav .nav-group {
	width: auto;
	float: none;
}
.nav .group-menu {
	width: auto;
}
.nav .button-list {
	display: none;
}
.nav-group .dropdown-menu {
	float: none;
}
.group-menu .group-sub-menu .group-menu-action .button-list {
	width: auto;
}
.group-menu .group-sub-menu .group-menu-action {
	width: auto;
	position: static;
}
.group-menu .group-sub-menu {
	float: none;
}
.group-menu .group-sub-menu li {
	float: none;
}
.group-menu ul {
	float: none;
}
.group-menu li {
	width: auto;
}
.group-sub-menu {
	float: none;
}
.group-sub-menu ul {
	float: none;
}
.group-sub-menu li {
	float: none;
}
.group-menu .group-sub-menu .group-menu-action a {
	float: none;
}
.group-menu-inner {
	margin-top: 0;
}
/* 검색 

.contents-title {
	width: auto;
	float: none;
}
.contents-title .board-search .side-search {
	margin-left: 0;
}
.search .search-input {
	width: 145px;
}
*/
/* layout - search  */

.search .button-search {
	color: #232f3e;
}
.search .search-input {
	-webkit-backdrop-filter: blur(5px);
	background-color: rgba(255, 255, 255, 00.8);
	border: 1px solid #232f3e;
	color: #000;
	-webkit-transition: none;
	transition: none;
}
.nav .contents-title {
	position: absolute;
	left: 0;
	top: 0;
	height: 0px;
	width: 100%;
	float: none;
	z-index: 99980;
	background-color: #232F3E;
}
.nav .contents-title .board-search {
	position: relative;
	width: 1090px;
	min-width: 1090px;
	margin: 0 auto;
	border-right: none;
	padding: 0px 0 0;
	height: 0;
}
.nav .contents-title .board-search .side-search {
	position: absolute;
	left: 700px;
	top: 13px;
}

.cs-buttom{
	position: absolute;
	left: 650px;
	top: 13px;
}

.copyright{
	position: absolute;
	left: 10px;
}
.button-wrap{
	position: absolute;
	left: 310px;
}


/* 멤버 */

.side-account {
	position: absolute;
	top: 50px;
	left: -28px;
	z-index: 9999;
	border: 0;
}
.side-account.after {
	border: 0;
}
/* 공감 */

.board-list .list-symph {
	display: none;
}
.board-list .list-title {
	padding-left: 7px;
}
/* 1024px 처리  */

.nav-container {
	width: 1024px;
	min-width: 1024px;
}
.content {
	width: 775px;
}
.content .action {
	margin-left: 744px;
}
.side-account {
	left: 0;
}
.board-list .list-title .list-subject {
	max-width: 480px;
}
#sympathyList {
	display: none;
}
#recentlyList {
	display: none;
}
.container {
	width: 1024px;
}
.card-view .list-row {
	width: 365px;
}
.card-view .card-image img {
	width: 333px;
}
.list-foot {
	width: 775px;
}
.mypage-nav {
	float: none;
}
/* 카테고리 길이 고정 및 가운데 정렬 추가 */

.subject-category {
	position: relative;
	float: left;
	text-align: center;
	width: 80px;
	margin: 0 5px 0 0;
}
.board-list .list-title .list-subject {
	float: left;
	margin-left: 5px;
}
.post-title .title-subject .subject-category {
	font-size: 14px;
	padding-top: 5px;
	vertical-align: middle;
}
/* 목록 줄무늬 적용 */

.board-list .list-row:nth-child(even) {
	background-color: #FAFAFA;
}
/* 라인 높이 맞춤(전체적 길이 줄임) */

.board-list .list-title {
	padding: 4px 0;
}
.comment .comment-row .comment-content {
	margin: 2px 10px;
}
.comment .comment-row .post-contact {
	height: auto;
	padding: 2px 6px;
}
.post hr.post-hr {
	margin: 0px auto 0px;
}
.comment-head {
	padding: 2px 10px 2px 4px;
}
.post .post-content .post-ccls {
	margin-top: 0px;
	height: 2px;
}
.post-title .title-symph span {
	padding: 2px 0;
}
.post-title {
	padding: 4px 15px;
}
.board-head {
	padding: 2px 0;
}
.comment-binbox {
	height: 2px;
}
.comment-hr {
	height: 2px;
	margin: 4px auto;
}
.post .button-region.active {
	padding: 2px 0;
}
.post .button-region {
	text-align: right;
}
.content {
	padding: 10px 30px 10px 30px;
}
.footer .container {
	padding: 2px;
}
.post .post-info {
	padding: 4px 0 4px 15px;
}
.post .post-foot {
	margin: 2px 15px;
}
.post .post-content {
	padding: 8px 16px 8px 16px;
}
p {
	margin: 0;
}
.fr-view p,
.post-article p {
	line-height: 1.6;
}
/* 상위 아이콘 색상 검정*/

.icon-board {
	background-color: #000;
}
/* 댓글 카운트 가독성 

.badge-reply {
	font-size: 9px;
	top: 1px;
	padding: 1px 7px 0px 7px;
	color: #fff;
	background-color: #81ade4;
	border-radius: 5px;
	border-left: 0px;
}
.preview .preview-list li .badge-reply {
	top: -1px;
}
.preview .preview-list .icon-image {
	top: 0px;
}
 */ 
}
