@-moz-document domain("forum.kadcon.de") {
:root {
    --hover-transitiontime: 0.3s;
    --background-image: none; /* user url(url) if you want to specify an from anywhere */
    --background-color: #444;
    /*
     * topMenu
     * http://prntscr.com/f4vhn2
     */
    
    --topMenu-color: black;
    --topMenu-bg-color: white;
    --topMenu-hover-color: white;
    --topMenu-hover-bg-color: black;
    --topMenu-badge: white;
    --topMenu-badge-bg: black;
    --topMenu-badge-hover: black;
    --topMenu-badge-hover-bg: white;
    
    /* 
     * searchbar
     * Den Placeholder umzufärben ist leider nicht möglich 
     */
    
    --topMenu-search-color: black;
    --topMenu-search-bg-color: white;
    
    /* Dropdown Menü wenn man bspw. auf Benachrichtigungen klickt */
    --topMenu-dropDown-bg-color: white;
    --topMenu-dropDown-color: black;
    --topMenu-dropDown-hover-bg-color: black;
    --topMenu-dropDown-hover-color: white;
    /* 
     * mainMenu
     * http://prntscr.com/f4vhyb
     */
    
    --main-color: black;
    --main-bg-color: white;
    --main-hover-color: rgba(0, 0, 0, 0.5);
    --main-hover-bg-color: white;
    --main-selected-color: white;
    --main-selected-bg-color-gradient-top: #555;
    --main-selected-bg-color-gradient-bottom: black;
    --main-selected-hover-color: rgba(255, 255, 255, 0.5);
    --main-selected-hover-bg-color-gradient-top: #555;
    --main-selected-hover-bg-color-gradient-bottom: black;
    --main-vip-color: black;
    --main-vip-bg-color: #E3C23C;
    --main-vip-hover-color: rgba(0, 0, 0, 0.2);
    --main-vip-hover-bg-color: #E3C23C;
    
    /* Badges im main Menu */
    
    --main-badge-color: white;
    --main-badge-bg-color: black;
    --main-badge-hover-color: white;
    --main-badge-hover-bg-color: black;
    --main-selected-badge-color: black;
    --main-selected-badge-bg-color: white;
    --main-selected-badge-hover-color: black;
    --main-selected-badge-hover-bg-color: white;
    
    /*
     * sidebars, navigation header & footer
     */
    
    --layout-background: black;
    --layout-text: white;
    --layout-link: white;
    --layout-link-hover: rgba(255,255,255,0.5);
    --layout-badge: black;
    --layout-badge-bg: white;
    --layout-badge-hover: black;
    --layout-badge-hover-bg: rgba(255,255,255,0.5);
    --layout-icons: white;
    --layout-icons-hover: black;
    --layout-icons-hover-bg: white;
    
    /*
     * Spezifisch für die sidebar
     */
    
    --side-link: rgba(255,255,255,0.3);
    --side-link-hover: rgba(255,255,255,0.8);
    --side-text: white;
    --side-button: black;
    --side-button-gradient-top: white;
    --side-button-gradient-bottom: #aaa;
    --side-selected: black;
    --side-selected-bg: white;
    
    /*
     * Buttons
     */
    
    --button: white;
    --button-gradient-top: #555;
    --button-gradient-bottom: black;
    --button-hover: rgba(255,255,255,0.5);
    --button-hover-gradient-top: #555;
    --button-hover-gradient-bottom: black;
    
    
    /*
     * Spezifisch für die shoutbox
     */
    
    --shout-link: #555;
    --shout-link-hover: rgba(0,0,0,0.8);
    --shout-text: black;
    --shout-hover: white;
    --shout-bg: white;    
    --shout-input: black;
    --shout-input-bg: white;
    
    /*
     * Die meisten UI Elemente
     * Beeinhaltet:
     * - Foren
     * - Sidebar von Beiträgen
     * - Top zeile vom Koversationstab
     */
    
    --ui-color: white;
    --ui-bg: black;
    --ui-badge: black;
    --ui-badge-bg: white;
    
    /*
     * Sidebars von Nachrichten
     */
    
    --u-bg: black;
    --u-name: white;
    --u-link: white;
    --u-link-hover: rgba(255,255,255,0.8);
    --u-startKov: black;
    --u-separateLine: white;
    --u-ingBadge: white;
    --u-badge: black;
    --u-badge-bg: white;
    
    
    /*
     * Auflistungen von Beiträgen/Konversationen
     */
    
    --table-head: white;
    --table-head-bg: black;
    --table-head-hover: rgbs(255,255,255,0.5);
    --table-head-hover-bg: black;
    --table-body-header: black;
    --table-body-other: #333;
    --table-body-bg: #333;
    --table-body-hover: black;
    --table-body-hover-other: #333;
    --table-body-hover-bg: white;
}

/*

TOP MENU
---------------------------------------------------------------------------------------------------------------------------

*/


.userPanel {
    background-color: var(--topMenu-bg-color);
}
.userPanel > div > ul.userPanelItems > li > a:hover,
.userPanel > div > ul.userPanelItems > li > a:hover > .icon{
    color: var(--topMenu-hover-color) !important;
    background-color: var(--topMenu-hover-bg-color) !important;
    transition: var(--hover-transitiontime) !important;
    text-shadow:none;
}
.userPanel > div > ul.userPanelItems > li > a,
.userPanel > div > ul.userPanelItems > li > a > .icon{
    color: var(--topMenu-color) !important;
    background-color: var(--topMenu-bg-color) !important;
    text-shadow:none;
    transition: var(--hover-transitiontime) !important;
}

.userPanel > div > ul.userPanelItems > li > a > .badge{
    color: var(--topMenu-badge) !important;
    background-color: var(--topMenu-badge-bg) !important;
    transition: var(--hover-transitiontime) !important;
    text-shadow:none;
}
.userPanel > div > ul.userPanelItems > li > a:hover > .badge {
    color: var(--topMenu-badge-hover) !important;
    background-color: var(--topMenu-badge-hover-bg) !important;
    text-shadow:none;
    transition: var(--hover-transitiontime) !important;
}

.userPanel > div > aside.searchBar > form > input[type=search],
.userPanel > div > aside.searchBar:before {
    border: none;
    color: var(--topMenu-search-color) !important;
    background-color: var(--topMenu-search-bg-color) !important;
}
input[type='search'] {
    box-shadow: none !important;
    border-radius: 0 !important;
    text-shadow: none !important;
}
div[id=dropdownMenuContainer] > ul.dropdownMenu:before {
    border: 10px solid transparent !important;
    border-bottom-color: var(--topMenu-dropDown-bg-color) !important;
    left: 6px !important;
    top: -7px !important;
    display: inline-block !important;
    border-top-width: 0 !important;
    content: "" !important;
    position: absolute !important;
    z-index: 101 !important;
}
div[id=dropdownMenuContainer] > ul.dropdownMenu {
    border-color: var(--topMenu-dropDown-bg-color) !important;
}
div[id=dropdownMenuContainer] > ul.dropdownMenu,
div[id=dropdownMenuContainer] > ul.dropdownMenu > li > a {
    color: var(--topMenu-dropDown-color) !important;
    background-color: var(--topMenu-dropDown-bg-color) !important;
    transition: var(--hover-transitiontime) !important;
}
div[id=dropdownMenuContainer] > ul.dropdownMenu > li > a:hover {
    color: var(--topMenu-dropDown-hover-color) !important;
    background-color: var(--topMenu-dropDown-hover-bg-color) !important;
    text-shadow: none;
    transition: var(--hover-transitiontime) !important;
}

/*

MAIN MENU
---------------------------------------------------------------------------------------------------------------------------

*/

.mainMenu > ul {
    background-color: var(--main-bg-color);
    text-shadow: none !important;
    transition: var(--hover-transitiontime) !important;
}
.mainMenu > ul:hover {
    background-color: var(--main-hover-bg-color);
    text-shadow: none !important;
    transition: var(--hover-transitiontime) !important;
}
.mainMenu > ul > li:not(.active) > a,
.mainMenu > ul > li:not(.active) {
    color: var(--main-color) !important;
    text-shadow: none !important;
    transition: var(--hover-transitiontime) !important;
}
.mainMenu > ul > li:hover:not(.active) > a {
    color: var(--main-hover-color) !important;
    text-shadow: none !important;
    transition: var(--hover-transitiontime) !important;
}
.mainMenu > ul > li.active > a {
    color: var(--main-selected-color) !important;
    text-shadow: none;
    transition: var(--hover-transitiontime) !important;
}
.mainMenu > ul > li.active > a:hover {
    color: var(--main-selected-hover-color) !important;
    text-shadow: none;
    transition: var(--hover-transitiontime) !important;
}
.mainMenu > ul > li.active > a {
    background-image: linear-gradient( var(--main-selected-bg-color-gradient-top), var(--main-selected-bg-color-gradient-bottom)) !important;
    transition: var(--hover-transitiontime) !important;
}
.mainMenu > ul > li.active > a:before,
.mainMenu > ul > li.active > a:after {
    color: rgba(0, 0, 0, 0);
    box-shadow: none;
}
.mainMenu > ul > li.active:hover > a {
    background-image: linear-gradient( var(--main-selected-hover-bg-color-gradient-top), var(--main-selected-hover-bg-color-gradient-bottom)) !important;
    transition: var(--hover-transitiontime) !important;
}
.mainMenu > ul > li.active > a > span.badge {
    color: var(--main-selected-badge-hover-color);
    background-color: var(--main-selected-badge-hover-bg-color);
}
.mainMenu > ul > li.active:hover > a > span.badge {
    color: var(--main-selected-badge-hover-color);
    background-color: var(--main-selected-badge-hover-bg-color);
}
.mainMenu > ul > li:not(.active):not(:nth-child(9)) > a > span.badge {
    color: var(--main-badge-hover-color);
    background-color: var(--main-badge-hover-bg-color);
}
.mainMenu > ul > li:hover:not(.active):not(:nth-child(9)) > a > span.badge {
    color: var(--main-badge-hover-color);
    background-color: var(--main-badge-hover-bg-color);
}

nav#mainMenu > ul > li:nth-child(9) > a{
    color: var(--main-vip-color) !important;
    background-color: var(--main-vip-bg-color) !important;
}

nav#mainMenu > ul > li:hover:nth-child(9) > a{
    color: var(--main-vip-hover-color) !important;
    background-color: var(--main-vip-hover-bg-color) !important;
}

/*

BACKGROUND
---------------------------------------------------------------------------------------------------------------------------

*/

body {
  margin-top: -20px;
  padding-top: 200px;
  background-image: var(--background-image);
  background-color: var(--background-color);
  background-position: center top;
}

/*

EASTER EGGS
---------------------------------------------------------------------------------------------------------------------------

*/

li > article[data-user-id='16323'] > div > aside.member > div > div.userRank > img{
  	display: inline-block;
    vertical-align: middle;
  	-moz-box-sizing: border-box;
  	box-sizing: border-box;
 	background: url('http://i.imgur.com/jFaCpOg.png') no-repeat!important;
    margin-top: 2px;
    margin-left: 2px;
    margin-bottom: -4px;
 	width: 20px; 
    height: 20px;
  	padding-left: 20px;
}

/*

NAVIGATION HEADER
NAVIGATION FOOTER
SIDEBAR
---------------------------------------------------------------------------------------------------------------------------

*/

.navigationHeader,
.navigationFooter,
#main > div > div > .sidebar,
.navigationFooter > ul.navigationItems,
#main > div > div > .sidebar > span > .collapsibleButton,
#main.sidebarOrientationRight{
 
    background-color: var(--layout-background) !important;
    border:none;
    border-left: none !important;
    transition: var(--hover-transitiontime) !important;
    
}

#main > div > div > .sidebar > span > .collapsibleButton,
.navigationHeader > ul.navigationIcons > li > a > span.icon,
.navigationFooter > ul.navigationIcons > li > a > span.icon,
.navigationFooter > ul.navigationItems > li > p > span.icon{
 
    color: var(--layout-icons);
    text-shadow:none;
    transition: var(--hover-transitiontime) !important;
    
}

#main > div > div > .sidebar > span > .collapsibleButton,
.navigationHeader > ul.navigationIcons > li > a,
.navigationHeader > ul.navigationIcons > li > a > span.icon,
.navigationFooter > ul.navigationIcons > li > a > span.icon,
.navigationFooter > ul.navigationItems > li > p > span.icon{
 
    color: var(--layout-icons);
    text-shadow:none;
    transition: var(--hover-transitiontime) !important;
    
}

.navigationHeader > ul.navigationIcons > li:hover > a,
.navigationHeader > ul.navigationIcons > li:hover > a > span.icon,
.navigationFooter > ul.navigationIcons > li:hover > a,
.navigationFooter > ul.navigationIcons > li:hover > a > span.icon{
 
    color: var(--layout-icons-hover) !important;
    background-color: var(--layout-icons-hover-bg) !important;
    text-shadow:none;
    transition: var(--hover-transitiontime) !important;
    
}

.navigationFooter > ul.navigationItems > li > p > span{
 
    color: var(--layout-text);
    text-shadow:none;
    transition: var(--hover-transitiontime) !important;
    
}

.navigationHeader > ul.navigationMenuItems > li > a,
.navigationFooter > ul.navigationMenuItems > li > a{
 
    color: var(--layout-link);
    text-shadow:none;
    transition: var(--hover-transitiontime) !important;
    
}

.navigation > ul.navigationMenuItems > li > .badge{
    
    color: var(--layout-badge) !important;
    background-color: var(--layout-badge-bg);
    transition: var(--hover-transitiontime) !important;
    
}

.navigation > ul.navigationMenuItems > li:hover > .badge{
    
    color: var(--layout-badge-hover) !important;
    background-color: var(--layout-badge-hover-bg);
    transition: var(--hover-transitiontime) !important;
    
}


.navigationHeader > ul.navigationMenuItems > li:hover > a,
.navigationFooter > ul.navigationMenuItems > li:hover > a{
 
    color: var(--layout-link-hover);
    text-shadow:none;
    transition: var(--hover-transitiontime) !important;
    
}

/*

SIDEBAR
---------------------------------------------------------------------------------------------------------------------------

*/

#main > div > div >.sidebar > div > fieldset > nav > ul > li:not(.active) > a,
#main > div > div > .sidebar > div > .dashboardBox > div > ul.sidebarBoxList > li > small > a,
#main > div > div > .sidebar > div > .dashboardBox > div > ul.sidebarBoxList > li > div > small > a{
    
    color: var(--side-link);
    text-shadow:none;
    text-decoration: none;
    transition: var(--hover-transitiontime) !important;
    
}

#main > div > div >.sidebar > div > fieldset > nav > ul > li:not(.active) > a:hover,
#main > div > div > .sidebar > div > .dashboardBox > div > ul.sidebarBoxList > li > small > a:hover,
#main > div > div > .sidebar > div > .dashboardBox > div > ul.sidebarBoxList > li > div > small > a:hover{
    
    color: var(--side-link-hover);
    text-shadow:none;
    text-decoration: none;
    transition: var(--hover-transitiontime) !important;
    
}


#main > div > div > .sidebar > div > .dashboardBox > legend,
#main > div > div > .sidebar > div > .dashboardBox > div > ul > li,
#main > div > div > .sidebar > div > .dashboardBox > div > ul > li > div > h3 > small,
#main > div > div > .sidebar > div > .dashboardBox > div > ul > li > div > h3 > small > time{
    
    color: var(--side-text);
    text-shadow:none;
    text-decoration: none;
    transition: var(--hover-transitiontime) !important;
    
}

#main > div > div >.sidebar > div > fieldset > nav > ul > li.active > a{
 
    color: var(--side-selected);
    background-color: var(--side-selected-bg);
    text-shadow:none;
    text-decoration: none;
    transition: var(--hover-transitiontime) !important;
    
}

/*

SHOUTBOX
---------------------------------------------------------------------------------------------------------------------------

*/

#main > div > div > .sidebar > div > .dashboardBox > div > .shoutboxBoxSidebar > div > div > ul > li > div > p > a{
    
    color: var(--shout-link);
    text-shadow:none;
    text-decoration: none;
    transition: var(--hover-transitiontime) !important;
    
}

#main > div > div > .sidebar > div > .dashboardBox > div > .shoutboxBoxSidebar > div > div > ul > li > div > p > a:hover{
    
    color: var(--shout-link-hover);
    text-shadow:none;
    text-decoration: none;
    transition: var(--hover-transitiontime) !important;
    
}

#main > div > div > .sidebar > div > .dashboardBox > div > .shoutboxBoxSidebar > div > div > ul > li.shoutboxEntry{
    
    background: var(--shout-bg);
    text-shadow:none;
    text-decoration: none;
    transition: var(--hover-transitiontime) !important;
    
}

#main > div > div > .sidebar > div > .dashboardBox > div > .shoutboxBoxSidebar > .shoutbox{
    
    background: var(--shout-bg);
    text-shadow:none;
    text-decoration: none;
    transition: var(--hover-transitiontime) !important;
    
}

#main > div > div > .sidebar > div > .dashboardBox > div > .shoutboxBoxSidebar > div > div > ul > li.shoutboxEntry:hover{
    
    background: var(--shout-hover);
    text-shadow:none;
    text-decoration: none;
    transition: var(--hover-transitiontime) !important;
    
}


#main > div > div > .sidebar > div > .dashboardBox > div > .shoutboxBoxSidebar > .shoutbox > form > input{
    
    color: var(--shout-input);
    background: var(--shout-input-bg);
    text-shadow:none;
    text-decoration: none;
    transition: var(--hover-transitiontime) !important;
    
}

#shoutbox .shoutboxContent.fadeOut:after{
   height: 0;   
}

/*

 BUTTONS
---------------------------------------------------------------------------------------------------------------------------

*/

a.button{
    
    color: var(--button);
    background-image: linear-gradient(var(--button-gradient-top),var(--button-gradient-bottom));
    border: none ;
    box-shadow: none;
    text-shadow: none;
    
}

a.button:hover{
    
    color: var(--button-hover);
    background-image: linear-gradient(var(--button-hover-gradient-top),var(--button-hover-gradient-bottom));
    text-shadow: none;
    
}

a.button > span.icon{
    color: var(--button);
    border: none ;
    box-shadow: none;
    text-shadow: none;   
}

a.button:hover > span.icon{
    color: var(--button-hover);
    border: none ;
    box-shadow: none;
    text-shadow: none;   
}

/*

CONTENT
---------------------------------------------------------------------------------------------------------------------------

*/

#content > div > ul > li > header > h2 > a,
#content > div > header > h2,
#content > div > ul > li > header > h2 > span.icon,
#content > div > ul > li > header > small,
#content > .userNotice > div > header > h2,
#content > .userNotice > div > header > small{
    color: var(--ui-color);
    text-shadow: none;
}

#content > .userNotice > .info,
#content > .userNotice > .info > a{

    color: var(--ui-color);
    background-image: linear-gradient(var(--ui-bg),var(--ui-bg));

}

#content > .userNotice > div > header,
#content > div > header,
#content > div > ul > li > header{
    
    background-color: var(--ui-bg);
    
}

#content > div > header > h2 > span.badge{
 
    color: var(--ui-badge);
    background-color: var(--ui-badge-bg);
    
}

#content > .marginTop > ul.messageList > li.marginTop > article.message > div {
    
    color:var(--ui-color);
    background-color: var(--ui-bg);
    text-shadow:none !important;
    
}

#content > .marginTop > ul.messageList > li.marginTop > article.message > div > .messageSidebar > div > header > h2.username > a > span{
 
    text-shadow:none !important;
    
}

}
