css:
@-moz-document domain("worldofwarcraft.com") {
a.Link.CharacterHeader-ilvl{
    height: 100%;
    padding: 6px;
}

/*Лого меньше*/
.Logo--horde.Logo--smaller {width: 45px;}
.Logo--alliance.Logo--smaller {width: 45px;}
/*Текст илвл и ачивки*/
.CharacterHeader-links {
    font-size: 1.5rem;
}
/*Сервер, лвл и раса */
.CharacterHeader-details {
    font-size: 1.02rem;
}
/*Длина*/
.CharacterProfile {
    height: 520px !important;
}
/*Менюшка */
.HorizontalNav--gutters .HorizontalNav-link {
    padding: 0;
}
.HorizontalNav-item {
    font-size: 1.1rem;
}
.space-small {
    height: 5px;
}
.Media--flush .Media-text {
    padding: 0;
    font-family: "Futura Demi","Open Sans",Arial,Helvetica,sans-serif;
    font-weight: 700;
    font-size: 1.7rem;
    /* color: white; */
}
/*шмотки*/
.gutter-large {
    padding: 0 200px;
    padding-bottom: 4px;
}
.CharacterProfile-gear--left .CharacterProfile-item, .CharacterProfile-gear--right .CharacterProfile-item {
    margin: 2px 0;
}
.CharacterProfile {
    height: 640px;
}
.color-quality-EPIC {
    line-height: 100%}
.CharacterProfile-itemDetails {
    font-size: 1.1rem;}
.CharacterProfile-render {
top: -60%;
}
.GameIcon--large {
    width: 44px;
    /*Размер иконок сжат. Изначальный 56px*/
}
.CharacterProfile-gear--bottom .CharacterProfile-item:first-child {
    margin-left: -58px;
}
.CharacterProfile-gear--bottom .CharacterProfile-item:last-child {
    margin-right: -58px;
}
.GameIcon--transmog .GameIcon-transmog {
height: 14px;
    width: 14px;
}

.CharacterProfile-itemLevel {
    padding: 3.33px;
    font-family: "Futura Demi","Open Sans",Arial,Helvetica,sans-serif;
        font-weight: 700;
    font-size: 1.4rem;
    line-height: 1.5em;
    color: #fff;
    text-shadow: 0 0 1px transparent, 0 1px 2px rgba(0,0,0,.8);
}
.Talent-body {
    padding: 0;
    /*display: none;*/
}
.Talent--leatherGrey {
    border: none;
    background: none;
}
element.style {
    height: auto;
}
.Box--leatherGreyDark {
border: none;
background: none;
}
/*Image background*/
.CharacterProfile-render {
    top: -44%;
    width: 70%;
}
.space-huge {
    height: 40px;
}
.space-medium {
    height: 28px;
}
/*Убираем нижнюю часть*/
.Pane--above .Pane-content {
    z-index: 2;
    display: none;
}
/*Raids*/
.RaidProgress-difficulty-name {
    width: 25%;
    display: none;
}
.RaidProgress-level.ru-ru, .ru-ru .RaidProgress-level {
    font-family: "Open Sans",Arial,Helvetica,sans-serif;
    display: none;
}
.RaidProgress-difficulty {
    border-bottom: solid 6px transparent;
}
.Progressbar-content {
    padding: 1px;
    min-height: 1.5em;
}
.Grid-1of4 {
    width: 20%;
}
.RaidProgress-name {
    font-size: 1.44rem;
    padding: 0;
    font-family: "Futura Demi","Open Sans",Arial,Helvetica,sans-serif;
    font-weight: 700;
    font-size: 1.3rem;
    line-height: 100%;
    /* color: white; */
}
.RaidProgress {
    /*background-color: rgba(0, 0, 0, 0.5);*/
    background-image: none;
    background-position: top center;
    border: solid 1px #505050;
    /*border: solid 2px #bf8d00;*/
}
/* Вертикальное древо талантов*/
.SyncHeight[queryselectoralways="39"] {
    margin-bottom: 30px;
}
.GameIcon.Media-icon{
	width: 20px;
    height: 20px;
}
.Media--small .Media-preText {
    font-size: 1.1rem;
}
.Media-preText {
	min-width: 29px;
    padding-right:0;
}
.font-bliz-light-xSmall-beige {
    font-size: 1.1rem;
}
.Grid.section-middle {
    clear:both;
}
.Media.Media--inline{
	display:block;
}
.Grid-1of3{
	clear:inherit;
}
/*Stats*/
/*.Grid[queryselectoralways="39"]::before{
	display: block;
    clear:none;
}
.Grid[queryselectoralways="39"]::after{
	display: block;
    clear:none;
}*/
/*PVP*/
.Box--leatherGrey {
    background-color: inherit;
    background-image: none;
    background-position: center center;
}
.Art--above{
	display:none;
}
.Chakram--pvpFrame {
	background-image:none;
}
.Chakram {
	background-image:none;
    width:auto;
    height:auto;
}
.Chakram::before{
	padding-top:35%;
}
.Chakram--textSmall .Chakram-text {
    font-size: 3rem;
    font-weight: 600;
}
.font-bliz-light-medium-white {
	line-height: 1.2em;
}
.Grid-1of2.SyncHeight-item.Grid-1of3.Grid-1of4[media-wide="Grid-1of4"]{
	    width: 25%;
}
.font-semp-xSmall-white {
    font-size: 1rem;
}
.Media--gutterLarge .Media-text {
    padding: 0 6px;
}
.Media-text{
	font-size: 1.7rem;
    font-weight: 700;
}
.CharacterProfile-export {
    bottom: -30px;
}
.Link--text {
    font-size: 1.1rem;
}
.position-right {
    right: 70px;
}
.List.List--guttersTiny{
	z-index: 3;
}
.CharacterHeader-name {
	line-height: .6em;
}
}
