css:
@-moz-document domain("www.smashcast.tv") {
body, body.channel {
    background-color: #111111;
}

a {
	color: #fff;
}

.htbx-button-show-more .htbx-icon {
    color: #fff;
}

.detail-sidebar__user, .detail-profile, .detail-chat, .feed-item, .detail-chat .chat-input__inner {
	background-color: #232428;
    color: #fff;
}

.chat-tabs {
    box-shadow: inset 0 -1px 0 0 #303030;
}

.resub-notification, .chat-input__inner {
	border-color: #303030;
}

.chat-input .public-DraftEditorPlaceholder-root, .chat-input__input, .chat-input__login {
	color: #fff;
}

chat-buttons .chat-buttons__button-row, chat-buttons .chat-buttons__tab-content {
	border-color: #303030;
}

.chat-tabs {
    color: #e5e5e5;
}

.htbx-cheering-esports-scores__team--team1, .htbx-cheering-esports-scores__team__name {
    color: #e5e5e5;
}

.htbx-cheering-voting-controls__team--team1, .htbx-cheering-voting-controls__team__progress {
    background-color: #e5e5e5;
}

.chat-tabs__item:after {
    background-color: #b4b6b6;
}

.main-nav {
	background-color: #232428;
    box-shadow: inset 0 -1px 0 #303030;
}

.nav-links__link {
	color: #ffffff;
}

.main-nav__item--burger .htbx-icon, .main-nav__item--language-select .htbx-icon, .main-nav__item--search .htbx-icon, .main-nav__item--signin-mobile .htbx-icon, .main-nav__item--studio .htbx-icon, .main-nav__item--user .htbx-icon {
	color: #ffffff;
}

.chat-input-panel__inner {
	border:solid #e5e5e5;
}

.chat-input-panel__inner:after {
	border-bottom:1px solid #e5e5e5;
}

.chat-input-panel__header {
	border-bottom:1px solid #e5e5e5;
	background-color:#e5e5e5;
}

.follow-button--variant-primary .follow-button__button {
    background: #0caafd;
}

.subscription-button {
    background-color: #8dbc14;
}

.subscription-button:hover {
    background-color: #9acb02;
}

.button--ok, .button--variant-ok {
    background-color: #8dbc14;
}

.button--ok, .button--variant-ok:hover {
    background-color: #9acb02;
}

.new-feed-item__tab--active {
    color: #8dbc14;
}

.logo__icon path {
  fill: #8dbc14;
}

.detail-sidebar--mobile {
    background-color: #232428;
}

h4 {
    color: #ffffff;
}

.detail-tabs__tab:after {
    background-color: #e5e5e5;
}

.user-card__name {
    color: #333333;
}

.user-card__name:hover {
    color: #208efc;
}

.commercial-break-settings__card1__button__button {
    background-color: #8dbc14;
}

.commercial-break-settings__card1__button__button:hover {
    background-color: #9acb02;
}

.studio-dashboard-stream-info__quality-text {
    color: #8dbc14;
}

.layout--page-dashboard {
    background-color: #1b1c1f;
}

.chatBody {
    background-color: #27282b;
}

.chat-messages.normal .message {
    color: #ffffff;
}

.chat-user-list .user .name {
    color: #ffffff;
}

body.dashboard {
    background-color: #232428;
}

.dash__column__header {
    color: #ffffff;
    text-shadow: 1px 1px 2px #000000;
}

.commercial-break-settings__card1 {
        background-color: #232428;
}

.dashboard__chat__mod-settings {
        background-color: #232428;
}

.tse-scrollable .tse-scroll-content {
        background-color: #232428;
}

.dash__input {
        background-color: #2a2b2e;
        border-bottom: 1px solid #232427;
        padding: 15px 15px 10px;
}

.dash__input__label {
        color: #68696c;
}

.dash__input__input {
        color: #d2d3d6;
}

.dash__column--column-stream-actions__bottom-gradient {
    background: transparent linear-gradient(0deg,#1b1c1f 20%,transparent);
}

.dash__column--column-actions .dash__column__header, .dash__column--column-stream-actions .dash__column__header, .dash__column--column-stream .dash__column__header {
    background: transparent linear-gradient(0deg,#1b1c1f 20%,transparent);
}

.chatBody .chat-buffer, .chatBody .chat-status-message {
        color: #d2d3d6;
}

.player-controls-time {
    color: #ffffff;
    text-shadow: 1px 1px 2px #000000;
}

.studio-dashboard-stream-title__viewers {
    color: #eb5055;
    text-shadow: 1px 1px 2px #000000;
}

htbx-collapsible-title, .collapsible__header__title {
    color: #d6d7da;
}

.collapsible__header {
    background-color: #2a2b2e;
}

.dash .collapsible {
    border-color: #2a2b2e;
}

.dash__separator.dashSepShow {
    background: #1f1f1f;
}

chat-input-wrap {
    background-color: #2a2b2e;
}

chat-buttons .chat-buttons__button-row__button:after {
    background: #ffffff;
}

.button {
    background-color: #8dbc14;
}

.button:hover {
    background-color: #9acb02;
}

.sub-nav__link {
    color: #ffffff;
}

.platform-info {
    background-color: #2a3139;
}

.nav-links__link {
    color: #bdbdbe;
}

.nav-links__link:hover {
    color: #8dbc14;
}

.platform-info__info__text {
    color: #e6e6e6;
}

.home-page__content__item .esports-media__content {
    background-color: #293039;
}

.esport-scores__team__name, .esport-scores__divider__text, .esport-scores__tournament__title, .esport-scores__info__lang {
    color: #e6e6e6;
}

.esport-scores__upcoming__text {
    background-color: #293039;
}

.esport-scores__upcoming {
    color: #8e9196;
}

.browse-channels--following, .browse-channels--following-vods {
    background-color: #16191d;
}

.home-page__content__item--category-esports, .home-page__content__item--category-recommended, .home-page__content__item--category-games, .home-page__content {
    background-color: #16191d;
}

.featured-media {
    background-color: #232428;
}

.sub-nav__item--is-active .sub-nav__link:hover {
    color:#8dbc14
}

.sub-nav__item--is-active {
    box-shadow:inset 3px 0 0 0 #8dbc14
}

.sub-nav__item--is-active .sub-nav__link {
    color:#8dbc14;
}

.main-nav__item--burger:hover .htbx-icon,.main-nav__item--language-select:hover .htbx-icon,.main-nav__item--search:hover .htbx-icon,.main-nav__item--signin-mobile:hover .htbx-icon,.main-nav__item--studio:hover .htbx-icon,.main-nav__item--user:hover .htbx-icon {
    color:#8dbc14
}

.navigation__item--active .navigation__item__link {
    color: #fff;
    background-color: #8dbc14;
}

.main-nav-search__header .button-close:hover .htbx-icon {
    color:#8dbc14
}

h1, .media-list__items .t-name {
    color:#e6e6e6;
}

.media-list__items .t-status a, .media-list-title__sub-title, .media-list__no-content {
    color: #9f9f9f;
}

.content-nav--variant-default {
    background-color: #292f38;
}

.content-nav--variant-default .content-nav__item--is-active .content-nav__item__link {
    color: #8dbc14;
}

.content-nav--variant-default .content-nav__item__link:after {
    background-color: #8dbc14;
}

.button__text {
    color: #ffffff;
}

.button--outline, .button--variant-outline {
    border: 1px solid #8dbc14;
}

.button--outline, .button--variant-outline:hover {
    border: 1px solid #8dbc14;
}

.dash__column__header, .studio-dashboard-stream-title {
    text-shadow: 1px 1px 2px #333;
    color: #ffffff;
}
}
