css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("app.simplenote.com") {
/*ツールバー縮小 toolbar shrink*/
    .toolbar{
        height: 42px !important;
    }
/*サイドバー縮小関連 sidebar shrink*/
   .sidebar{
       width: 280px !important;
   }
   /*タグバー位置調整 move(adjust) tagbar*/
      .tagbar{       
          left: 280px !important;
          padding-left: 15px !important;
      }
      ul.as-selections{
          padding-left: 0px !important;
      }
   /*各種ツールバーの移動 move tools*/
      .toolbar .left-tools .tbutton.add {
          margin: 6px 15px 0px 30px !important;
      }
      .toolbar .tools {
          position: static !important;
      }
      .left-side-menu {
          left: 540px !important;
          top: 12px !important;
      }
   /*サイドバーのリスト関連 sidebarlist change size&adjust&add dashed bar*/
      .notes li{
          padding: 3px 4px 3px 6px !important;
          border-bottom: dashed 1.2px #448ac9 !important;
      }
      .notes li .note-preview-title{
          width: 250px !important;
          text-overflow: ellipsis
      }
      .notes li .note-preview-line{
          width: 250px !important;
          text-overflow: ellipsis
      }   
   /*検索ボックス及び検索ボックス内の全消去ボタンの位置調整 serchbox adjust*/
      .searchfield {
          left: 8px !important;
          top: 9px !important;
          width: 230px !important;
      }
      .searchcancel {
          left: 248px !important;
      }
/*プレビューの表示及び調整 markdown preview display&adjust*/
    #static_content {
        width: 50%!important;
        float: right !important;
        padding: 6px   8px 6px   8px !important;
    }
    .note #static_content h1{
        font-size: 2.3em !important;
        margin-bottom: 0.3em !important;
    }
    .note #static_content h2{
        font-size: 1.8em !important;
        margin-bottom: 0.5em !important;
    }
    .note #static_content h3,.note #static_content h4{
        margin-bottom: 0.3em !important;
    }
    .note #static_content h1, .note #static_content h2, .note #static_content h3, .note #static_content h4{
        margin-top: 0.2em !important;
    }
    .note #static_content p{
        margin-bottom: 4px  !important;
    }
   /*編集画面の調整 edit screen adjust*/
      #txtarea{
          display: inline-block !important;
          width: 50% !important;
          padding: 6px 8px 6px 8px !important;
      }
    .note {
        left: 280px !important;
    }
/*ページ下部消去 page bottom margin&footer none*/
    .wrapper{
        height:100% !important; 
    }
    .footer {
        display: none !important;
    }
}
