css:
@-moz-document domain("open.spotify.com") {
.nav-bar-container:after {
    content: "Menu here :)";
    position: absolute;
    top: 80px;
    left: 150px;
    transform: rotate(90deg);
    width: 120px;
    color: rgba(225,225,225,.3);
    animation: menuhere 10s linear;
    opacity: 0;
}

@keyframes menuhere {
    0% { opacity: 1;}
    100% { opacity: 0;}
}

#main .root .top-container .main-view-container {
    padding-left: 0px;
}

#main .root .top-container .nav-bar-container {
    left: -200px;
    transition: .3s;
    background-color: rgba(0,0,0,.3);
}

#main .root .top-container .nav-bar-container:hover {
    left: 0 !important;
    background-color: rgba(0,0,0,.9) !important;
}

.navlist .download-item {
    display: none;
}

.recently-played .navlist>div:nth-child(5) {
    display: inline !important;
}

@media (min-width: 1200px){
.col-lg-3 {
    width: 16.66666667%;
}
}
}
