css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("smashcast.tv") {
/* BEGIN OF NAVBAR */
.main-nav__container {
    background-color: #333;
}
.nav-links__link {
    position: relative;
    display: block;
    line-height: 60px;
    height: 60px;
    font-size: 14px;
    color: white;
    padding: 0 20px;
}
.main-nav__item--burger .htbx-icon, .main-nav__item--language-select .htbx-icon, .main-nav__item--search .htbx-icon, .main-nav__item--signin-mobile .htbx-icon, .main-nav__item--studio .htbx-icon, .main-nav__item--user .htbx-icon {
    color: white;
}
/* END OF NAVBAR */
/* BEGIN OF HOMEPAGE */
    .home-page__content__item--category-recommended .t-meta .t-name, .home-page__content__item--category-recommended .t-meta .t-status{
        color: white;
    }
    .home-page__content__item--category-esports, .home-page__content__item--category-recommended{
        background-color: #333;
    }
    .home-page__content__item--category-esports .carousel-controls__button .htbx-icon, .home-page__content__item--category-recommended .carousel-controls__button .htbx-icon {
        background-color: #333;
    }
    .home-page__content__item--category-recommended .htbx-carousel-pagination__item {
        background-color: white;
    }
    .home-page__content__item--category-esports .htbx-carousel-pagination__item {
        background-color: white;
    }
    .home-page__content__item--category-esports .htbx-icon__icon {
        color: white;
    }
    .home-page__content__item--category-recommended .media-list-carousel__headline__title {
        color: white;
    }
    .home-page__content__item--category-recommended a {
    text-decoration: none;
    border: 0;
    color: white;
    cursor: pointer;
}
     .home-page__content__item--category-esports a {
    text-decoration: none;
    border: 0;
    color: white;
    cursor: pointer;
    }
    .home-page__content__item--category-recommended .htbx-icon__icon {
        color: white;
    }
 /* END OF HOMEPAGE */
 /* BEGIN OF DASHBOARD */
 body.dashboard {
    background-color: #333;
     color: white;
}
.dash__column__header, .studio-dashboard-stream-title {
    display: block;
    margin-bottom: 0;
    border-bottom: 0;
    width: 100%;
    height: 50px;
    font-size: 16px;
    font-weight: 600;
    color: white;
    line-height: 50px;
    white-space: nowrap;
    }

    .collapsible--variant-default, .collapsible--variant-default-active {
    background-color: #333;
    transition: border-color .2s ease-in-out,background-color .2s ease-in-out;
}
 .dash__column--column-actions .dash__column__header, .dash__column--column-stream-actions .dash__column__header {
background: transparent;
}
    .dash__column--column-stream-actions__bottom-gradient {
        background: transparent;
    }
    .dash__column__header, .studio-dashboard-stream-title {
        text-shadow: none;
    }
    .dash .dash__separator__hostmode.dashSepShow {
        background-color: #333;
    }
    .dash__separator.dashSepShow {
        background-color: #333;
    }
/* END OF DASHBOARD */
/* BEGIN OF LIVE PAGE */
    .detail-chat {
        background-color: #252729;
    }
    .detail-chat a {
        color: white;
    }
body.channel {
    background-color: #333;
    color: white;
}
    .feed-item-header__user__name {
        color: #333;
    }
    .related-channels a {
        color: white;
    }
    .detail-chat .htbx-icon__icon{
    color: white;
}
    .emotes-list__not-subscribed__text {
        color: #333;
    }
    .choice-text {
        color: #333;
    }
    .poll__question {
        color:#333;
    }
    .chat-gif-selector__search__input {
        color: white;
    }
}
/* END OF LIVE PAGE */
