css:
@-moz-document domain("randaris-anime.net") {
/* Main */
::-webkit-scrollbar {
  width: 10px;
}
::-webkit-scrollbar-thumb {
  background: #676767;
  border-radius: 20px;
}
::-webkit-scrollbar-track {
  background: #151515;
  border-radius: 20px;
}
body {
	color:#4e5152;
}
a, p, label, button, b, li, article, .text-center, .header, blockquote, small, bold, .panel-heading {
    color: #cacaca !important;
}
h1, h2, h3, h4 {
	color: #ffffff;
}
a > h3 {
	color: #cacaca
}
select {
	background-color: #252525 !important;
	box-shadow: none !important;
	outline: none !important;
}
.btn-ra-default:active, .btn-ra-default:active:active, .btn-ra-default:active:focus, .btn-ra-default:active:hover, .btn-ra-default:focus, .btn-ra-default:focus:active, .btn-ra-default:focus:focus, .btn-ra-default:focus:hover, .btn-ra-default:hover, .btn-ra-default:hover:active, .btn-ra-default:hover:focus, .btn-ra-default:hover:hover {
	background-color: #848484;	
}

.navbar-default {
    background-color: #252525 !important;
}
.link-list > a {
	background-color: #0ea585 !important;
}
.navbar-brand {
    filter: invert(100%);
    overflow: visible;
}
.link-random > a{
    background-color: #6d6d6d !important;
}
.glyphicon {
	color: #cacaca;
}
.dropdown-menu {
	background-color: #252525;
    border: 1px solid #353535;
}
.open>a {
	background-color: #848484 !important;
}
.dropdown-menu > li > a:hover {
	background-color: #848484 !important;
}

input {
	background-color: #121212 !important;
	border: 1px solid #353535 !important;
	box-shadow: none !important;
	color: #999 !important;
}

#site-body > .container {
    background-color: #252525;
}
#site-body {
    background-color: #1b1b1b;	
}
.searchbar-large{
	background: #1b1b1b !important;
	border-bottom: none; 
}
#liveSearch, #liveSearchLarge, .alphabetical-list {
	border: none !important;
	background-color: #121212 !important;
}
#liveSearch {
	font-size: 15px;
	box-shadow: none !important;
}
.search {
	background-color: #2d2d2d !important;
}
.input-group-btn > .btn-ra-default {
    background: linear-gradient(to right, #121212, #292929) !important;
	border: none !important;
	color: #cacaca !important;
}

.alert {
	background-color: #2d2d2d;
	border: 1px solid #353535;
	color: #cacaca !important;
}

img {
	filter: brightness(90%);
}
.adblock-box {
	display: none !important;
}
.social-icons > li > a {
	background-color: #2d2d2d !important;
	border: 1px solid #353535 !important;
}
.social-icons>li>a.facebook {
	border-right: none !important;
    border-left: none !important;
}

.btn-ra-default {
	background-color: #2d2d2d;
    border: 1px solid #353535;
}
.chat {
	border: none;
}
.chat-wrapper > .chat {
	background-color: #2d2d2d !important;
}
.chat__message {
    border-bottom: 1px solid #353535;
}
.chat__message__content {
    filter: brightness(300%);
}

.chat__input-wrapper {
	border-top: 4px solid #252525;
}
.chat__submit {
	background-color: #2d2d2d !important;
    border: 1px solid #353535 !important;
	opacity: 1 !important;
}
.is-center {
	color: #cacaca !important;
	background-color: #2d2d2d;
    border: 1px solid #353535;
}

.js-activity-feed {
	background-color: #252525 !important;
}
.js-activity-feed > iframe {
	background-color: #2d2d2d !important;
	border: 1px solid #353535 !important;
}
.activity-stream {
	border: none !important;
}
.activity-stream > ul > li {
	background-color: #252525 !important;
}


.btn-default, .alert-ra-home, .tab-content-frontpage {
	background-color: #2d2d2d !important;
	border: 1px solid #353535 !important;
}
.thumbnail {
	border: 2px solid #353535;
}
.tab-content-frontpage {
	background-color: #2d2d2d;
    border: 1px solid #353535;
	border-top: none !important;
}
.nav-tabs > li > a {
	background-color: #252525 !important;
    border: 1px solid #353535 !important;
    height: 27px !important;
	box-sizing: content-box;
}
.nav-tabs > li > a:hover,.nav-tabs > .active > a  {
	background-color: #2d2d2d !important;
	border: 1px solid #353535 !important;
    border-bottom: none !important;
	height: 28px !important;
}
.table-anime-updates-header {
	background-color: #2d2d2d;
	border: 1px solid #353535 !important;
	border-bottom: none !important;
}
.table-bordered>tbody>tr>td, .table-bordered>tbody>tr>th, .table-bordered>tfoot>tr>td, .table-bordered>tfoot>tr>th, .table-bordered>thead>tr>td, .table-bordered>thead>tr>th {
	border: 1px solid #353535 !important;
}
.table-anime-updates td.item-image {
	background: #2d2d2d !important;
}
.table-hover>tbody>tr:hover>td, .table-hover>tbody>tr:hover>th {
	background-color: #848484;	
}

/* Recommendations */
.table-striped>tbody>tr:nth-child(odd)>td, .table-striped>tbody>tr:nth-child(odd)>th {
	background-color: #2d2d2d;
}
.table-hover>tbody>tr.warning:hover>td, .table-hover>tbody>tr.warning:hover>th, .table-hover>tbody>tr>td.warning:hover, .table-hover>tbody>tr>th.warning:hover, .table>tbody>tr.warning>td, .table>tbody>tr.warning>th, .table>tbody>tr>td.warning, .table>tbody>tr>th.warning, .table>tfoot>tr.warning>td, .table>tfoot>tr.warning>th, .table>tfoot>tr>td.warning, .table>tfoot>tr>th.warning, .table>thead>tr.warning>td, .table>thead>tr.warning>th, .table>thead>tr>td.warning, .table>thead>tr>th.warning {
    background: #1c242b;
}

/* Friends */
.article {
	background-color: #2d2d2d;
    border: 1px solid #353535;
}
.article > table {
	border: none;
}

/* History */
.body-main-col > div > ul.list-group > li {
	background-color: #2d2d2d;
    border: 1px solid #353535;
}

/* Anime List / Profile */
.user-info > li:not(.rank-item) {
	background-color: #2d2d2d;
    border: 1px solid #353535;
}

.table>tbody>tr.success>td, .table>tbody>tr.success>th, .table>tbody>tr>td.success, .table>tbody>tr>th.success, .table>tfoot>tr.success>td, .table>tfoot>tr.success>th, .table>tfoot>tr>td.success, .table>tfoot>tr>th.success, .table>thead>tr.success>td, .table>thead>tr.success>th, .table>thead>tr>td.success, .table>thead>tr>th.success {
	background-color: #1c2b20;
}

.btn-info {
	background-color: #084a4e;
}
.btn-info:hover {
	background-color: #0c6f75;
}

.glyphicon-tree-conifer {
	color: #4e5152;
}
.glyphicon-fire {
	color: #a94442;
}
.glyphicon-thumbs-up {
	color: #4e5152;
}

.user-anime-stats {
	background-color: #2d2d2d;
}

thead {
	background-color: #252525 !important;
	border-bottom: 3px solid #353535;
}

.animelist-nav > li > a {
	background-color: #052d00 !important;
	border: 2px solid #353535;
}
.animelist-nav > li:hover > a {
	background-color: #0b6100 !important;
}

/* Anime Detail */
.anime-details .rating-distribution ul .rating-distribution-bar, .rating-distribution ul .rating-distribution-bar {
	background-color: #2d2d2d;
}

.rating-number-large {
	border: 1px solid #353535;
}

.shariff-button {
	border: 1px solid #353535 !important;
}
.shariff-button > a {
	background-color: #2d2d2d !important;
	color: #cacaca !important;
}

.anime-description {
	background-color: #2d2d2d;
    border: 1px solid #353535;
    color: #cacaca;
}

.user-lists-data > div {
	background-color: #252525;
	border: 3px solid #2d2d2d;
}

.btn-warning {
	color: #fff !important;
}

.episode-list > .panel-default {
 	background-color: #2d2d2d;
	border-color: #353535;
}
.panel-default > .panel-heading {
	background-color: #2d2d2d;
}
.panel-group .panel-heading+.panel-collapse .panel-body {
	border-color: #353535;
}
.episode-list li:nth-child(2n), .update-list li:nth-child(2n) {
	background-color: #252525;
}

.white-popup {
	background-color: #1b1b1b !important;
}

/* Anime Video Watching */

.panel-default {
	border: 1px solid #353535;
}
.panel-heading {
	border-color: #353535 !important;
}
.panel-body {
	background-color: #2d2d2d;
	border-color: #353535;
}
.mirror-card {
	border-color: #353535;
}
.mirror-card:hover {
	background-color: #848484;
}
.mirror-card > hr {
	border-color: #353535 !important;
}
.mirror-card.active {
	background-color: #848484;
}
.mirror-card.active > div, .mirror-card.active > div > b {
	color: #fff !important;
}
.mirror-card.active > hr {
	border-color: #afafaf  !important;
}

/* Pages */

.pagination > li > a {
	background-color: #2d2d2d;
	border-color: #353535; 
}
.pagination > li.active > a {
	background-color: #1c242b !important;
	border-color: #2b3742 !important; 
}

/* Anime Seasons */

div.list-group-item {
	background-color: #252525;
}
a.list-group-item {
	background-color: #2d2d2d;
}
a.list-group-item:hover {
	background-color: #848484;
}
}
