css:
@-moz-document domain("open.spotify.com")
{
  nav.navBar
  {
    background: #151515 !important;
  }

  .JKKBXFILp09eIN4WugwgY, .tracklist-row:focus, .inputBox, ._1wLr_CVCgF5x-vj1F2EkS7
  {
    background: #151515 !important;
  }

  .now-playing-bar
  {
    background: #151515 !important;
  }

  .tracklist-row .play-pause, .track-name, .position, .tracklist-row .tracklist-col.name .track-name-wrapper .artists-album a, .total-duration, .btn, a, body, .recently-played .navlist .navlist-item .navlist-itemlink .type, .inputBox-input
  {
    color: #FFFF !important;
    text-shadow: #FFFF !important;
  }

  .artists-album-separator
  {
    color: #151515 !important;
  }

  .tracklist-row
  {
    transition: 0s !important;
  }

  .tracklist-row:hover
  {
    background-color: #151515 !important;
    outline: 1px solid #FFFFF !important;
  }

  .progress-bar__fg
  {
    background-color: #FFFFF !important;
  }

  .control-button--active, .tracklist-row.active .duration, .tracklist-row.active .play-pause, .tracklist-row.active .position, .tracklist-row.active .track-name
  {
    color: #23CF5F !important;
  }

  .navBar .group
  {
    border-top: 1px solid #4b4d4b !important;
  }

  .ads-container
  {
    background-color: transparent !important;
    border-top: 1px solid transparent !important;
  }

  .inputBox-input
  {
    border-bottom: 1px solid #FFFFF !important;
  }

  .btn.btn-black
  {
    background: #151515 !important;
    border: 2px solid #151515 !important;
  }

  .btn.btn-green
  {
    background-color: #23CF5F !important;
    color: #FFFFF !important;
  }

  ._2rScaV3bBHoj7O47a_JnaH:after, ._2rScaV3bBHoj7O47a_JnaH:hover:after
  {
    background-color: #FFFFF !important;
  }

  .connect-bar
  {
    background-color: #FFFFF !important;
  }

  .connect-bar:after
  {
    border-bottom: 10px solid #FFFFF !important;
  }

  .control-button:hover
  {
    color: #FFFFF !important;
  }
}
