css:
@-moz-document url-prefix("https://avistaz.to/"), url-prefix("https://privatehd.to/"), url-prefix("https://cinemaz.to/") {
body {
background:#222222;
color:#ccc;
}
.navbar-default {
background:#008A67;
border:0;
}
.navbar-default .navbar-nav>.active>a, .navbar-default .navbar-nav>.active>a:focus, .navbar-default .navbar-nav>.active>a:hover, .navbar-default .navbar-nav>li>a:focus, .navbar-default .navbar-nav>li>a:hover {
background-color:#007C5D;
}
.ratio-bar {
/* background:#464545; */
background:#303030;
border:0;
}
.ratio-bar .badge-user {
    background: transparent;
}
.ratio-bar a {
	color:#639BE4;
}
.block, .panel {
    border: 1px solid #464545;
    background-color: #303030;
}
.form-bordered .form-group {
    border-color: #464545;
}
.table-striped>tbody>tr:nth-of-type(odd) {
    background-color: #222222;
}
a, h1.title a:visited {
    color: #00DBA3;
    text-decoration: none;
}
a:focus, a:hover, a.torrent-filename:visited:hover {
    color: #ccc;
    text-decoration: none;
}
a.torrent-filename:visited {
    color: #008A67;
}
i.torrent-icon {
    color: #999;
}
.table>tbody>tr.success>td, .table>tbody>tr.danger>td, .table>tbody>tr.warning>td, .table>tbody>tr.info>td {
    background:none;
}
.table>tbody>tr.success>td .torrent-icon, .table>tbody>tr.success>td:first-child {
    color:#5CB85C;
}
.table>tbody>tr.danger>td .torrent-icon, .table>tbody>tr.danger>td:first-child {
    color:#E74C3C;
}
.table>tbody>tr.warning>td .torrent-icon, .table>tbody>tr.warning>td:first-child {
    color:#F39C12;
}
.table>tbody>tr.info>td .torrent-icon, .table>tbody>tr.info>td:first-child {
    color:#5BC0DE;
}
.badge-extra {
    color: #999;
    border: 0;
}
.badge-user {
    border: 0;
}
.group-member, .group-member a, .ratio-bar {
    color: #ccc;
}
.group-vip, .group-vip a {
    color: #c742ff;
}
.group-newbie, .group-newbie a {
    color: #888;
}
.group-uploader, .group-uploader a {
    color: #00bc63;
}
.group-moderator, .group-moderator a {
    color: #f39c12;
}
.group-admin, .group-admin a {
    color: #e74c3c;
}
.navbar-default .dropdown-menu {
    background: #303030 !important;
}
.table-bordered, .table-bordered>tbody>tr>td, .table-bordered>tbody>tr>th, .table-bordered>tfoot>tr>td, .table-bordered>tfoot>tr>th, .table-bordered>thead>tr>td, .table-bordered>thead>tr>th {
    border-color:#222;
    border-bottom:0;
}
.table>tbody>tr>td, .table>tbody>tr>th, .table>tfoot>tr>td, .table>tfoot>tr>th, .table>thead>tr>td, .table>thead>tr>th, .table>thead>tr>th {
    border-top:0;
    border-bottom:0;
}
.table-hover>tbody>tr:hover, .table>tbody>tr.active>td, .table>tbody>tr.active>th, .table>tbody>tr>td.active, .table>tbody>tr>th.active, .table>tfoot>tr.active>td, .table>tfoot>tr.active>th, .table>tfoot>tr>td.active, .table>tfoot>tr>th.active, .table>thead>tr.active>td, .table>thead>tr.active>th, .table>thead>tr>td.active, .table>thead>tr>th.active, .table-hover>tbody>tr.info:hover>td, .table-hover>tbody>tr.info:hover>th, .table-hover>tbody>tr:hover>.info, .table-hover>tbody>tr>td.info:hover, .table-hover>tbody>tr>th.info:hover {
    background-color: #464545;
}
.table {
	border:1px #222 solid;
}
.table-bordered {
    border-bottom:1px #222 solid;
}
.table-striped>tbody>tr:nth-of-type(odd) td {
    border-color:#303030;
}
.table-striped>tbody>tr:nth-of-type(odd) td:first-child {
    border-left-color:#222;
}
.table-striped>tbody>tr:nth-of-type(odd) td:last-child {
    border-right-color:#222;
}
.table thead {
    background-color: #404040;
}
.table, .table .table {
	background-color: #303030;
}
.nav-tabs>li>a {
    background-color:#555;
    color: #ccc;
    position: relative;
    top: 1px;
    z-index: 100;
}
.nav-tabs>li.active>a, .nav-tabs>li.active>a:focus, .nav-tabs>li.active>a:hover {
    color: #ccc;
    background-color: #303030;
    border: 1px solid #464646;
    border-bottom-color: #303030;
    cursor: default;  
}
.nav>li>a:focus, .nav>li>a:hover {
    text-decoration: none;
    background-color: #008a67;
}
.nav-tabs>li>a:hover {
    border-color: #464646 #464646 #303030;
}
.nav-tabs {
    border-bottom: 1px solid transparent;
}
.panel-default>.panel-heading, .panel-footer {
    color: #ccc;
    background-color: #404040;
    border-color: #464545;
}

.pagination>.disabled>a, .pagination>.disabled>a:focus, .pagination>.disabled>a:hover, .pagination>.disabled>span, .pagination>.disabled>span:focus, .pagination>.disabled>span:hover {
    color: #999;
    background-color: #303030;
    border-color: #464545;
}
.pagination>li>a, .pagination>li>span {
    color: #cccccc;
    background-color: #222222;
    border: 1px solid #464545;
}
.pagination>.active>a, .pagination>.active>a:focus, .pagination>.active>a:hover, .pagination>.active>span, .pagination>.active>span:focus, .pagination>.active>span:hover {
    color: #fcfcfc;
    background-color: #555;
    border-color: #464545;
}
.pagination>li>a:focus, .pagination>li>a:hover, .pagination>li>span:focus, .pagination>li>span:hover {
    color: #ccc;
    background-color: #008a67;
    border-color: #464545;
}
.label-default {
    background-color: #222222;
    color: #ccc;
}
.form-control {
    background-color: #464545;
    border: 1px solid #222222;
}
.form-control:focus {
    border-color: #008a67;
    outline: 0;
    box-shadow: none;
}
.form-control, output {
    color: #cccccc;
}
.select2-container--bootstrap.select2-container--focus .select2-selection, .select2-container--bootstrap.select2-container--open .select2-selection {
    box-shadow: none;
    border-color: #008a67;
}
.select2-container--bootstrap .select2-selection {
    background-color: #464545;
    border: 1px solid #222;
    color: #ccc;
}
.select2-container--bootstrap .select2-selection--single .select2-selection__rendered {
    color: #cccccc;
    padding: 0;
}
.select2-container--bootstrap .select2-dropdown {
    border-color: #222222;
    background-color: #464545;
}
.select2-container--bootstrap .select2-results__option--highlighted[aria-selected] {
    background-color: #008a67;
    color: #ccc;
}
.select2-container--bootstrap .select2-search--dropdown .select2-search__field {
    background-color: #555;
    border: 1px solid #222;
    color: #cccccc;
}
ul.comments-list li {
    background-color: #222222;
}
.comment-compose {
    background-color: #303030;
}
.well {
    background-color: #303030;
    border: 1px solid #464545;
    position: relative;
    top: -5px;
}
.tab-content {
    border: 1px #464646 solid;
    position: relative;
    top: -5px;
    padding:10px;
}
form .tab-content {
	border:0;
    padding:0;
    position:static;
}
.tab-content .table {
margin:0;
}
.footer {
    background-color: #303030;
}
.subfooter {
    background-color: #404040;
}
.movie-list {
    background-color: #404040;
}
.btn {
    background: transparent;
}
.btn-default.active, .btn-default:active, .btn-default:hover, .open>.btn-default.dropdown-toggle {
    color: #ccc;
    background-color: #555;
    border-color: #464545;
}
.btn-default {
    color: #ccc;
    background-color: #222222;
    border-color: #464545;
}
.btn-primary {
    color: #ccc;
    background-color: #375a7f;
    border-color: #2f4d6d;
}
.btn-success {
    color: #ccc;
    background-color: #008a67;
    border-color: #007c5d;
}
.btn-success.active, .btn-success:active, .btn-success:hover, .open>.btn-success.dropdown-toggle, .btn-success.focus, .btn-success:focus {
    color: #fff;
    background-color: #007c5d;
    border-color: #006535;
}
.btn-info {
    color: #ccc;
    background-color: #008a67;
    border-color: #007c5d;
}
.btn-info.active.focus, .btn-info.active:focus, .btn-info.active:hover, .btn-info:active.focus, .btn-info:active:focus, .btn-info:active:hover, .open>.btn-info.dropdown-toggle.focus, .open>.btn-info.dropdown-toggle:focus, .open>.btn-info.dropdown-toggle:hover, .btn-info.active, .btn-info:active, .btn-info:hover, .open>.btn-info.dropdown-toggle {
    color: #fff;
    background-color: #007c5d;
    border-color: #007c5d;
}
.btn-info.focus, .btn-info:focus {
    color: #fff;
    background-color: #007c5d;
    border-color: #007c5d;
}
.btn-warning {
    color: #ccc;
    background-color: #D48300;
    border-color: #794b00;
}
.btn-danger {
    color: #ccc;
    background-color: #c73626;
    border-color: #9c2a1e;
}
.jstree .file-size {
    background-color: transparent;
    border: 1px solid #777;
}
.jstree-default .jstree-hovered, .jstree-default .jstree-clicked {
    background: transparent;
    box-shadow: none;
}
.jstree-default>.jstree-striped {
    background:none;
}
.jstree-default .jstree-leaf>.jstree-ocl {
    filter: invert(50%);
}
pre {
    color: #ccc;
    background-color: #222222;
    border: 1px solid #464545;
}
.progress {
    background-color: #BBB;
    border: 1px solid #464545;
}
.avatar-cell {
    border: 1px solid #222222 !important;
    background: #222222;
    margin: auto 0;
}
hr {
    border-top: 1px solid #464545;
}
body#tinymce {
	background-color: #464545;
}
.mce-panel {
    border:0;
}
.checked input:checked+i {
    border-color: #00DBA3;
    color: #00DBA3;
}
a.list-group-item:focus, a.list-group-item:hover, button.list-group-item:focus, button.list-group-item:hover, .list-group-item.active, .list-group-item.active:focus, .list-group-item.active:hover {
    color: #ccc;
    background-color: #555;
    border-color: #464545;
}
.list-group-item {
  	color: #ccc;
    background-color: #222;
    border: 1px solid #464545;
}
.badge, .list-group-item.active>.badge, .nav-pills>.active>a>.badge {
    background-color: #008a67;
    color: #ccc;
}
a.list-group-item, button.list-group-item {
    color: #ccc;
}
.message-sent .message-headers {
    background-color: #008a67;
}
.message-sent .message-body {
    background-color: #007c5d;
}
.message-headers {
    background-color: #464646;
}
.message-body {
    background-color: #404040;
}
.message-unread .message-headers {
    background-color: #2f4d6d;
}
.message-unread .message-body {
    background-color: #375a7f;
}
.message-headers .fa-eye.text-success, .message-headers .fa-eye-slash.text-danger  {
    color: #222222;
}
.btn.btn-purple {
    background-color: #222222;
}
.modal-content {
    background-color: #303030;
    border: 1px solid #464545;
}
.modal-header, .modal-footer {
    border-color:#464545;
    background-color:#404040;
}
.jumbotron {
    border: 1px solid #464545;
}
.jumbotron {
    background-color: #303030;
}
.shadowed {
    box-shadow:none;
}
.alert-success {
    background-color: #008a67;
    border-color: #007c5d;
    color: #ccc;
}
.alert-info {
    background-color: #375a7f;
    border-color: #2f4d6d;
    color: #ccc;
}
.alert-warning {
    background-color: #d48300;
    border-color: #794b00;
    color: #ccc;
}
.alert-danger {
    background-color: #c73626;
    border-color: #9c2a1e;
    color: #ccc;
}
.spoiler {
    border: 1px solid #464545;
    background: #303030;
}
.spoiler .spoiler-toggle {
    background: #404040;
    margin: 0 -15px 0;
    color: #00DBA3;
}
.spoiler .spoiler-open:before {
    color: #00DBA3;
}
.spoiler .spoiler-toggle:before {
    color: #00DBA3;
}
.select2-container--bootstrap .select2-selection--multiple .select2-selection__choice {
    color: #ccc;
    background: #333;
    border: 1px solid #222;
}
.select2-container--bootstrap .select2-selection--multiple .select2-selection__choice__remove:hover {
    color: #fff;
}
.img-thumbnail {
    border: 1px solid #222;
}
.img-thumbnail {
    background-color: #464545;
}
.qq-uploader {
    background-color: #464545;
    border: 1px solid #222;
    box-shadow: none;
}
.qq-upload-list li, .qq-upload-list li.qq-upload-success, .qq-upload-list li.qq-upload-fail {
    color: #ccc;
    background-color: #303030;
    border: none;
    margin-top: 5px;
}
.qq-upload-list li.qq-upload-success {
    background-color: #007c5d;
}
.qq-upload-list li.qq-upload-fail {
    background-color: #9c2a1e;
}
.qq-edit-filename-icon, .qq-upload-spinner {
	filter: invert(100%);
}



.text-green {
    color: #5CB85C;
}
.text-red {
    color: #ff0039;
}
.text-black {
    color: #999;
}
.progress-bar.text-black {
    color: #000;
}
/* not color */
.torrent-filename {
    margin-left: 8px;
