css:
@-moz-document domain("www.contributors.ro") {
#header,
.content_right,
.nice_banners,
.fb_iframe_widget,
#twitter-widget-0,
#menu-meniu-2,
.avatar {
    display: none;
}
body,
html {
    background-color: hsl(0, 0%, 30%)!important;
    color: hsl(0, 0%, 80%)!important;
}
.head_wrap {
    margin-top: 20px !important;
}
#navigation {
    margin-top: 20px !important;
    font-family: 4px !important;
}
a {
    color: hsl(0, 0%, 90%)!important;
}
a:hover {
    color: hsl(0, 0%, 70%)!important;
}
.box {
    border: none !important;
}
.odd,
.even {
    color: hsl(0, 0%, 80%)!important;
}
#menu-meniu-1 {
    background-color: hsl(0, 0%, 50%)!important;
}
h2,
#footer,
.sub-menu {
    background: none !important;
    background-color: hsl(0, 0%, 50%)!important;
    border-radius: 0px !important;
    border: none !important;
}
.content_left {
    background-color: hsl(0, 0%, 50%)!important;
    padding: 10px !important;
}
.author,
.time,
.discuss,
.the-views,
.categories {
    color: hsl(0, 0%, 80%)!important;
    background: none !important;
    padding: 0 !important;
    margin: 0 !important;
    margin-left: 7px !important;
}
.reply,
.submit,
.btinput {
    color: hsl(0, 0%, 80%)!important;
    background: none !important;
    padding: 0 !important;
    margin: 0 !important;
}
.post-author-photo {
    border: none !important;
}
.meta {
    border: none !important;
}
.article {
    margin-bottom: 50px !important;
}
h2 {
    margin: 0 !important;
    margin-left: -11px !important;
}
#comments {
    margin-right: 20px !important;
}
ul.main li ul {
    border: none !important;
    background-color: hsl(0, 0%, 50%)!important;
    color: hsl(0, 0%, 80%)!important;
    opacity: 1 !important;
}
ul.main li ul a,
.menu-item-type-taxonomy {
    color: hsl(0, 0%, 80%)!important;
}
#navigation,
.nav main {
    background-color: hsl(0, 0%, 50%)!important;
    border: none !important;
    border-radius: 0px !important;
}
.menu-item {
    background: none !important;
}
}
