css:
@-moz-document domain("lifehacker.ru") {
/* COLOR */
/* GRID:
    Blue Backgrounds:
        - #15161a hsb(222, 18, 10)
        - #1f2226 hsb(218, 18, 15)
        - #2a2e33 hsb(214, 18, 20)
        - #343a40 hsb(210, 18, 25)
        - #3f474d hsb(206, 18, 30)
        - #495359 hsb(202, 18, 35)
        - #546066 hsb(198, 18, 40)
        - #5e6e73 hsb(194, 18, 45)

    Accent:
        - #e5ae5c hsb(36, 60, 90)
        - #f2bd61 hsb(38, 60, 95); 
        - #ffd18c hsb(36, 45, 100);

    Foreground:
        - #e9f2f2 hsb(180, 4, 95)
        - #dce5e5 hsb(184, 4, 90)
        - #d0d7d9 hsb(188, 4, 85)
        - #c4cacc hsb(192, 4, 80)
        - #b8bdbf hsb(196, 4, 75)
*/

:root {
   --color_bg1: #15161a;
    --color_bg2: #1b1c21;
    --color_bg3: #212329;
    --color_bg4: #272a30;
    --color_bg5: #2d3238;
    --color_bg6: #343940;
    --color_bg7: #3a4147;
    --color_bg8: #40484f;
    
  --color_accent1: #e5ae5c;
  --color_accent1_op50p: rgba(229, 174, 92, 0.75);
  --color_accent2: #f2bd61;
  --color_accent3: #ffd18c;
    
	--color_fg6: #e9f2f2;
	--color_fg5: #dce5e5;
	--color_fg4: #d0d7d9;
	--color_fg3: #c4cacc;
	--color_fg2: #b8bdbf;
	--color_fg1: #abb0b3;
}

/* BACKGROUND */ 

.ref--opener {
  background-color: transparent !important;
}

.desktop-fixed-bar__left:after {
	background: none;
}

body.single {
  background-color: var(--color_bg1);
}

body,
.footer__content,
div.wpappbox,
div.wpappbox div.appicon,
.post-content .white-box,
.single-format-chat .single-container,
.single-container {
  background-color: var(--color_bg1) !important;
}

div.wpappbox div.applinks:after {
  content: '';
  display: block;
  min-width: 100%;
  min-height: 100%;
  background: var(--color_bg2) url(https://lifehacker.ru/wp-content/plugins/wp-appbox/img/appstore.png) no-repeat 50% 9%;
  z-index: 9;
}

.secondary-menu,
.single__content,
pre,
.single-format-chat .lh-cards .menu,
.single-format-chat .lh-cards__excerpt,
.single-format-chat .lh-cards .lh-card,
.single-format-chat .lh-cards__footer,
.single__content-and-sidebar, .desktop-fixed-bar, .single__bottom-widgets {
  background-color: var(--color_bg2);
}

.single__bottom-widgets,
.hc__reply__box__textarea, .hc__reply__box textarea,
.hc__menu__comment__popup,
.hc__message__popup,
.pagination li>a:active, .pagination li>a:focus, .pagination li>span:active, .pagination li>span:focus, .pagination li>a, .pagination li>span,
.hc__authorization__box,
.top-mc-form,
.top-menu--wrapper .search-form,
div.wpappbox div.appbuttons a, div.wpappbox div.appbuttons span,
.post-content .lightgrey-box,
.ref__popup, .container-fluid, .profile, .profile-content, .user-info {
  background-color: var(--color_bg3) !important;
}

.hc__menu__line {
  background-color: var(--color_fg1);
}

.hc__form__send,
.top-mc-form__input,
.top-menu--wrapper .search-form form button[type='submit']:hover,
div.wpappbox div.appbuttons a:hover, div.wpappbox div.appbuttons span:hover, .profile-menu__item.active {
  background-color: var(--color_bg4) !important;
}

.hc__form__send:hover, .profile-menu__item.profile-menu__item--disable {
  background-color: var(--color_bg5);
}

#stop {
  background-color: #fff;
}

.single .up-button {
  background-color: rgba(0, 0, 0, 0);
}

.single .up-button:hover {
  background-color: rgba(0, 0, 0, 0.6);
}

/* BORDERS */

.single .footer {
	border-color: var(--color_bg2);
}

div.wpappbox,
div.wpappbox div.appicon,
div.wpappbox div.applinks, div.wpappbox.compact a.applinks,
.post-content .white-box,
.plista_widget_sidebar .itemLink, .plista_widget_sidebar .itemLinkPET {
  border-color: var(--color_bg4) !important;
}

.top-menu--wrapper,
.top__item,
.flow-post,
.post-content hr,
.single-thumbnail-footer .date-and-source,
.hc__reply__box__textarea, .hc__reply__box textarea,
.hc__authorization__box,
.top-mc-form,
div.wpappbox,
div.wpappbox.screenshots>div.screenshots,
pre,
.table>thead>tr>th, .table>tbody>tr>th, .table>tfoot>tr>th, .table>thead>tr>td, .table>tbody>tr>td, .table>tfoot>tr>td {
  border-color: var(--color_bg5) !important;
}

.desktop-fixed-bar {
    box-shadow: 0 1px 0 0 var(--color_bg4);
}

.hc__reply__box__textarea:focus, .hc__reply__box textarea:focus,
.hc__reply__box__textarea[active=true], .hc__reply__box textarea[active=true],
.hc__menu__comment__popup,
.hc__message__popup,
.top-mc-form__input,
.ref__popup {
  border-color: var(--color_bg7) !important;
}

.top-menu--wrapper .search-form,
.top-menu--wrapper .search-form form button[type='submit'],
.post-content .lightgrey-box {
  border: 1px solid var(--color_bg7) !important;
}

.hc__reply__box__textarea, .hc__reply__box textarea,
.hc__reply__box__textarea:focus, .hc__reply__box textarea:focus,
.hc__reply__box__textarea[active=true], .hc__reply__box textarea[active=true] {
  box-shadow: none;
}

.hc__menu__comment::before {
  border-color: var(--color_fg3) transparent transparent transparent !important;
}

/* FOREGROUND */

.secondary-menu ul li a,
.top__text .title,
.single .up-button i,
.mywidget__ttl,
.hc__menu__item,
.hc__reply__box__textarea, .hc__reply__box textarea,
.hc__menu__comment__popup div,
.hc__message__txt, .hc__message__txt b, .hc__message__txt u, .hc__message__txt i, .hc__message__txt s, .hc__message__txt q, .hc__message__txt code,
.hc__message__popup__item span,
.hc__message__txt a, .hc__message__txt b a, .hc__message__txt u a, .hc__message__txt i a, .hc__message__txt s a, .hc__message__txt q a, .hc__message__txt code a,
.lead__info,
.lead__description,
.can-list p.description,
.how-it-works .work_title, .how-it-works .work_title span, .play-btn span,
.happining .expand-h p, .how-it-works .work_description,
.why .list .description,
.expand-with-you p.name,
.expand-with-you ul li span, .faq .light-description span,
.today-start .timer-title,
.text-center,
.flow-post__meta,
.pagination li>a:active, .pagination li>a:focus, .pagination li>span:active, .pagination li>span:focus, .pagination li>a, .pagination li>span,
.hc__authorization__title,
.hc__squaredThree span,
.top-mc-form__title,
.top-menu--wrapper .search-form,
.single-thumbnail-header__hide-thumbnail .title-block a,
.single-thumbnail-header__hide-thumbnail .title-block,
.post-content blockquote.cut p,
div.wpappbox div.appbuttons a, div.wpappbox div.appbuttons span,
div.wpappbox,
body,
.right-lead__description,
pre,
.post-content .speech-wrapper .speech,
.post-content .speech-wrapper .name-author,
.plista_widget_belowArticleMore .plistaHeadline
.plista_widget_sidebar .plistaHeadline,
.itemTitle,
.plista_widget_belowArticleMore .plistaHeadline
.plista_widget_belowArticle2 .plistaHeadline,
.plista_widget_sidebar .plistaHeadline,
.plista_widget_belowArticleMore .plistaHeadline,
.plista_widget_belowArticle2 .plistaHeadline,
.plista_widget_underArticle .plistaHeadline {
  color: var(--color_fg3) !important;
}

.single__excerpt,
.post-content,
.secondary-menu__icons [class*='icon-'],
.widget-week h2, 
.the-same-buttons__flex .with-comments span,
.mywidget__header,
.hc__menu__count,
.lead__title,
h1, h2, h3, h4, h5, h6,
.flow-post__excerpt {
  color: var(--color_fg1) !important;
}

.post-content h2,
.top__text .numbers .icon-view,
.widget-week__item .numbers .count,
.single-thumbnail-footer .date-and-source>span a,
.single-format-chat .lh-cards .menu a,
.post-content blockquote em,
.post-content .wp-caption-text, .post-content .wp-thumbnail-caption,
.single-format-chat .lh-cards__excerpt {
  color: var(--color_fg1);
}

.widget-week__item:hover .numbers .count, .widget-week__item:hover .numbers .icon-view,
.top-mc-form__input,
.post-content .lightgrey-box,
.textwidget div[id^="header_"], 
div[id^="container_"] .list-container-item .title, .ringo_is_thumbnail_hiding .single-post-header .meta-info__item, .desktop-fixed-bar__subtitle, h1.single__title {
  color: var(--color_fg4) !important;
}

.secondary-menu ul li a:hover,
.secondary-menu__icons [class*='icon-']:hover,
.the-same-buttons__flex .with-comments:hover span,
.expand-with-you ul li, .why .list.rose .description,
.expand-with-you p.description,
.single-format-chat .lh-cards .menu a.active-menu-item,
.single-format-chat .lh-cards .menu a:hover {
  color: var(--color_fg5) !important;
}

.hc__menu__login svg path {
  fill: var(--color_fg1);
}

.hc__menu__login:hover svg path,
.hc__reply__clip__img:hover path,
.hc__reply__smile:hover path,
.hc__voting__good:hover svg path,
.hc__voting__bad:hover svg path,
.hc__footer__mail:hover svg path, .hc__footer__rss:hover svg path, .hc__footer__logo:hover svg path,
.hc__authorization__close path {
  fill: var(--color_fg4);
}

.hc__menu__comment__popup div:hover,
.hc__message__popup__item:hover span {
  color: var(--color_bg1) !important;
}

.desktop-fixed-bar__site-logo {
	filter: invert(1);
}

.primary-menu ul li a, .btn:focus, .btn:active, .btn:active:focus {
	color: #fff !important;
}

/* ACCENTS */
a, 
div[id^="container_"] .list-container-item .title:hover, 
.post-content a,
.mywidget__ttl:hover,
.hc__nick,
.lead__title:hover,
.top__text:hover .title,
.plista_widget_belowArticleMore .itemTitle:hover {
  color: #ed5e42 !important;
}

.ref__order,
.ref--opener {
  color: #9256ff;
  cursor: pointer;
}

.ref--opener {
  border-color: #9256ff;
}
}

@-moz-document domain("lifehacker.ru") {
/* FONT */

@font-face {
        font-family: 'Lato';
        font-style: normal;
        font-weight: 300;
        src: local('Lato Light'), 
            url(https://apushkarev.github.io/font/Lato-Light.woff2) format('woff2');
    }

@font-face {
        font-family: 'Lato';
        font-style: italic;
        font-weight: 300;
        src: local('Lato Light Italic'), 
            url(https://apushkarev.github.io/font/Lato-LightItalic.woff2) format('woff2');
    }

@font-face {
        font-family: 'Lato';
        font-style: normal;
        font-weight: 400;
        src: local('Lato Regular'), 
            url(https://apushkarev.github.io/font/Lato-Regular.woff2) format('woff2');
    }

@font-face {
        font-family: 'Lato';
        font-style: italic;
        font-weight: 400;
        src: local('Lato Italic'), 
            url(https://apushkarev.github.io/font/Lato-Italic.woff2) format('woff2');
    }

@font-face {
        font-family: 'Lato';
        font-style: normal;
        font-weight: 700;
        src: local('Lato Bold'), 
            url(https://apushkarev.github.io/font/Lato-Bold.woff2) format('woff2');
    }

@font-face {
        font-family: 'Lato';
        font-style: italic;
        font-weight: 700;
        src: local('Lato Bold Italic'), 
            url(https://apushkarev.github.io/font/Lato-BoldItalic.woff2) format('woff2');
    }

    * {
        font-family: Lato !important;
    }

    .single__excerpt,
.post-content p,
.mywidget__ttl {
        font-weight: 300 !important;
    }

    .post-content h2,
.mywidget__header {
        font-weight: 400 !important;
    }

    .single__content--wrapper *,
.secondary-menu ul li a, .post-content h2, .top__text .title, .single .up-button i, .mywidget__ttl, .hc__menu__item, .hc__reply__box__textarea, .hc__reply__box textarea, .hc__menu__comment__popup div, .hc__message__txt, .hc__message__txt b, .hc__message__txt u, .hc__message__txt i, .hc__message__txt s, .hc__message__txt q, .hc__message__txt code, .hc__message__popup__item span, .hc__message__txt a, .hc__message__txt b a, .hc__message__txt u a, .hc__message__txt i a, .hc__message__txt s a, .hc__message__txt q a, .hc__message__txt code a, .lead__info, .lead__description, .can-list p.description, .how-it-works .work_title, .how-it-works .work_title span, .play-btn span, .happining .expand-h p, .how-it-works .work_description, .why .list .description, .expand-with-you p.name, .expand-with-you ul li span, .faq .light-description span, .today-start .timer-title, .text-center, .flow-post__meta, .pagination li>a:active, .pagination li>a:focus, .pagination li>span:active, .pagination li>span:focus, .pagination li>a, .pagination li>span, .hc__authorization__title, .hc__squaredThree span, .top-mc-form__title, .top-menu--wrapper .search-form {
        text-shadow: 0.7px 0.7px 0 rgba(0, 0, 0, 0.2);
    }

    .post-content p {
        font-size: 21px;
    }

.ref {
    font-size: 15px;
}
}

@-moz-document domain("lifehacker.ru") {
/* LAYOUT */

    .single__content-and-sidebar {
        border-left: 1px solid rgba(0, 0, 0, 0.3);
        border-right: 1px solid rgba(0, 0, 0, 0.3);
    }

    .single-container,
.single__content-and-sidebar {
        border-radius: 4px;
    }

.single__content-and-sidebar {
    border-bottom: 1px solid var(--color_bg5);
}

.ref__popup_close {
    width: 0;
    display: inline-block;
}

.ref__popup_close:after {
    content: '+';
    display: inline-block;
    color: var(--color_fg5);
    font-size: 27px;
    transform: rotate(45deg);
        position: relative;
    top: -19px;
    right: 10px;
}

.post-content .red-box {
    background: #2b2827;
    border-color: #3d0a00;

}
}
