css:
@-moz-document regexp("http(s)?://userstyles.org/.*") {
/*global transition*/
  *{transition-timing-function:ease;transition-duration:.15s;transition-property:all!important}

  /*font color*/
  body,#bottomLinks,#headline,#information_key_left,#information_key,#share_div,.style_author_long,.style_description_long,.navigation .links-box .navigation-link .navigation-title{color:#567;text-shadow:1px 1px 1px #000}
  h1,.navigation_title,#copyrights,.see_more,#infomation_value_left,#information_value,#applies_link,.text_style_page,.navigation_category,.navigation .links-box .navigation-link:hover .navigation-title{color:#89a}
  #title_long a{color:#89a!important}
  .style_title,#category_style_card_title{color:#bbc}
  a,h4,h4 a,.footer_link a{color:#89a !important}
  a:hover,h4 a:hover,.navigation_link:hover span,#title_long a:hover{color:#3498db !important}
  .author-name a:hover,#discussion_author a:hover{color:#3498db !important}
  .author-name a,#discussion_author a, #discussion_author a:visited{color:#18a !important}
  h4 a:visited{color:#678 !important}

  /*main frame*/
  body,#nav-view{background-color:#0a0f14}
  #nav-view{justify-content:center}
  #footer{background-color:transparent;padding:14px 0 0}
  #header{background-color:#273848;box-shadow:0 5px 5px #000}
  #title_div{box-shadow:inset 0 -1px 1px 0 #273848}
  #container{margin:0;width:100%}
  #navigation{background-color:transparent;padding-bottom:0}
  #view{margin:0;min-width:550px;flex-grow:1}
  #view_inner,#style_info{width:calc(100% - 20px);margin:0 20px 0 32px}
  #nav-view-responsive{background-color:#0a0f14}

  /*list*/
  #installBox,.line_seperator,.styles div.style_card_long:nth-child(3),.style_data_long a{display:none}
  .styles{width:calc(100% - 25px);justify-content:flex-start}
  .style_card{margin-right:8px!important}
  .linksBox .navigation_link:last-child{box-shadow:none}
  .us-stylecard--short:hover{border:1px solid #3498db;box-shadow:1px 8px 10px #000}
  .us-stylecard--short:hover a{color:#3498db !important}
	 .us-stylecard--short{background-color:#0a0f14;border:1px solid #273848;box-shadow:1px 3px 4px #000;border-radius:5px;height:auto}
	 .us-stylecard--short .us-stencil{box-shadow:none}
	 .us-category--stylecard-long .us-category__title,.us-category--stylecard-long .us-category__title--narrow,.us-category--stylecard-short .us-category__title,.us-category--stylecard-short .us-category__title--narrow{width:calc(100% - 80px)}
	 .us-category--stylecard-long .us-stylecards__container,.us-category--stylecard-short .us-stylecards__container{width:calc(100% - 80px);display:flex;flex-wrap:wrap;justify-content:space-between}
	 .us-category--stylecard-short,.us-category--stylecard-long{padding:0 10px}
  .us-stylecard--long{background-color:#0a0f14;border:1px solid #273848;margin:8px 0;padding:0;box-shadow:1px 3px 4px #000;border-radius:5px;width:calc(50% - 8px);height:auto}
  .us-stylecard--long:hover{border:1px solid #3498db;box-shadow:1px 8px 10px #000;transform:translate(0, -4px)}
  .us-stylecard--long:hover a,.style_card_long:hover #title_long a{color:#3498db !important}
	 .us-stylecard--long .us-stencil{border-radius:5px;overflow:hidden}
  .screenshot_wrapper{border:0;height:auto;margin-bottom:-11px}
  .style_info{margin:0 12px}
  #categories{width:calc(100% - 20px);margin:0 20px 0 0}
  .style_category_card_wrapper{background-color:#273848;box-shadow:1px 3px 4px #000}
  #categories a:nth-child(3n) .style_category_card_wrapper{margin-right:22px}
  .adLong,.adContainer{display:none}
  #wrapper-carousel{margin:0 calc(50vw - 535px)}
  #ovals-wrapper{margin:15px 0}
  #overlay-info{background-color:#0a0f14}
  .oval{background-color:#273848}

  /*head bar*/
  #search_input{background-color:rgba(0,0,0,0.8)!important;box-shadow:inset 1px 1px 4px #000}
  #search_input input{color:#677!important}
  #search_input input:focus{color:#cdd!important}
  #create_new_style,#create_new_style a{color:#17a !important;border-color:#17a;text-shadow:none}
  #create_new_style:hover{background:rgba(0,0,0,0.2);border-color:#3498db;box-shadow:inset -1px 1px 4px #000}
  #create_new_style:hover a{color:#3498db !important;transform:translate(-1px, 1px)}
  .loggedIn{white-space:nowrap}
  #top_android_button{background-color:#0a0f14;box-shadow:none}
  #top_android_button:hover{background-color:#0a0f14;box-shadow:none}
  #top_android_button .android_button_text{color:#567}
  #top_android_button:hover .android_button_text{color:#17a}

  /*pager*/
  .current{background-color:#3498db;color:#0a0f14;font-weight:bold;text-shadow:none}
  .div-arrow{background:0;border:0}
  .div-arrow:hover{border:0;box-shadow:1px 1px 4px #000}
  .arrow-icon{fill:#456!important}

  /*footer*/
  #bottomLinks{margin-top:8px}

  /*navigation bar*/
	 .navigation{background-color:#16181a;padding:0 0 0 16px;z-index:20}
	 .navigation .links-box{box-shadow:none}
  .navigation .links-box .navigation-link{padding:8px 0;box-shadow:none}
  .navigation .links-box .navigation-link:hover{background:0;border-color:#3498db}
	 .navigation .navigation-category{margin:16px 0 8px 0}
	 .navigation .navigation-ad{display:none}
	 .navigation .links-box .navigation-link .logo-navigation-link{filter: drop-shadow(0 0 .8px #3498db)}

  /*buttons*/
  .white_button,.css_button,.advanced_button,#send_feedback_button,.see_more,.customize_button{color:#89a;background-color:#0a0f14;border:1px solid #273848;box-shadow:1px 3px 4px #000;padding:0 8px}
  .white_button{width:auto}
  .white_button:hover,.css_button:hover,.advanced_button:hover,#send_feedback_button:hover,.see_more:hover,.customize_button:hover{background-color:#0a0f14;color:#3498db;border-color:#3498db;transform:translate(0, -2px);box-shadow:1px 5px 6px #000}
  #install_style_button{border:0;color:#ddd;background-color:#17a;box-shadow:1px 3px 4px #000;width:auto;padding:0 8px}
  #install_style_button:hover{background-color:#19c;color:#fff;transform:translate(0, -2px);box-shadow:1px 5px 6px #000}
  #update_style_button{border:0;box-shadow:1px 3px 4px #000;width:auto;padding:0 8px}
  #update_style_button:hover{transform:translate(0, -2px);box-shadow:1px 5px 6px #000}
  #style_installed_button{background-color:#16181a;width:auto;padding:0 8px}
  #archive_style_button{box-shadow:none;width:auto;padding:0 8px}
  input[onclick],input[type="submit"]{color:#ddd !important;background:#17a!important;border-radius:2px;box-shadow:1px 1px 3px #000 !important;border:0 !important}
  input[onclick]:hover,input[type="submit"]:hover{color:#fff !important;background:#19c!important;border-radius:2px;box-shadow:1px 1px 3px #000 !important}
  input[onclick]:active,input[type="submit"]:active{color:#bbb !important;background:#169!important;border-radius:2px;box-shadow:inset 1px 1px 3px #000 !important}
  .installed-icon{margin:5px 4px 0}

  /*textarea*/
  div.form-controls input,.option-user-url{background-color:#070707;color:#89a;border:1px solid #273848;padding:2px 5px;margin:2px 5px 2px 0;box-shadow:inset 1px 1px 4px #000}
  div.form-controls textarea{background-color:#070707;color:#89a;padding:0 10px;border:1px solid #273848;max-width:calc(100% - 32px)!important;box-shadow:inset 1px 1px 4px #000}
  #notes_textarea{background-color:#070707;color:#89a;padding:10px 15px}
  div.form-controls input:focus,.option-user-url:focus,#notes_textarea:focus,div.form-controls textarea:focus{border-color:#3498db}
  .CodeMirror{border:1px solid #273848;text-shadow:none}
  .CodeMirror-scrollbar-filler, .CodeMirror-gutter-filler{background:#000}

	/*CodeMirror*/
  .CodeMirror{background:#000;color:#89a}
  .CodeMirror pre span{font-family:monaco}
  .CodeMirror div.CodeMirror-cursor{border-color:#98a}
  .CodeMirror-gutters{background:#16181a;border:0}
  .CodeMirror-gutter-wrapper{left:-32px!important}
  .CodeMirror-linenumber{color:#89a}
  .page-inner form li span,.cm-s-default .cm-operator,.cm-s-default .cm-variable-3{color:#f8f8f2}
  .cm-s-default .cm-qualifier,.cm-s-default .cm-builtin,.cm-s-default .cm-atom{color:#a6e22d}
  .cm-s-default .cm-number{color:#ae81ff}
  .cm-s-default .cm-tag,.cm-s-default .cm-variable-2,.cm-s-default .cm-keyword,.cm-s-default .cm-def{color:#f92772}
  .cm-s-default .cm-comment{color:#75715e}
  .cm-s-default .cm-property,.cm-s-default .cm-attribute,.cm-s-default .cm-string-2{color:#66d9ef}
  .CodeMirror-scrollbar-filler, .CodeMirror-gutter-filler{background:#282436}

  /*grid*/
  .statistic table, th, td{border:0;padding:2px 8px}
  tr{border-bottom:1px solid #000}

  /*code*/
  #stylish-code{background-color:#070707;border:0;border-radius:2px;padding:0 8px;box-shadow:inset 1px 1px 4px #000}

  /*advance setting*/
  .advancedsettings_hidden{display:flex;max-width:640px;max-height:0;flex-direction:row;flex-wrap:wrap;opacity:0;overflow:hidden}
  .advancedsettings_shown{background-color:transparent;margin:0;padding:20px 20px 0}
  .title_setting{color:#89a}
  .text_setting_input{background:#070707;border:1px solid #273848;color:#567;margin-bottom:10px}
  #style-settings select{background:#070707;border:1px solid #273848;color:#567}

  /*comment*/
  #single_discussion{box-shadow:none;border-bottom:1px solid #000}

  /*Theme page*/
  #style_info > div,#style_info > a{width:auto;max-width:800px}
	 #left_information,#infomation_value_left{max-width:500px}
  a[href^="https://userstyles.org/styles/delete"]{width:173px;margin-top:30px}
  #archive_style_button{margin:0}
  #preview_image_div{background-size:contain,contain!important}
}

@-moz-document regexp("http(s)?://userstyles.org/") {
#view_inner{flex-direction:row;flex-wrap:wrap}
  .styles{width:690px;margin-left:-690px;margin-top:45px}
  .styles:nth-child(2n){margin-right:auto}
  .category_head{width:690px;height:40px;padding-right:9px;vertical-align:top}
  .category_title{font-size:20px;margin:0}
  .see_more{margin:0}
}

@-moz-document regexp("http(s)?://userstyles.org/styles/[0-9]+/(?!edit|new).*") {
#view{flex-grow:0}
}

@-moz-document regexp("http(s)?://userstyles.org/(d/)?styles/([0-9]+/(edit|new))") {
  page{height:auto}
  label{padding:0}
  .edit-setting{background:0}
  .edit-color-setting,.edit-dropdown-setting,.edit-image-setting{background-color:#1c3040;box-shadow:1px 3px 4px #000;padding:10px 15px;margin:10px 0}
	 #edit-style-settings>li>ul>li{margin:5px 0}

	 #iframe{overflow:visible!important}
  .view_inner{width:100%;padding:0 32px}
  #container{height:calc(100vh - 150px)!important}
  .navigation>.navigation_category:nth-child(2){display:none}
  .linksBox:nth-child(3){display:none}
}

@-moz-document regexp("http(s)?://forum.userstyles.org/.*") {
/*global transition*/
	*{transition-timing-function:ease;transition-duration:.2s;transition-property:border-color,border-radius,background-color,color,box-shadow,opacity,height,width,trasform,font-size,font-weight!important}

	/*text*/
	body{background-color:#111113;color:#89a;font-family:Verdana}
	h1, h2, h3, h4, h5, h6,.Pager .Highlight{color:#bbc;font-family:Verdana}
	a,a.TextColor, a .TextColor,.FlyoutMenu a{color:#388}
	a:hover, a.TextColor:hover, a:hover .TextColor{color:#4bb}
	code, pre{background:#063326;border:0;text-shadow:1px 1px 1px #011;box-shadow:inset 1px 1px 4px #000;padding:4px 6px 2px;margin:0 1px}
	.ItemDiscussion .Meta, .DataList .Meta,.DiscussionForm label{color:#89a}
	input.DateBox, input.InputBox, input.SmallInput, textarea,input.SmallInput:focus, input.InputBox:focus, textarea:focus{background:#070707;color:#89a;box-shadow:inset 1px 1px 4px #000}

	/*mainframe*/
	#Head{background-image:linear-gradient(to bottom,#033,#022);position:fixed;width:100%;height:27px;top:0;z-index:800;box-shadow:0 2px 5px #000;padding:12px 0 8px!important}
	#Body{position:relative;top:60px}
	#Foot{display:none}

	/*Header*/
	#Head a{text-transform:uppercase;font-family:Cambria;text-shadow:1px 1px 3px #011}

	@media(min-width:968px){.SiteSearch{position:fixed;top:8px;right:calc((100% - 960px)/2)}}
	@media(max-width:968px){.SiteSearch{position:fixed;top:8px;right:8px}}
	.SiteSearch .Button,.SiteSearch .Button:focus{background-color:transparent!important;background-image:linear-gradient(to bottom,#033,#022)!important;background-position:0 20px;width:20px;height:20px;border:3px solid #033;border-radius:10px;margin:1px 2px}
	.SiteSearch .Button:hover{background-position:0 0;border:0px solid #022;border-radius:0}
	.SiteSearch .Button:active{background-position:0 0;background-image:linear-gradient(to bottom,#066,#044)!important;border:0px solid #033}
	.SiteSearch .InputBox{font-size:10pt;height:30px;padding:3px 30px 0 8px}

	/*topic list*/
	.Item.Read{background:0}
	.Item.Read div.Title a{font-style:italic}

	/*topic tag*/
	.Tag-Announcement,.Tag-Closed{font-size:0;padding:0;margin:0 4px 0 0}
	.Tag-Announcement:before{display:inline-block;content:"　";overflow:hidden;width:4px;height:11px;position:relative;bottom:4px;background:#448}
	.Tag-Announcement:after{display:inline-block;content:"　";overflow:hidden;width:4px;height:3px;position:relative;top:1px;right:4px;background:#448}
	.Tag-Closed:before{display:inline-block;content:"　";overflow:hidden;width:4px;height:18px;position:relative;top:2px;background:#833;transform:rotate(45deg)}
	.Tag-Closed:after{display:inline-block;content:"　";overflow:hidden;width:4px;height:18px;position:relative;top:2px;right:4px;background:#833;transform:rotate(-45deg)}
	.HasNew{background:#54a;color:#ddd}

	.HomepageTitle{margin-left:22px}
	.Sprite16{background-color:#448;border-radius:2px}
	.Count{background-color:#448;box-shadow:1px 1px 3px #000}

	/*hr*/
	#Panel .FilterMenu li, .PanelInfo li{border:0}
	.PanelCategories li:first-child, .PanelCategories li:nth-last-child(2){border-image:linear-gradient(to right,#335,#16181a) 1 0}
	.DataList .Item, .NarrowList .Item{border-image:linear-gradient(to right,#111113,#335)1 0;transition:all .15s linear}

	/*button*/
	.Button,.NavButton{color:#bbb;margin:0 6px 1px;background-color:#335;background-image:linear-gradient(to bottom,#559,#335);background-position:0;background-repeat:no-repeat!important;border:1px solid;border-color:rgba(0,0,0,.1) rgba(0,0,0,.1) rgba(0,0,0,.25);box-shadow:inset 0 1px 0 rgba(255,255,255,.2),0 1px 3px #111;text-shadow:0 1px 1px #000;border-radius:3px;transition-duration:.13s!important}
	.Button:hover,.NavButton:hover{color:#ccc;background-position:0 -8px;border-color:rgba(0,0,0,.1) rgba(0,0,0,.1) rgba(0,0,0,.25)}
	.Button.Active,.ButtonGroup.Open .Button.Handle,.Button:focus,.Button:active,.NavButton:active,.NavButton:focus{color:#ccc;background-color:#335;background-image:linear-gradient(to bottom,#559,#335);background-position: 0 -20px;border-color:rgba(0,0,0,.2) rgba(0,0,0,.2) rgba(0,0,0,.35);box-shadow:inset 0 1px 0 rgba(255,255,255,.1),0 1px 1px #000}

	.HasNew{background:#54a;color:#ddd}

	.ReactQuote{display:none}
	.ReactButton.Quote{display:inline-block;font-family:Times New Roman;font-size:0;text-transform:uppercase;color:transparent;background:transparent;padding:3px 3px 1px;border-radius:10px;border:1px solid #335;box-shadow:inset 1px 1px 3px #000,1px 1px 3px #000;text-shadow:1px 1px 2px #000}
	.ReactButton.Quote:before,.ReactButton.Quote:after{color:#335;font-size:18pt;transition-duration:.2s;line-height:0;position:relative}
	.ReactButton.Quote:before{content:"“";top:8px}
	.ReactButton.Quote:after{content:"”";top:16px}
	.ReactButton.Quote:hover{background:#335;font-size:9pt;color:#fff;border-radius:3px;box-shadow:1px 1px 3px #000}
	.ReactButton.Quote:hover:before,.ReactButton.Quote:hover:after{color:#fff;font-size:9pt;position:relative;top:0px}
	.ReactButton.Quote:hover:after{margin-left:3px}

	/*quote*/
	blockquote.Quote, blockQuote.UserQuote{background:#1a1d23;box-shadow:1px 1px 4px #000}

	/*setting*/
	table.PreferenceGroup tbody tr:hover td{background:#335}

	/*pop-out*/
	.FlyoutMenu,.MenuItems{color:#bbc;text-shadow:1px 1px .5px #000;background:#559;background-image:linear-gradient(to bottom,#559,#335);border:0;box-shadow:inset 0 1px 0 rgba(255,255,255,.2),0 1px 3px #000}
	.Flyout::after{border-bottom-color:#559}
	.PopList .Meta{color:#89a}
	.MenuItems hr{border-color:#333}
	.MenuItems a, .MenuItems a:link, .MenuItems a:visited, .MenuItems a:active{color:#bbc!important}
	.MenuItems a:hover{color:#fff!important;background:rgba(0, 0, 0, 0.05)!important}
}
