css:
@-moz-document domain("www.criticatac.ro") {
body {
    background-color: hsl(0, 0%, 30%)!important;
    color: hsl(0, 0%, 80%)!important;
    border: none !important;
    margin: 0px !important;
}
body,
html,
#header,
#access,
.slider_modif,
.i,
.index_middle_boxes,
.cont_col1,
.cont_col,
.index_boxes top_boxes,
#container,
.widget-container,
.index_boxes,
.top_boxes,
.lefteast,
.sprijina,
.last_box,
.sitedonate,
.main_extented,
#main,
#main.main_extented,
.entry-content,
#masthead,
.homecontent,
.extend,
#content,
.post,
.page-title,
.widget-title,
.entry-title {
    background-color: hsl(0, 0%, 30%)!important;
    color: hsl(0, 0%, 80%)!important;
    border: none !important;
    margin: 0px !important;
}
#site-description,
#site-title,
#likeup,
.see_all,
.index_boxes_bottom,
#colophon,
.clear,
.cleard,
.widget-area {
    display: none;
}
.slider_modif {
    overflow: hidden;
}
.zcom {
    background: none !important;
}
.cont_col h2,
.cont_col1 div.entry-content,
.cont_bd div.entry-content,
.cont_bd h2 {
    background-color: hsl(0, 0%, 30%)!important;
    color: hsl(0, 0%, 80%)!important;
    border: none !important;
    margin: 0px !important;
}
#header,
#slides,
.top_boxes,
.index_boxes {
    overflow: hidden;
    margin: 0;
    padding: 1px 0 0 0;
    padding-top: 20px !important;
}
.index_middle_boxes {
    margin-top: 0px !important;
    padding-top: 40px !important;
}
.index_boxes.top_boxes {
    padding-top: 20px !important;
    border-bottom: 0px !important;
    margin: 0;
    padding: 1px 0 0 0;
}
#header {
    padding-bottom: 20px !important;
}
.slide div.t a,
.slide div.t a:visited,
.slide div.t a:hover {
    background-color: hsl(0, 0%, 30%)!important;
    color: hsl(0, 0%, 80%)!important;
    border: none !important;
    margin: 0px !important;
}
.index_boxes.top_boxes .index_box .entry-title a.lros,
.index_middle_boxes div.index_middle_box .entry-title .lros,
.index_middle_boxes div.index_middle_box .entry-meta .lros,
.meta-sep,
a.lros {
    color: hsl(0, 0%, 80%)!important;
    text-decoration: none;
}
a {
    color: hsl(0, 0%, 90%)!important;
    text-decoration: none;
}
a:hover {
    color: hsl(0, 0%, 70%)!important;
    text-decoration: none;
}
.post {
    padding-right: 20px !important;
}
}
